css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
div#right_column,
div#right_column:after {background: none !important;}

body {background:#d9cec7 url(http://static.tumblr.com/gootfij/Ukyme6n65/union_j.png) repeat fixed! important }


#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: transparent !important;

  }


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(255,255,255,0.4) !important; padding: 15px;     
    -webkit-border-radius: 20px;

    -moz-border-radius: 20px;

    border-radius: 20px;
}

img#content_top, img#content_bottom {

display: none;

}



*{text-shadow:none !important; color:#4b4b4b !important;}

.tab_notice {top: 0px !important;
background-image:none!important;
margin-left: 5px !important;
height:18px !important;
width:auto !important;
text-align: center !important;
border-radius: 2px !important;
box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.4) !important;
vertical-align:middle !important; }

*.tab_notice_value{text-shadow:none !important; color:#f2e7e0!important;}



ol#posts li.notification{background-color: #f2e7e0 !important; border-bottom: 2px solid #9d92a5 !important; color: #4b4b4b !important;}


#right_column a#dashboard_switch_blog_menu_current:hover {background:#f2e7e0 ;}


#nav .nav_item.active{background-color:#af99a1;}


#left_column #posts .post .permalink{background-color:#f2e7e0 ;}


#dashboard_switch_blog_menu_current,


#dashboard_switch_blog_arrow_and_divider,


#dashboard_switch_blog_arrow_current


{background-image:none !important}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 


	background: none !important;


	width: 0px !important;


	height: 0px !important;


	margin-top: 0px !important;


	border-bottom: solid 11px white !important;


	border-right: 11px solid transparent !important;


	border-left: 11px solid transparent !important; }



.selection_nipple:not([class="selection_nipple white"]) {



	border-bottom: solid 11px #f2e7e0 !important;}



.avatar_frame {

background-color: rgba(255,255,255,0.1) !important; border-color: rgba(255,255,255,0.1) !important }



.notification {

background: rgba(0,0,0,0.4) !important;

border: rgba(255,255,255,0.1) !important;

background-image: none !important;

margin-bottom: 1px !important }



.notification a { border-bottom: 1px solid rgba(0,0,0,0.3) !important }

.notification a:hover { border-bottom: 1px solid rgba(0,0,0,0.5) !important }



.notification_type_icon { display: none !important }



.nipple { border-right-color: rgba(0,0,0,0.07) !important }


.controls_section li { background: rgba(255,255,255,0.4) !important }

.with_subtitle small { color: #737373 !important }

.recessed a { color: #737373 !important }

.radar { display: none !important;}

.count {text-shadow: none !important;}








.selected .following,



.selected .followers,



.selected .settings,



.selected .posts,



.selected .messages,



.selected .drafts,



.selected .queue, 



.selected .likes, 



a.explore_more_tags,



a.add_and_remove {



	color: #737373 !important; 



	background: transparent!important;}



a.customize,



a.mass_editor



	{background-color:#f2e7e0 !important;  



	background-image:none !important;



	border:0px !important}



div.gradient,



div.dashboard_nav_item, 



a.permalink



	{background-color:#f2e7e0 !important;



	opacity:.9; 



	background-image:none !important;



	border:0px !important}



.content.is_media, 



.mask.top, 



.mask.bottom {



	background: none !important; }



.post.photo_post .photo, 



.post.photo_post .video_thumbnail {



	border-radius: 16px !important;}



.post.photo_post .photo, 



.post.photo_post .video_thumbnail 



	{-moz-box-shadow: 1px 1px 3px #000000 !important;}



#auto_pagination_loader



	{background: #A099A1 !important;}



div.return_to_top_icon{	background: none !important;



	width: 0px !important;



	height: 0px !important;



	margin-top: 0px !important;



	border-bottom: solid 30px white !important;



	border-right: 15px solid transparent !important;



	border-left: 15px solid transparent !important;}



#left_column #posts .notification {



background-color: rgba(255,255,255,0.6) !important;



color:#737373 !important;



border-bottom-color: #eee !important}


#new_post_notice_container{



color:#737373 !important}



#new_post_notice_container a{



color:#f2e7e0 !important}




.permalink {background: none !important;



width: 16px !important;



height: 16px !important;



  background: #f9f9f9 !important;



  border-bottom-left-radius: 10px !important;



  border-top-right-radius: 10px !important;



  margin-top: 3px !important;



  margin-right: 4px !important;}









#pagination a {

background: rgba(0,0,0,0.4) !important;

color: rgba(255,255,255,0.7) !important }

#pagination a:hover {

color: rgba(255,255,255,0.9) !important }



#dashboard_controls a, #dashboard_controls .control {

color: rgba(255,255,255,0.6) !important }



.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {

display: none !important }



.blue_note, .blue_note a {

background: rgba(0,0,0,0.2) !important;

color: rgba(0,0,0,0.6) !important }







#footer, #footer a {

border: none !important;

color: rgba(0,0,0,0.5) !important }



@-moz-document url-prefix('http://www.tumblr.com/reblog/') , url-prefix("https://www.tumblr.com/reblog/") {



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    padding: 40px 0;

  }

}





