css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("viclivingston.blogspot.com") {
#header {
background: -moz-linear-gradient(top,  rgba(0,0,0,0.65) 0%, rgba(0,0,0,0) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.65)), color-stop(100%,rgba(0,0,0,0))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  rgba(0,0,0,0.65) 0%,rgba(0,0,0,0) 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  rgba(0,0,0,0.65) 0%,rgba(0,0,0,0) 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top,  rgba(0,0,0,0.65) 0%,rgba(0,0,0,0) 100%); /* IE10+ */
background: linear-gradient(to bottom,  rgba(0,0,0,0.65) 0%,rgba(0,0,0,0) 100%); /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#a6000000', endColorstr='#00000000',GradientType=0 ); /* IE6-8 */
}
.widget h2 {color:#AE8C04 !important;}
.widget-content {color:#000 !important;background:#fff;padding:5px 5px 5px 5px;}
.content-inner{background-color: #fff !important;font-size: 120% !important;}
.content-outer{color:#000;background-color: #F0F0F0 !important;}
.comment-thread {color:#000;background:#fff;padding:5px 50px 5px 50px;}  
.comments .comment-thread.inline-thread {background-color: #fff !important;}
table {border: 0px!important;border-collapse: collapse !important;}
td {padding: 5px 10px 5px 10px;background:#fff !important;border-collapse:collapse;}
a {font-weight: bold;}
a:link {color: #AE8C04 !important;text-decoration: none;}
a:visited {color: #AE8C04 !important;text-decoration: none;}
a:hover {color: #C09B05 !important;text-decoration: underline;}
a:active {color: #C09B05 !important;text-decoration: none;}
a:link span[style] {color: #AE8C04 !important;text-decoration: none;}
.post-body img {padding:5px 10px 5px 10px !important;}
}
