css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plattentests.de") {


html {
    background: #ffffff !important;
}

body {
    background: #ffffff !important;
    font-size: 12px !important;
    margin: 3px !important;
    padding: 0px !important;
}

span {
    display: inline !important;
}

a {
    display: inline !important;
    text-decoration: underline !important;
    color: #000000 !important;
    background: transparent !important;
}

a:hover {
    text-decoration: none !important;
    color: #ffffff !important;
}

h2 {
    color: #ffffff !important;
    background: #000069 !important;
    font-size: 13px !important;
    text-align: right !important;
    width: auto !important;
    height: auto !important;
    margin: 0px !important;
    padding: 5px !important;
}


input, select, button {
    width: auto !important;
    height: auto !important;
    margin: 0px !important;
}

button {
    border: 2px outset buttonhighlight !important;
    background: buttonface !important;
}

#siteheader,
#mainwrapper,
#sitefooter {
    width: 100% !important;
}

#mainwrapper {
    margin-top: 7px !important;
    background: #ffffff !important;
    background-image: -webkit-gradient(
        linear,
        left top,
        right top,
        color-stop(0, #ffcc88),
        color-stop(190px, #ffcc88),
        color-stop(190px, #ffffff),
        color-stop(196px, #ffffff),
        color-stop(196px, #007ba6),
        color-stop(100%, #007ba6)
    ) !important;
    background-image: -webkit-linear-gradient(
        left,
        #ffcc88,
        #ffcc88 190px,
        #ffffff 190px,
        #ffffff 196px,
        #007ba6 196px,
        #007ba6 100%
    ) !important;
    background-image: -moz-linear-gradient(
        left,
        #ffcc88,
        #ffcc88 190px,
        #ffffff 190px,
        #ffffff 196px,
        #007ba6 196px,
        #007ba6 100%
    ) !important;
    background-image: -o-linear-gradient(
        left,
        #ffcc88,
        #ffcc88 190px,
        #ffffff 190px,
        #ffffff 196px,
        #007ba6 196px,
        #007ba6 100%
    ) !important;
}

#sidenav {
    float: left !important;
    overflow: hidden !important;
    display: block !important;
    width: 190px !important;
    background: #ffcc88 !important;
}

#startseite,
#rezension,
#forum,
#lesercharts,
#impressum,
#links,
#hilfe,
#suche,
#archivliste,
#specialcontent,
#userreg {
    float: none !important;
    overflow: hidden !important;
    display: block !important;
    position: relative !important;
    width: auto !important;
    margin: 0px 0px 0px 196px !important;
    padding: 0px !important;
    background: #007ba6 !important;
}

/*****************************************************************************
 * siteheader
 ****************************************************************************/

#siteheader h1 {
    width: 500px !important;
    height: 100px !important;
    margin: 0px auto !important;
    background: #eeeeee !important;
    background-image: url("http://www.plattentests.de/img/plattentests2.gif") !important;
}

#siteheader h1 span {
    display: none !important;
}

#topnav {
    height: auto !important;
    margin-top: 3px !important;
    background: transparent !important;
}

#topnav a {
    font-size: 13px !important;
    font-weight: bold !important;
    color: #ffffff !important;
}

#topnav a:hover {
    text-decoration: underline !important;
    color: #000069 !important;
    background: #ffffff !important;
}

#topnav ul {
    display: table !important;
    table-layout: fixed !important;
    border-collapse: collapse !important;
    width: 100% !important;
}

#topnav li {
    float: none !important;
    width: auto !important;
    height: auto !important;
    position: static !important;
    display: table-cell !important;
    text-align: center !important;
    padding: 3px !important;
    border-left: 7px solid #ffffff !important;
    border-right: 7px solid #ffffff !important;
    line-height: normal !important;
    background: #000069 !important;
}

#topnav .start,
#topnav .hilfe {
    border: 0px solid red !important;
}

#topsearch,
#topwerbung {
    display: none !important;
}

/*****************************************************************************
 * sidenav
 ****************************************************************************/

#sidenav a {
    color: #383838 !important;
}

#sidenav a:hover {
    color: #ffffff !important;
}

#sidenav h2 {
    margin: -5px -5px 5px -5px !important;
    text-align: left !important;
}

#sidenav h3 {
    margin-top: 19px !important;
    font-size: 12px !important;
    color: #000000 !important;
}

#sidenav ul {
    margin: 0px !important;
    padding: 0px !important;
}

#sidenav li {
    width: auto !important;
    margin: 0px !important;
    padding: 0px !important;
    background: transparent !important;
}

#sidenav form {
    margin: 0px !important;
}

#sidenav .box {
    position: static !important;
    background: #ffcc88 !important;
    margin: 0px !important;
    padding: 5px !important;
}

#sidenav .specials,
#sidenav .archiv,
#sidenav .amazon {
    border-top: 6px solid #ffffff !important;
}

#sidenav .archiv li:before {
    content: "- ";
    color: #000000 !important;
}

/*****************************************************************************
 * sitefooter
 ****************************************************************************/

#sitefooter {
    width: auto !important;
    padding: 3px 25% !important;
    font-size: 12px !important;
    color: #808080 !important;
    background: #ffffff !important;
}

#sitefooter a {
    color: #808080 !important;
    text-decoration: none !important;
}

#sitefooter a:hover {
    color: #000000 !important;
}

#sitefooter p {
    margin: 0px !important;
}

#sitefooter p:last-child a:first-child {
    text-decoration: underline !important;
}

/*****************************************************************************
 * startseite
 ****************************************************************************/

#startseite .mehr {
    display: inline-block !important;
    width: 12px !important;
    height: 13px !important;
    background-image: url("http://www.plattentests.de/cover/mehr.gif") !important;
}

#startseite .mehr span {
    display: none !important;
}

#startseite h3 a {
    text-decoration: none !important;
    color: #000069 !important;
}

#startseite h3 a:hover {
    color: #ffffff !important;
}

#startseite h3 {
    font-size: 16px !important;
}

#startseite h4 {
    font-style: italic !important;
}

#startseite .adw {
    float: none !important;
    position: relative !important;
    width: auto !important;
    height: auto !important;
    min-height: 183px !important;
    padding: 0px !important;
    background: #ffcc88 !important;
    border-bottom: 8px solid #ffffff !important;
}

#startseite .adw h3 {
    margin: 8px 13px 0px 172px !important;
}

#startseite .adw h4 {
    margin: 0px 13px 0px 186px !important;
}

#startseite .adw p {
    margin: 0px 13px 13px 186px !important;
    text-align: justify !important;
}

#startseite .adw .cover {
    position: absolute !important;
    width: 140px !important;
    height: 140px !important;
    top: 33px !important;
    left: 8px !important;
}

#startseite .newsletter {
    display: none !important;
}

#akt_highlights {
    width: auto !important;
    margin: 0px 0px 8px 0px !important;
}

#akt_highlights p {
    text-align: justify !important;
}

#akt_highlights .a,
#akt_highlights .b {
    width: 50% !important;
    height: auto !important;
    min-height: 111px !important;
    margin: 0px !important;
    padding: 0px !important;
    background: transparent !important;
}

#akt_highlights .cover {
    position: absolute !important;
    width: 95px !important;
    height: 95px !important;
    top: 8px !important;
}

#akt_highlights .a .cover {
    right: 13px !important;
}

#akt_highlights .a h3 {
    margin: 8px 115px 0px 8px !important;
}

#akt_highlights .a h4,
#akt_highlights .a p {
    margin: 0px 115px 0px 23px !important;
}

#akt_highlights .b .cover {
    right: 8px !important;
}

#akt_highlights .b h3 {
    margin: 8px 110px 0px 13px !important;
}

#akt_highlights .b h4,
#akt_highlights .b p {
    margin: 0px 110px 0px 28px !important;
}

#vorschau,
#weiteraktuell {
    width: 50% !important;
    margin: 0px !important;
}

#vorschau {
    margin-left: -6px !important;
}

#vorschau ul {
    margin: 10px 30px 10px 36px !important;
    padding: 0px !important;
}

#weiteraktuell {
    margin-right: -5px !important;
}

#weiteraktuell h2 {
    padding-right: 10px !important;
}

#weiteraktuell ul {
    margin: 10px 35px 10px 30px !important;
    padding: 0px !important;
}

#startwerbung {
    padding: 16px 0px !important;
}

/*****************************************************************************
 * rezension
 ****************************************************************************/
 
#rezension .rezi:before {
    content: "Information";
    display: block;
    margin-left: -153px !important;
    padding: 5px;
    font-size: 13px;
    font-weight: bold;
    text-align: right;
    color: #ffffff;
    background: #000069;
}

#rezension .rezi {
    float: none !important;
    display: block !important;
    width: auto !important;
    min-height: 175px !important;
    height: auto !important;
    margin: 0px 180px 0px 0px !important;
    padding: 0px 0px 0px 153px !important;
    border-right: 8px solid #ffffff !important;
    background: transparent !important;
}

#rezension .rezi h2 {
    margin: 5px 5px 0px 0px !important;
    padding: 0px !important;
    text-align: left !important;
    font-size: 20px !important;
    color: #000000 !important;
    background: transparent !important;
}

#rezension .rezi .cover {
    position: absolute !important;
    width: 140px !important;
    height: 140px !important;
    top: 30px !important;
    left: 5px !important;
}

#rezension .rezi p {
    margin: 0px 0px 0px 0px !important;
}

#rezension .rezi .bewertung {
    float: none !important;
    display: inline-block !important;
    min-width: 150px !important;
    width: 48% !important;
    margin-top: 18px !important;
    font-weight: bold !important;
}

#rezension .rezi p:last-child {
    margin: 5px 5px 5px 0px !important;
}

#rezension .bewertung.b0 { background-image: url("http://www.plattentests.de/img/0_10.gif") !important; }
#rezension .bewertung.b1 { background-image: url("http://www.plattentests.de/img/1_10.gif") !important; }
#rezension .bewertung.b2 { background-image: url("http://www.plattentests.de/img/2_10.gif") !important; }
#rezension .bewertung.b3 { background-image: url("http://www.plattentests.de/img/3_10.gif") !important; }
#rezension .bewertung.b4 { background-image: url("http://www.plattentests.de/img/4_10.gif") !important; }
#rezension .bewertung.b5 { background-image: url("http://www.plattentests.de/img/5_10.gif") !important; }
#rezension .bewertung.b6 { background-image: url("http://www.plattentests.de/img/6_10.gif") !important; }
#rezension .bewertung.b7 { background-image: url("http://www.plattentests.de/img/7_10.gif") !important; }
#rezension .bewertung.b8 { background-image: url("http://www.plattentests.de/img/8_10.gif") !important; }
#rezension .bewertung.b9 { background-image: url("http://www.plattentests.de/img/9_10.gif") !important; }
#rezension .bewertung.b10 { background-image: url("http://www.plattentests.de/img/10_10.gif") !important; }

#rezension .charts {
    float: right !important;
    display: inline-block !important;
    position: absolute !important;
    top: 0px !important;
    right: 0px !important;
    width: 180px !important;
    min-height: 175px !important;
    height: auto !important;
    margin: 0px !important;
    padding: 0px !important;
    background: #ffcc88 !important;
}

#rezension .charts form:before {
    content: "Abstimmen: Sag Deine Meinung im Forum und gib Deine Bewertung für die Lesercharts ab.";
    display: block;
    text-align: justify;
}

#rezension .charts form,
#rezension .charts p {
    margin: 5px !important;
}

#rezension .charts select {
    display: block !important;
}

#rezitext {
    float: none !important;
    clear: both !important;
    display: block !important;
    width: auto !important;
    margin: 0px 0px 32px 0px !important;
    border-top: 8px solid #ffffff !important;
    background: transparent !important;
}

#rezitext h3 {
    margin: 44px auto 17px auto !important;
    font-size: 20px !important;
}

#rezitext p {
    margin: 14px 13px !important;
    text-align: justify !important;
    font-size: 14px !important;
    line-height: 16px !important;
}

#rezitext p.autor {
    text-align: center !important;
}

#reziwerbung {
    display: none !important;
}

#rezihighlights,
#rezitracklist,
#reziref {
    float: left !important;
    clear: left !important;
    display: block !important;
    width: 65% !important;
    margin: 7px 8px !important;
    padding: 0px !important;
    background: transparent !important;
}

#surftipps {
    float: none !important;
    clear: both !important;
    display: inline-block !important;
    width: 30% !important;
    margin: 7px 8px 7px 0px !important;
    padding: 0px !important;
    background: transparent !important;
}

#rezension h4 {
    margin: 0px !important;
    font-style: normal !important;
}

#rezension h4:after {
    content: ":";
}

#rezitracklist h4 {
    display: inline !important;
}

#rezitracklist p,
#reziref p {
    margin: 0px !important;
}

#rezension ul,
#rezension ol,
#rezension li {
    display: inline !important;
    margin: 0px !important;
}
#surftipps li {
    display: block !important;
}

#rezihighlights li:after,
#rezitracklist li:after {
    content: "; ";
}

#rezihighlights li:last-child:after {
    content: "" !important;
}

#rezitracklist li:last-child:after {
    content: "" !important;
}

#reziweitere,
#reziforum {
    display: none !important;
}

/* werbung */
#rezension div[style] {
    clear: both !important;
    overflow: hidden !important;
    margin: 16px auto !important;
}

/*****************************************************************************
 * charts
 ****************************************************************************/

#lesercharts {
    position: relative !important;
}

#lesercharts h2:after {
    content: "von Plattentests online";
    margin-left: 4px;
}

#lesercharts h2 + p {
    margin-left: 20px !important;
    margin-right: 200px !important;
}

#lesercharts p.stand {
    position: absolute !important;
    top: 36px !important;
    right: 22px !important;
    margin: 0px !important;
    font-weight: bold !important;
}

#lesercharts > table {
    margin-top: 26px !important;
    margin-bottom: 8px !important;
}

#lesercharts thead,
#lesercharts .voe {
    display: none !important;
}

#lesercharts tr {
    background: transparent !important;
    display: block !important;
    margin: 2px 20px !important;
    padding: 1px 5px 1px 75px !important;
}

#lesercharts td {
    padding: 0px !important;
    text-align: left !important;
    font-size: 12px !important;
    border-width: 0px !important;
}

#lesercharts .rang:after {
    content: ".";
    margin-right: 3px;
}

#lesercharts .topten tr {
    position: relative !important;
    height: auto !important;
    min-height: 70px !important;
}

#lesercharts .topten .rang,
#lesercharts .topten .album {
    font-weight: bold !important;
}

#lesercharts .topten img {
    position: absolute !important;
    top: 1px !important;
    left: 1px !important;
    width: 70px !important;
    height: 70px !important;
}

#lesercharts .topten .wert {
    display: block !important;
    margin-top: 14px !important;
}

#lesercharts .topten .wert:before {
    content: "Eure Ø-Wertung:";
    margin-right: 3px;
}

#lesercharts .topten .stimmen {
    display: block !important;
}

#lesercharts .topten .stimmen:after {
    content: "Leser stimmten für dieses Album.";
    margin-left: 3px;
}

#lesercharts tbody:last-child .wert:before {
    content: "(";
    margin-left: 3px;
}

#lesercharts tbody:last-child .wert:after {
    content: "-";
    margin: 0px 3px;
}

#lesercharts tbody:last-child .stimmen:after {
    content: "Stimmen)";
    margin-left: 3px;
}

/*****************************************************************************
 * impressum
 ****************************************************************************/

#impressum {
    padding: 0px 48px 8px 58px !important;
}

#impressum > * {
    margin-left: 0px !important;
    margin-right: 0px !important;
}

#impressum h2 {
    margin: 0px -48px 0px -58px !important;
}

#impressum h3 {
    margin: 8px -48px 8px -58px !important;
    font-size: 20px !important;
}

#impressum > h4 {
    margin-bottom: 0px !important;
    font-size: 16px !important;
}

#impressum .urlmail {
    text-align: left !important;
}

#impressum > p:nth-of-type(1),
#impressum > p:nth-of-type(2),
#impressum > p:nth-of-type(3),
#impressum > p:nth-of-type(4),
#impressum > p:nth-of-type(5),
#impressum > p:nth-of-type(6),
#impressum > p:nth-of-type(7) {
    font-size: 14px !important;
}

#impressum table {
    width: 100% !important;
}

#impressum th,
#impressum tr,
#impressum td {
    background: transparent !important;
    border: 0px !important;
}

#impressum font {
    color: #000000 !important;
}

#impressum table:last-of-type {
    margin-bottom: 20px !important;
}

#impressum table ~ h4 {
    margin-bottom: 20px !important;
}

#impressum h4 a {
    text-decoration: none !important;
}

#impressum table ~ p,
#impressum li {
    text-align: justify !important;
}

#impressum > h5 {
    margin: 18px 0px !important;
    font-size: 14px !important;
    font-weight: normal !important;
}

#impressum ul {
    list-style: disc !important;
}

#impressum h5 + p,
#impressum ul + p {
    margin-left: 40px !important;
}

#impressum h5 + p + ul {
    list-style: none !important;
}

#impressum li > ul {
    padding-left: 0px !important;
}

/*****************************************************************************
 * hilfe
 ****************************************************************************/

#hilfe {
    padding: 0px 18px 8px 18px !important;
    font-size: 14px !important;
}

#hilfe h2 {
    margin: 0px -18px 0px -18px !important;
}

#hilfe h2:after {
    content: "Häufig gestellte Fragen zu Plattentests online";
    display: block;
    margin: 5px -5px -5px -5px;
    padding: 8px 8px 0px 18px;
    font-size: 20px;
    text-align: center;
    color: #000000;
    background: #007BA6;
}

#hilfe ul {
    list-style: disc !important;
}

#hilfe h3 {
    font-size: 16px !important;
}

#hilfe h3 a {
    text-decoration: none !important;
}

#hilfe p {
    text-align: justify !important;
    line-height: 16px !important;
}

#hilfe p,
#hilfe p + ul {
    margin-left: 40px !important;
    margin-right: 40px !important;
}

#hilfe strong {
    font-size: 12px !important;
    font-style: italic !important;
}

#hilfe .inhalt {
    margin-top: 16px !important;
    font-size: 16px !important;
    font-weight: bold !important;
}

#hilfe .inhalt a {
    color: #000069 !important;
    text-decoration: none !important;
}

#hilfe .inhalt a:hover {
    color: #ffffff !important;
}

#hilfe .inhalt + p {
    margin: auto 0px !important;
}

/*****************************************************************************
 * links
 ****************************************************************************/

#links .inhalt {
    margin: 12px 8px !important;
    font-size: 16px !important;
    font-weight: bold !important;
    list-style: disc !important;
}

#links .inhalt a {
    text-decoration: none !important;
    color: #000069 !important;
}

#links .inhalt a:hover {
    color: #ffffff !important;
}

#links h3 {
    margin: 0px 8px !important;
    padding: 10px 0px 0px 0px !important;
    border-top: 2px solid white !important;
}

#links h3 a {
    font-size: 16px !important;
    text-decoration: none !important;
}

#links table {
    margin: 8px 46px !important;
}

#links td {
    border: 0px !important;
}

/*****************************************************************************
 * suche
 ****************************************************************************/

#suche h3 {
    margin: 16px 8px 0px 8px !important;
}

#suche > p,
#suche > iframe,
#suche > div {
    margin: 8px !important;
}

#suche ul {
    margin: 0px 8px !important;
    padding: 0px !important;
    list-style: none !important;
}

/*****************************************************************************
 * archivliste
 ****************************************************************************/

#archivliste {
    padding: 0px 18px 8px 18px !important;
    font-size: 14px !important;
}

#archivliste a:hover {
    text-decoration: none !important;
}

#archivliste h2 {
    margin: auto -18px !important;
}

#archivliste h3 {
    margin: 24px 0px 16px 0px !important;
    font-size: 12px !important;
}

#archivliste h4 {
    margin: 30px 0px 0px 0px !important;
    font-size: 14px !important;
}

#archivliste h4 a {
    text-decoration: none !important;
}

#archivliste ul {
    margin: 0px 0px 0px 0px !important;
    padding: 0px !important;
    list-style: none !important;
}

/* für sort=adw */
#archivliste li {
    font-weight: bold !important;
}

#archivliste li a {
    font-weight: normal !important;
}

/*****************************************************************************
 * specialcontent
 ****************************************************************************/

#specialcontent {
    font-size: 14px !important;
}

#specialcontent a:hover {
    text-decoration: none !important;
}

#specialcontent h2[align] {
    margin: 0px !important;
    padding: 0px !important;
    font-size: 24px !important;
    text-align: center !important;
    color: #000000 !important;
    background: transparent !important;
}

#specialcontent h1 {
    margin: 22px 0px !important;
    font-size: 32px !important;
    background: transparent !important;
}

#specialcontent p {
    font-size: 14px !important;
}

#specialcontent h4 {
    color: #000000 !important;
    background: transparent !important;
}

#specialcontent tr,
#specialcontent th,
#specialcontent td {
    color: #000000 !important;
    background: transparent !important;
    border: 0px !important;
}

#specialcontent font {
    color: #000000 !important;
}

/*****************************************************************************
 * forumsübersicht
 ****************************************************************************/

#forum {
    padding: 5px !important;
}

#forum h2 {
