css:
/*************************************************************************
 *                                                                       *
 *                            SUBDUED THEME                              *
 *                                                                       *
 *  Author:     Leif Frydenlund-Clark                                    *
 *                                                                       *
 *  Website:    craigslist.org                                           *
 *                                                                       *
 *  Comment:    This theme is for Craigslist. Its intention is           *
 *              to reduce strain on the eyes at night as well as be      *
 *              aesthetically pleasing. This is a base theme.            *
 *                                                                       *
 *  Disclaimer: This theme may not always provide the desired results    *
 *              due to the large, dynamic nature of its intended domain. *
 *              Some updates to the Craigslist's website may cause       *
 *              errors in this theme. Please leave comments in the       *
 *              forum this style is contained in.                        *
 *                                                                       *
 *************************************************************************/

/*                           THEME COLORS
    
    bleached red:    #983748   rgb(152, 55, 72)
    subdued blue:    #234565   rgb(35, 65, 101)
    
    * various grays used: #111, #222, #333, #777...
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("craigslist.org") {

    /* sets backgrounds and colors */
    html,
    body {
        background: 
        -moz-linear-gradient(left,  rgba(0,0,0,0.5) 0%, 
        /* for firefox */           rgba(0,0,0,0.4) 2%, 
                                    rgba(0,0,0,0) 13%, 
                                    rgba(0,0,0,0) 87%, 
                                    rgba(0,0,0,0.4) 98%, 
                                    rgba(0,0,0,0.5) 100%),
                                    
        url('/*[[backgroundImage]]*/') !important;
                                    
        background:         
        -webkit-linear-gradient(left, rgba(0,0,0,0.5) 0%,
        /* for chrome */              rgba(0,0,0,0.4) 2%,
                                      rgba(0,0,0,0) 13%,
                                      rgba(0,0,0,0) 87%,
                                      rgba(0,0,0,0.4) 98%,
                                      rgba(0,0,0,0.5) 100%),

        url('/*[[backgroundImage]]*/') !important;
        color: /*[[textColor]]*/ !important;
    }
    
    /* sets horizontal rule color */
    hr {
        color: #333 !important;
    }
    
    /* sets emphasized text color */
    em {
        color: #983748 !important;       /* bleached red */
    }
    
    /* sets font element color */
    font {
        color: #983748 !important;       /* bleached red */
    }

    textarea {
        background: #333 !important;
        color: #BBB !important;
    }

    fieldset {
        background: #111 !important;
        border-radius: 4px !important;
    }
    
    div {
        background: transparent !important;
    }

    /* sets smaller element colors */
    p,
    label {
        color: #777 !important;
    }
    
    td {
        background: transparent !important;
    }

    /* sets selected city background */
    #satabs b {
        background: #111 !important;
        border-radius: 3px !important;
        border: none !important;
    }

    /* sets panel backgrounds and borders */
    .state_delimiter,                    /* state header CITIES */
    .continent_header,                   /* continent header CITIES */
    #leftbar,                            /* left panel */
    #rightbar,                           /* right panel */
    #topban,                             /* top banner */
    .ban {                               /* heading banner */
        background: /*[[panelColor]]*/ !important;
        border: none !important;
        border-top: none !important;
        border-radius: 4px;
    }
    
    /* removes banner inherited background above calendar */
    #calttl {                             /* calendar title */
        background: transparent !important;
    }
    
    /* sets banner background color */
    .ban {                                /* banner */
        background: /*[[bannerColor]]*/ !important;
    }
    
    /* links */
    a,
    h5.hot,
    #leftlinks li,
    #postlks li {
        background: transparent !important;
        color: /*[[textColor]]*/ !important;
        border: none !important;
        border-radius: 4px;
        text-decoration: none !important;

    }

    /* sets visited link color */
    a:visited {
        color: #CCC !important;
    }
    
    /* sets panel and category links background and color on hover */
    .home a:hover,                       /* home links CITIES */
    h5:hover.hot,                        /* header hover */
    #postlks li:hover,                   /* post link hover */
    #leftlinks li:hover,                 /* left link hover */
    .s a:hover,                          /* panel links */
    .cats a:hover {                      /* category links */
        background: #777 !important;
        color: #222 !important;
    }
    
    /* sets category link padding */
    .home a,                             /* home links CITIES */
    h5.hot,                              /* header */
    .s a,                                /* anchor */
    .cats a {                            /* category links */
        padding-left: 4px !important;
        padding-right: 4px !important;
    }
    
    /* sets left panel link color on hover */
    #leftlinks li:hover a,               /* left panel links */
    #postlks li:hover a {                /* post panel links */
        color: #222 !important;
    }
    
    /* sets categegory heading banner padding */ 
    h4.ban {                             /* category heading banner */
        padding: 3px !important;
    }
    
    /* sets panel heading banner style */ 
    h5.ban {                             /* panel heading banner */
        background: #222 !important;
        color: /*[[textColor]]*/ !important;
        margin: 2px !important;
    }
    
    /* sets footer background */
    footer,
    #footer {                            /* footer */
        background: #070707 !important;
        border: none !important;
        position: relative !important;
        bottom: 0 !important;
        width: 100% !important;
    }

    /* sets highlighted div backgrounds */
    .highlight {                        /* highlighted */
        background: #983748 !important;
    }

    /* sets section background */
    section.body {
        background: rgba(0, 0, 0, .35) !important;
        border-radius: 3px;
        padding: 5px !important;
    }

    aside.tsb {
        background: #111 !important;
        border: none !important;
        border-radius: 3px !important;
    }

                                  /* POSTING */

    /* sets post reply box background and color */
    .posting.gms {                            /* posting */
        background: #161616 !important;
        color: #3A4006 !important;
    }
    
    /* sets required option labels lighter color */
    .req {                                /* required option labels */
        color: #74800D !important;
        border-color: #74800D !important;
    }
    
    /* sets required input borders and colors */
    input.req,                            /* required inputs */
    textarea.req {                        /* required textarea */
        border: 2px inset #222 !important;
        color: #BBB !important;
    }
    
    /* sets posting review titles colors back */
    .postingtitle,                        /* post title */
    .posting .bchead,                     /* bread crumb */
    #userbody {                           /* post body */
        color: #777 !important;
    }

    #ci {
        border: none !important;
    }
    
                               /* FEEDS */
    
    /* sets feed content background and border radius */
    #feedBody,                            /* feed body */
    #feedHeaderContainer {                /* feed container */
        background: #222 !important;
        border-radius: 8px !important;
    }

                             /* LOGIN BOX */

    /* sets login box background */
    #loginBox table {                     /* login box body */ 
        background: #1B1B1B !important;
    }
    
    /* sets login box background transparent */
    #loginBox {                           /* login box container */
        background: transparent !important;
    }

    /* sets login box heading background */
    #loginBox h4 {                        /* login box heading */
        background: #161616 !important;
    }

                            /* PAGE MANAGER */    
    /* styles page manager */
    #paginator {                          /* page manager */
        background: #333 !important;
        box-shadow: inset 0 0 3px #111 !important;
        border: 1px solid #555 !important;
    }
    
    /* sets manage link color */
    .managebtn {                          /* manage link */
        color: white !important;
        text-decoration: none !important;
    }
 
                             /* CALENDAR */
                             
    /* sets calendar background*/ 
    table.cal {                          /* calendar */
        background: #777 !important;
        box-shadow: inset 0 0 5px #222;
    }
    
    /* sets calendar day backgrounds*/
    table.cal td {                       /* calendar day td */
        background: #222 !important;
    }
    
    /* sets calendar anchor color */
    table.cal a {                        /* calendar day anchor */
        color: /*[[textColor]]*/ !important;
    }
    
    /* sets calendar heading color*/
    table.cal th {                       /* calendar heading */
        color: #222 !important;
        text-shadow: 1px 1px #AAA;
    }
    
    /* sets calendar current day background and color*/
    table.cal .tdy a {                   /* today */
        background: #777 !important;
        color: #222 !important;
        box-shadow: inset 0 0 5px #333;
    }

                               /* VIEWS */

    /* styles the view buttons */
    button {
        background: #777 !important;
        color: #222 !important;
        border-color: #888 !important;
        text-shadow: 1px 1px 0 #888;
    }
    
    button.down {
        background: #222 !important;
        color: #777 !important;
        border-color: #111 !important;
        text-shadow: -1px -1px 0 #111;
    }

    /* sets grid view styles */
    .toc p.row {
        border: none !important;
        background: rgba(0, 0, 0, .35);
        border-radius: 3px;
        padding: 10px !important;
        margin: 1px !important;
    }

    /* fixes image location for new style */
    p.row span.i {
        top: 0 !important;
        border: none !important;
    }

    /* sets map view items found */
    .itemsfound b {
        background: transparent !important;
        color: #983748 !important;
        text-shadow: 1px 1px 0 #111 !important;
        border: none !important;
    }

                           /* FORMAT TOGGLE */

    /* sets format toggle colors */
    .fsel.a {                            /* format toggle */
        background: transparent !important;
        color: /*[[textColor]]*/ !important;
    }
    
    /* sets format toggle colors on hover */
    .fsel.a:hover {                      /* format toggle */
        background: #777 !important;
        color: #222 !important;
        border-radius: 4px;
    }    
    
    /* sets selected format toggle background and color */
    .fsel {                              /* selected format toggle */
        background: #161616 !important;
        color: #222 !important;
        border: none !important;
        border-radius: 4px !important;
        padding: 4px !important;
    }

                           /* CATEGORY VIEW */

    /* centers and sets rows width */
    #toc_rows {                          /* post rows */
        width: 80%;
        margin: 0 auto;
    }

    /* removes top cookie crumb background */
    .bchead {                            /* crumb */
        background: transparent !important;
        border: none !important;
    }
    
    /* styles search field set */
    #searchfieldset {                    /* category search field */
        background: #111 !important;
        border: 1px solid #333 !important;
        border-radius: 4px;
        width: 85%;
        margin: 0 auto;
        
    }
    
    /* sets inner search table background */
    #searchtable {                       /* search table */
        background: #111 !important;
        border: none !important;
    }
    
    /* ensures search form does not spill out */
    #searchform {                        /* search form container */
        overflow: hidden !important;
    }
    
    /* sets 'pic' and 'img' label color */
    .p {
        color: #234565 !important;       /* subdued blue */
        font-weight: 900;
    }
    
    /* sets date color */
    .itemdate {                          /* date text */
        color: #983748;                  /* bleached red */
    }
    
    .i {                                 /* images */
        position: relative;
        bottom: 1em;
    }
    
    #floater img {
        border: 1px solid black !important;
    }
    
    /* removes post anchor underlines */
    .toc a {                             /* post anchors */
        text-decoration: none !important;
    }
    
    /* lightens anchor text on hover */
    .toc a:hover {                       /* post anchors */
        color: #BBB !important;
    }
    
    /* indents post rows */
    .toc .row {                          /* post rows */
        margin-left: 20px !important;
    }

        /* sets reply option panel background and border */
    #reply_options {                     /* reply option panel */
        background: #111!important;
        border: 1px solid #777 !important;
    }
    
    /* styles email input */
    #anonemail {                         /* email input */
        background: #222 !important;
        border: 1px solid #777 !important;
    }
    
    /* sets flag container border */
    #flags {                             /* flag container */
        border: 1px solid #777 !important;
        background: #222 !important;
    }

    /* styles view tabs */
    .modetabrow b {                                  /* current tab */
       border-radius: 4px !important;
       background: #111 !important;
       border: none !important;
    }
    
    /* removes tab bottom border */
    .modetabrow {                        /* tab container */
        border: none !important;
    }

                              /* CITIES */
    
    
    /* centers continent header text */
    .continent_header {                  /* continenet header */
        text-align: center;   
    }
    
    /* sets state header padding and removes boxshadow */
    .state_delimiter {                   /* state header CITIES */
        padding-left: 5px !important;
        box-shadow: none !important;
        min-height: 12px !important;
    }

                               /* BLOG */
    
    /* sets blog content background */
    #page {                              /* blog content container */
        background: #111 !important;
        border-radius: 8px !important;
        border-right: 1px solid black;
        box-shadow: 3px 3px 5px black;
    }
    
    /* removes blockquote left border fix */
    #page blockquote {               
        border-left-color: transparent !important;
    }
    
    /* fixes header and footer */
    #page #header {                      /* blog header */
        background: black !important;
        border-radius: 8px 8px 0 0 !important;
    }
    
    #page #footer {                      /* blog footer */
        background: black !important;
        border-radius: 0 0 8px 8px !important;
    }
    
    #page #header a {                    /* blog header anchor */
        text-shadow:0 -1px 0 #BBB; 
    }

    /* overrides previous anchor style that adds background */
    #page #header a:hover,               /* header anchor */
    #page #content a:hover {             /* blog content anchors */
        background: transparent !important;
        color: /*[[textColor]]*/ !important;
    }
    
    /* sets fixed follow link background transparent */
    .bsub {                              /* social media link */
        background: transparent !important;
    }
    
    /* sets header colors */
    #page h2 {
        color: /*[[textColor]]*/ !important;
    }
    
    /* styles side links on hover */
    #page ul li ul li:hover {
        background: #777 !important;
        color: transparent !important;
        border-radius: 2px;
    }
    
    /* sets anchor text on hover */
    #page ul li ul li a:hover {
        color: #222 !important;
    }
    
  
                              /* COMMENTS */

    /* styles comments */
    .comment {                           /* comments */
        background: #333 !important;
        border: none !important;
        border-radius: 4px;
    }

    /* sets comment reply box background */
    #comment-form-comment {              /* comment reply box */
        background: #333 !important;
    }
    
    /* sets comment reply box inner container text */
    #comment {                           /* comment reply box inner container */
        color: /*[[textColor]]*/ !important;
        text-shadow: 1px 1px 1px black !important;
    }

                            /* HELP PAGES */

    /* sets panel background */
    #banner {                            /* panel container */
        background: black !important;
    }
    
    /* sets required text color */
    .required {
        color: #74800D !important;
    }

    /* sets side panel color */
    #update,                             /* updates div */
    #banner span {                       /* spans */
        color: /*[[textColor]]*/ !important;
        font-family: sans-serif !important;
    }
    
    /* centers heading */
    #sc h3 {                             /* heading */
        padding: 0 !important;
        text-align: center !important;
    }
    
    /* sets span color */
    #sc #content span {
        color: #983748 !important;       /* bleached red */
    }

                               /* FORUMS */

    /* sets handle (name) color */
    .hnd {                               /* handle */
        color: #234565 !important;       /* subdued blue */
    }
    
    /* sets image label color */
    .R {
        color: #884411 !important;       /* bleached red */
    }
}
