css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



     /*   

	 Dashboard by http://http://rosescreation.tumblr.com/

     Â© Rose's Creation

	 */



	 

	 

	 

div#right_column,

div#right_column:after {background: none !important;}	 

	 

body { background-image:url('http://img231.imageshack.us/img231/7956/fondowt.jpg') !important;

background-color:#fff !important;

background-repeat:repeat !important;

background-position: center top !important;

background-attachment:fixed !important;

color: #29496f !important;

opacity: 1 !important;

font-size: 12px !important;

font-family: georgia !important;

text-align: justify !important;}



img {-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;

transition: all 0.4s linear;}

    

img:hover {opacity:0.6 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;

transition: all 0.4s linear;}   



a:link, a:active, a:visited {color: #936896 !important; text-decoration: none !important;

-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;

transition: all 0.4s linear;} 

a:hover {color: #1e3459 !important; text-decoration: none !important;

-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;

transition: all 0.4s linear;} 



.post_info a:link, .post_info a:active, .post_info a:visited {color: #29496f !important;

-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;

transition: all 0.4s linear; text-decoration: none !important;} 

.post_info a:hover {color: #81507f !important; text-decoration: none !important;

-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;

transition: all 0.4s linear;} 



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: transparent !important;}

.post_info {color: #22365a !important;

  font-size: 12px !important;}

  

.post_question {font-family: 'georgia' !important; font-size: 12px !important; 

background: #c29bc2 !important; color: white !important;}

.post_question_nipple {background: none !important;

width: 0px !important;

height: 0px !important;

border-right: solid 6px transparent !important;

border-bottom: 0px solid transparent !important;

border-left: 0px solid transparent !important;

border-top: 0px solid transparent !important;

margin-top: 0px !important;}



blockquote {border-left: 4px solid #ab3b2c !important; background:none; padding: 5px;

border-radius: 0px 0px 0px 0px; text-shadow: 0px 0px 5px #fff;}











/*tumblr logo*/



#logo {

  width: 0px !important;

  height: 0px !important;

  padding-left: 300px !important;

  padding-top: 85px !important;

  background-image: url("http://img849.imageshack.us/img849/9114/logoxox.png") !important;

  background-repeat: no-repeat !important;}

  

  

#right_column {padding-top: 1px !important;}







/*new post icons bar*/



ol#posts li.post.new_post {background: #ffffff url('http://img37.imageshack.us/img37/1386/navigationgj.jpg')  top center no-repeat !important;

box-shadow: 0px 0px 10px #fff !important;

border: 0px solid #775e6d !important;

border-radius:20px !important;

padding-top:40px!important; margin:0px !important; padding-bottom:0px !important;

font-family:'georgia' !important; text-transform:none!important; font-size:12px!important;

text-shadow: 0px 0px 5px #fff !important;}



.new_post_label_icon{background-image:none !important}



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

    width:0;

    height:70px;

    padding-right:250px;

    opacity: 0;

}







.notification  {background: #bb8fb7 !important; border: none !important; color: #fff !important;}

.notification :link, .notification :active, .notification :visited {color: #955282 !important; text-decoration: none !important; border: none !important;}







.avatar_frame {border: 0px solid #fff !important;background-color:#FFF !important;}



.post_avatar {border: 2px solid #fff !important;

border-radius:5px !important;

box-shadow: 0px 0px 7px #7fd4ed !important;}





/* Place, forme des images */

.image { display: block !important;

margin-left: auto !important;

margin-right: auto !important;

border-radius:0px !important;}



.photoset img {border-radius:0px !important;}







/* RETURN TO TOP */

div.return_to_top_icon{	background: none !important;

background-image: url('http://img441.imageshack.us/img441/7651/frecciazv.png') !important;

width: 70px !important;

height: 70px !important;

margin-top: 0px !important;

border-bottom: solid 0px #fff !important;

border-right: 0px solid transparent !important;

border-left: 0px solid transparent !important;}











/*.selection.nipple - dashboard, user, inbox - "white pages"*/



/*.selection_nipple.white - create new blog, help, preferences*/



/*SELECTION ARROW begin - points to dashboard selection*/

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 4px #ffffff !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}



.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px #ffffff !important;}



/*SIDE ARROWS*/

.arrow {

background: none !important;

width: 0px !important;

height: 0px !important;

border-right: solid 9px #ffffff !important;

border-bottom: 9px solid transparent !important;

border-left: 2px solid transparent !important;

border-top: 9px solid transparent !important;

margin-top: 0px !important;

}

.nipple {

background: none !important;

width: 0px !important;

height: 0px !important;

border-right: solid 6px #ffffff !important;

border-bottom: 6px solid transparent !important;

border-left: 0px solid transparent !important;

border-top: 6px solid transparent !important;

margin-top: 0px !important;

}

.nipple.border {

background: none !important;

width: 0px !important;

height: 0px !important;

border-right: solid 6px #ffffff !important;

border-bottom: 6px solid transparent !important;

border-left: 1px solid transparent !important;

border-top: 6px solid transparent !important;

margin-top: 1px !important;}





/*tags*/

.posts {background-color:#bd9cc2!important; text-transform:none!important}



.tag:hover {-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;

transition: all 0.4s linear; background:#7a4e7c !important; cursor:help; 

padding: 3px; color:white !important; border-radius: 10px;}



.tags{font-size:12px!important;margin-top:0px!important; letter-spacing: 1px !important;

font-family:'trebuchet ms';vertical-align:middle !important;}



.source_url{font-size:12px!important;

text-transform:uppercase;

font-family:'trebuchet ms'!important;

vertical-align:middle !important;

margin-right:4px!important;}

.footer_links{margin-top:20px!important;

vertical-align:middle !important;}



.post_info A,

.post_controls A,

.footer_links.with_tags SPAN A {

color:#333333 !important;}



.token {border: 0px solid #444 !important; 

background-color:#b89ac0!important;}











/*text post message body background*/



.mceContentBody {

  background: white !important;

  color: #727272 !important;

  /*font-family: georgia !important;*/

  font-size: 12px !important;}







/*top content bar - rounded bar with design similar to buttons*/



#content_top {



  width: 0px !important;



  height: 0px !important;



/*uncomment the lines below if you wish to replace the top content image with your own*/



/*  background: url(http://assets.tumblr.com/images/content_top_edit_form.png) !important;  /*replace with your top content bar image url*/



/*  width: 900px !important; /*replace with your top content bar image width*/



/*  height: 25px !important; /*replace with your top content bar image height*/



}







/*bottom content bar - rounded bar with design similar to buttons*/



#content_bottom {



  width: 0px !important;



  height: 0px !important;



/*uncomment the lines below if you wish to replace the bottom content image with your own*/



/*  background: url(http://assets.tumblr.com/images/content_top_edit_form.png) !important;  /*replace with your bottom bar content image url*/



/*  width: 900px !important; /*replace with your bottom content bar image width*/



/*  height: 25px !important; /*replace with your bottom content bar image height*/



}







/*large rectangular area containing text message, controls and form fields = content container frame*/



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  padding-top: 25px !important; /*pads the content container frame to make up for the missing top content bar */



  padding-bottom: 25px !important; /*pads the content container frame to make up for the missing bottom content bar*/



   border-radius: 20px !important; /*rounds the corners of the content container frame for appearance*/



}







/*some text in the right column that was too hard to read*/



LABEL, .count {  color: #bb9abf !important;}











#new_post .arrow {  margin-left: -0px !important;}



 }
