css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wykop.pl") {
    .bgfff .brbottdf {
        border:none!important;
    }
    .bgfff .brbottbd {
        border-bottom: 1px solid rgb(232, 232, 232)!important;
    }
    
    .bgfff .button {
        box-shadow:none!important;
        border:1px solid #ddd!important;
        transition:0.3s;
    }
    
    .bgfff .button:hover {
        box-shadow:0 1px 5px #ccc!important;
        transition:0.3s;
    }
    
    .bgfff .button.selected:hover {
        box-shadow:inset 0 1px 2px #ccc!important;
    }
    
    .bgfff .button.blue {
        background: none repeat scroll 0% 0% #3d89af!important;
        border:1px solid #2f6986!important;
        color:#fff!important;
    }
    
    .bgfff ul.comments > li.subcomment > blockquote {
        margin-left:84px!important;
    }
    
    .bgfff ul.comments > li > blockquote {
        margin-left:40px!important;
    }
    
    .bgfff ul.comments .hlight header {
        background:#eee!important;
        border:1px solid #e3e3e3!important;
        border-left:none!important;
    }
    
    .bgfff ul.comments header {
        padding-left:18px!important;
        border:1px solid #f3f3f3!important;
        border-left:none!important;
    }
       
    .bgfff ul.comments > li p {
        margin: 6px 6px 3px 18px!important;
    }
    
    .bgfff #comments-list-entry p {
        padding-bottom:6px !important;
    }
    
    .bgfff .brc8 {
        box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.2)!important;
        border:0!important;
    }
    
}
