css:
@-moz-document domain("novosti-kosmonavtiki.ru") {

/*** Главная ***/ /*** все страницы ***/
body, html
{	background: #DCDCC8 none !important;
	color: #000000 !important;
	font: 12px "Verdana","Arial","Helvetica",sans-serif !important;
}
/*** Головной блок, все страницы ***********************************************************************************************/
#header
{	background-image: none !important;
	left: 0% !important;	
	width: 100% !important;
	height: 125px !important;
	margin: 0px 0px 0px 0px !important;
}
/*** логотип ***/
#logo
{	top: 10px !important;
	left: 10px !important;
	width: 207px !important;
}
/*** верхнее меню ***/
#topmenu
{	background-color: #2C2C18 !important;
	top: 12px !important;
	left: 230px !important;
	width: 442px !important;
	height: 64px !important;
}
/*** сдвиг кнопок относительно фона меню ***/
#topmenu div
{	margin: -4px -9px 0px -8px !important;
}
/*** номер телефона ***/
#phone
{	top: 65px !important;
	left: 10px !important;
}
#knopki
{	top: 95px !important;
	left: 10px !important;
}
#knopki a
{	width:30px !important;
	height: 15px !important;
}
#knopki1 { 
background-position: 0px -5px !important;
}
#knopki2 { 
background-position: -40px -5px !important;
}
#knopki3 { 
background-position: -82px -5px !important;
}
/*** быстрый поиск, позиция ***/
#search-sform
{	top: 90px !important;
	left: auto !important;
	right: 200px !important;
}
/*** быстрый поиск, запрос ***/
#search-sform form input.q
{	background-color: #C8C8B4 !important;
	border: 1px solid #006450 !important;
	color: #000000 !important;
	font: 12px "Verdana","Arial","Helvetica",sans-serif !important;
}
/*** быстрый поиск, кнопка ***/
#search-sform form input.submit
{	background-color: #006450 !important;
	top: 0px !important;
	left: 135px !important;
	padding: 1px 5px 1px 5px !important;
}
/*** ccылки ***/
a
{	color: #0000dd !important;
	font-family: "Verdana","Arial","Helvetica",sans-serif !important;
	text-decoration: none !important;
}
/*** ccылки с наведённым указателем ***/
a:hover
{	color: #000077 !important;
	text-decoration: underline !important;
}
/*** войти\регистрация ***/
#login
{	top: 92px !important;
	left: auto !important;
	right: 10px !important;
}
/*** войти, логин\пароль ***/
#login-form-window,
#forum-login-form-window
{	background-color: #C8C8B4 !important;
	border: 1px solid #006450 !important; 
	color: #000000 !important;
}
/*** Центральный блок **********************************************************************************************************/
#center
{ 	background: #C8C8B4 none !important;
	left: 1% !important;
	width: 98% !important;
	margin: 0px 0px 0px 0px !important;
	padding: 10px 0px 10px 0px !important;
	border: 1px solid #006450 !important;
}
/*** левая колонка ***************************/
#leftcol
{	width: 22% !important;
	min-width: 150px !important;
	margin: 0px 0.5% 0px 0% !important;
	padding: 0px 0.5% 0px 1.5% !important;
	border-right: 1px solid #006450 !important;
}
#leftcol div h3
{	color: #bb4C00 !important;
	font-weight: bold !important;
}
#leftcol div.left_news p.news-item span.news-date-time
{	color: #000000 !important;
}
#leftcol div.left_news h3 a,
#leftcol div.left_news p.news-item a
{	color: #0000DD !important;
}
#leftcol div.left_news h3 a:hover,
#leftcol div.left_news p.news-item a:hover
{	color: #000077 !important;
}
/*** правая колонка **************************/
#wa
{	width: 66% !important;
	padding: 0px 0px 50px 4% !important;
	border-left: 1px solid #006450 !important;
}
#wa h1
{	color: #bb4C00 !important;
}
/*** Блок время, фото, ФИО, должность ***/
#wa div.card
{	width: 33% !important;
	min-width: 120x !important;
	margin: 0px 0% 20px 0% !important;
	font-family: "Arial","Helvetica",sans-serif !important;
}
#wa div.card div
{	border: none !important;
}
/*** время, должность ***/
#wa div.card div p,
#wa div.card p.name span
{	color: #000000 !important;
	font-weight: normal !important;
}
/*** ФИО ***/
#wa div.card p.name
{	width: 120px !important;
	color: #000077 !important;
	font-weight: bold !important;
}
/*** Нижний блок ***************************************************************************************************************/
#footer1,
#footer2
{ 	background: #DCDCC8 none !important;
	left: 0% !important;
	width: 100% !important;
	height: 0px !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
}
#footer2
{	height: 80px !important;	
}
#botmenustr
{	width: 90% !important;
	margin: 0px 0% 0px 0% !important;
	padding: 25px 0% 0px 2% !important;
}
#botmenustr a
{	padding:  0px 1% 0px 1% !important;
	font-weight: bold !important;
	font-size: 11px !important;
}
#botmenustr a.selected
{	color: Gray !important;
	text-decoration: underline !important;
}
#copyright
{	margin: 0px 0px 0px 0px !important;
	padding: 25px 0px 0px 1% !important;
}
#counters
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 1% 5px 0% !important;
}
#bottomline
{	height: 0px !important;
	margin: 0px 0px 0px 0px !important;
}
/*** Журнал ********************************************************************************************************************/
/*** правая колонка **************************/
#wa div.newnomer h1,
#wa div.newnomer h2
{	color: #bb4C00 !important;
}
#wa div.newnomer p
{	margin: 0px 0px 50px 0px !important;
}
#wa div.newnomer div.pred
{	margin: 0px 5% 0px 0% !important;
}
#wa div.newnomer div.pred p
{	margin: 5px 0px 0px 0px !important;
}
#wa div.newnomer div.pred p a
{	font-family: "Arial", "Helvetica", sans-serif !important;
}
/*** Журнал, архив номеров *****************************************************************************************************/
/*** правая колонка **************************/
#wa div.nomera
{	padding: 25px 0px 0px 0% !important;
}
#wa div.nomera div
{	margin: 0px 5% 0px 0% !important;
	height: 209px !important;
}
#wa div.nomera div p
{	margin: 5px 0px 0px 0px !important;
	text-align: center !important;
}
#wa div.nomera div p a
{	font-family: "Arial", "Helvetica", sans-serif !important;
	font-size: 10pt !important;
}
/*** Журнал, номер *************************************************************************************************************/
/*** левая колонка ***************************/
#magindex div p
{	color: #0000dd !important;
}
#magindex div p:hover
{	color: #000077 !important;
	text-decoration: underline !important;
}
#magindex div div p
{	color: #000000 !important;
	margin: 3px 0px 7px 10% !important;
}
#magindex div div p:hover
{	color: #000000 !important;
	text-decoration: none !important;
}
/*** правая колонка **************************/
#wa div.nomer div.article span
{	color: #000000 !important;
}
/*** Журнал, статья **************************/
#wa h3
{	color: #000000 !important;
}
#wa img
{	vertical-align: text-bottom !important;
}
/*** Новости *******************************************************************************************************************/
/*** левая колонка ***************************/
#leftcol div.news-filter form
{	width: 145px !important;
}
/*** правая колонка **************************/
#wa div.news-list
{	background-color: #DCDCC8 !important;
	border: 2px solid #006450 !important;
	width: 85% !important;
}
#wa div.news-list p.news-item
{	margin: 0px 0px 0px 0px !important;
	padding: 5px 1.5% 0px 1.5% !important;
	border-bottom: 1px solid #C8C8B4 !important;
	line-height: 150% !important;
}
#wa div.news-list p.news-item span.news-date-time
{	color: #000000 !important;
}
/*** Главная форума ************************************************************************************************************/
#wa2
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 1% 0px 1% !important;
}
div.forum-menu-box
{	background: #C8C8B4 url(http://novosti-kosmonavtiki.ru/bitrix/components/bitrix/forum.interface/show_file.php?fid=20391) top no-repeat !important;
	margin: 0px 0px 0px 0px !important;
	padding: 50px 0px 20px 0px !important;
	border: none !important;
	text-align: center !important;
}
div.forum-menu-box span.forum-menu-item
{	margin: 0px 0% 0px 0% !important;
	padding: 0px 1% 0px 1% !important;
	border: none !important;
	font-weight: bold !important;
	font-size: 11px !important;
}
div.forum-header-box
{	background: #006450 none !important;
	height: 25px !important;
	padding: 0px 1% 0px 0% !important;
	border: none !important;
}
div.forum-header-box div.forum-header-title
{	padding: 5px 20% 0px 0px !important;
	color: #FFFFFF !important;
	font-weight: bold !important;
	font-size: 11px !important;
	text-align: center !important;
}
div.forum-block-container
{	background-color: #DCDCC8 !important;
	background: rgb(210, 210, 190) !important;
	background: rgba(210, 210, 190) !important;
	margin: 0px 0px 15px 0px !important;
	border: 2px solid #006450 !important;
	border-top: none !important;
}
table.forum-forum-list
{	margin: 0px 0% 0px 0% !important;
}
table.forum-table th.forum-column-title,
table.forum-table th.forum-column-topics,
table.forum-table th.forum-column-replies,
table.forum-table th.forum-column-views,
table.forum-table th.forum-column-lastpost
{	background-color: #C8C8B4 !important;
	border-left: none !important;
	border-right: 1px solid #DCDCC8 !important;
	color: #000000 !important;
	font-size: 11px !important;
}
table.forum-table th.forum-column-title div.forum-head-title span a
{	font-weight: bold !important;
	font-size: 12px !important;
}
table.forum-table th.forum-column-topics,
table.forum-table th.forum-column-replies,
table.forum-table th.forum-column-views
{	text-align: center !important;
}
table.forum-table th.forum-column-lastpost
{	border-right: none !important;
	text-align: center !important;
}
.forum-row-first td,
.forum-row-last td,
.forum-row-odd td,
.forum-row-even td
{	border: none !important;
	border-bottom: 1px solid #C8C8B4 !important;
}
td.forum-column-icon,
td.forum-column-title,
td.forum-column-topics,
td.forum-column-replies,
td.forum-column-views,
td.forum-column-lastpost
{	background-color: #DCDCC8 !important;
	border-top: none !important;
	border-left: none !important;
	border-right: 1px solid #C8C8B4 !important;
	font-size: 10px !important;
}
td.forum-column-title
{	width: 70% !important;
}
td.forum-column-lastpost
{	border-right: none !important;
}
div.forum-icon
{	height: 16px !important;
	width: 16px !important;
}
div.forum-icon-newposts {
background-image: url('data:image/gif;base64,') !important;
}
div.forum-icon-default {
background-image: url("data:image/gif;base64,") !important;
}
div.forum-icon-sticky-newposts {
background-image: url("data:image/gif;base64,R0lGODlhEAAQAOMDAAAAAP8AALV6DPSkEP+rEbW1tefv9/f39/SkEPSkEPSkEPSkEPSkEPSkEPSkEPSkECH5BAEKAAgALAAAAAAQABAAAARQEMlJq734gk2F94AEBGSICAdBDIOwkTDwze4IB7KqE6MdCysWy+YLAYXD2MYYHBaKrlMTUKguDdGj82kYdLPTTbfbYgrHXmGWxgaJlvB4KAIAOw==")!important;
}
div.forum-icon-sticky {
background-image: url("data:image/gif;base64,R0lGODlhEAAQALMAAAAAAGuUtYytxqW11q3G3rW1tdbe7+fv9/f39/8AAP/Ozv///////////////////yH5BAEAAAoALAAAAAAQABAAQARZUMlJqwqGaA2SByAgBYiREQHorQC2hccQD4JoVeFo7kbXrYnWiQAoGGG1i2DJbDJtt+goQE1JfkGls4b9uDbED+syJBawgOTXfI7F1IYBQUaf0WyhvB6kiAAAOw==")!important;
}
div.forum-icon-closed, div.forum-icon-sticky-closed {
background-image: url("data:image/gif;base64,") !important;
}
div.forum-item-name span.forum-item-title a
{	font-weight: bold !important;
	font-size: 12px !important;
}
div.forum-lastpost-box span.forum-lastpost-title a
{	font-size: 12px !important;
}
div.forum-lastpost-box span.forum-lastpost-title a span.forum-lastpost-author
{	font-size: 10px !important;
}
td.forum-column-footer
{	background-color: #C8C8B4 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 5px 0px 5px 0px !important;
	border: none !important;
	font-weight: bold !important;
	font-size: 11px !important;
}
/*ниже блок-контейнера*/
div.forum-users-online,
div.forum-users-birthday,
div.forum-statistics
{	background-color: #DCDCC8 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 5px 1% 5px 1% !important;
	border: 2px solid #006450 !important;
	border-bottom: 1px solid #C8C8B4 !important;
	font-size: 10px !important;
}
div.forum-users-birthday
{	border-top: none !important;
}
div.forum-statistics
{	border-top: none !important;
	border-bottom: 2px solid #006450 !important;
}
span.forum-user-online a,
span.forum-users-birthday a
{	font-weight: bold !important;
	text-decoration: underline !important;
}
span.forum-user-online a:hover
{	text-decoration: none !important;
}
div.forum-main-footer
{	background-color: #C8C8B4 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 25px 0% 0px 0% !important;
	border: none !important;
}
div.forum-legend-info
{	font-size: 11px !important;
}
/*** Раздел форума **********************************************************************/
/*** номера страниц ***/
div.forum-navigation-box
{	background: #C8C8B4 !important;
	border: none !important;
}
div.forum-page-navigation
{	float: right !important;
	font-weight: bold !important;
	font-size : 11px !important;
}
div.forum-page-navigation a,
div.forum-page-navigation span.forum-page-current,
div.forum-page-navigation span.forum-page-dots
{	padding: 0px 0px 0px 3px !important;
	border-left: none !important;
	border-right: 1px dashed #777777 !important;
	text-decoration: none !important;
}
div.forum-page-navigation a
{	text-decoration: underline !important;
}
div.forum-page-navigation a:hover
{	text-decoration: none !important;
}
/*** Cсылки в заголовках справа ***/
div.forum-header-options
{	padding: 4px  0px  0px  0px  !important;
}
div.forum-header-options span.forum-option-feed,
div.forum-header-options span.forum-option-subscribe,
div.forum-header-options span.forum-option-bbcode,
div.forum-header-options span.forum-option-rules,
div.forum-header-options span.forum-option-profile,
div.forum-header-options span.forum-option-pmessage,
div.forum-header-options span.forum-option-pmessage-folder
{	background-color: #DCDCC8 !important;
	padding: 0px  5px  2px  5px  !important;
	font-weight: bold !important;
	font-size: 11px !important;
}
div.forum-lastpost-box span.forum-lastpost-date a,
div.forum-lastpost-box span.forum-lastpost-title span.forum-lastpost-author
{	font-size: 11px !important;
}
/*** Раздел форума в авторизации ********************************************************/
/*** Кнопка "Новая тема", а в теме кнопка "Ответить" ***/
div.forum-navigation-box div.forum-new-post
{	background-color: #DCDCC8 !important;
	margin: 0px 2.5% 0px 0px !important;
	padding: 0px 2.5% 1px 2.5% !important;
	border: 1px solid #006450 !important;
}
/*** Страницы по ссылкам RSS,Подписаться ***/
div.forum-subscribes-rss, div.forum-subscribes
{	background-color: #DCDCC8 !important;
	border: 2px solid #006450 !important;
	border-top: none !important;
}

/*** Тема форума ************************************************************************/
table.forum-post-table
{	margin: 0px 0px 0px 0px !important;
	line-height: 150% !important;
}
table.forum-post-first,
table.forum-post-odd,
table.forum-post-last,
table.forum-post-even 
{	border: none !important; 
	border-bottom: 1px solid #C8C8B4 !important;
}
/*** Юзер ***/
table.forum-post-table td.forum-cell-user
{	background-color: #DCDCC8 !important;
	width: 170px !important;
	border-left: none !important;
	border-right: 1px solid #C8C8B4 !important;
	line-height: 100% !important;
}
div.forum-user-info div.forum-user-name a span
{	font-size: 12px !important;
}
div.forum-user-info div.forum-user-status,
div.forum-user-info div.forum-user-additional
{	display: inline !important;
}
div.forum-user-info div.forum-user-status span,
div.forum-user-info div.forum-user-additional span
{	font-weight: normal !important;
	font-size: 10px !important;
}
/*** Сообщение ***/
table.forum-post-table td.forum-cell-post
{	background-color: #DCDCC8 !important;
	border: none !important;
}
div.forum-post-date
{	background-color: #DCDCC8 !important;
	padding: 0px 0.5% 0px 0.5% !important;
	border: none !important;
	color: #000000 !important;
	font-size: 10px !important;
}
div.forum-post-entry
{	padding: 0px 0.5% 0px 0.5% !important;
 	font-size: 12px !important;
}
div.forum-post-entry .forum-post-text a:visited
{	color: #808000 !important;
}
div.forum-post-entry .forum-post-text a:visited:hover
{	color: #000000 !important;
}
/*** Цитаты, спойлер, код ***/
table.forum-quote,
table.forum-spoiler,
table.forum-code,
div.forum-code-box
{	width: 95% !important;
	margin: 0px 0.5% 0px 2.5% !important;
	padding: 0px 0.5% 0px 0.5% !important;
	border: 1px solid #DCDCC8 !important;
 	font-size: 12px !important;
}
/*** Поле со словом "цитата" ***/
table.forum-quote th,
table.forum-spoiler th,
table.forum-code th
{	background-color: #C8C8B4 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	color: #777777 !important;
	font-size: 10px !important;
	font-weight : normal !important;
	line-height: 100% !important;
}
/*** поле с содержанием цитаты ***/
table.forum-quote td,
table.forum-spoiler td,
table.forum-code td,
div.forum-code-box ol
{	background-color: #C8C8B4 !important;
	color: #000000 !important; 
}
/*** Подпись юзера ***/
div.forum-post-entry div.forum-user-signature span,
div.forum-post-entry div.forum-post-lastedit
{	padding: 0px 0px 0px 0px !important;
 	font-size: 10px !important;
}
/*** Полоса над подписью ***/
div.forum-post-entry div.forum-user-signature div.forum-signature-line
{	border-top-color: #777777 !important;
}
/*** Скрыть в изменении имя юзера  ***/
span.forum-post-lastedit-user a,
span.forum-post-lastedit-user a:hover
{	color: #DCDCC8 !important;
}
/*** Тема форума в авторизации **********************************************************/
/*** Поля сообшение,емайл и изменить,цитировать,имя ***/
table.forum-post-table td.forum-cell-contact,
table.forum-post-table td.forum-cell-actions
{	background-color: #DCDCC8 !important;
	height: 0px !important;
	border: none !important;
 	font-size: 11px !important;
}
table.forum-post-table td.forum-cell-contact
{	border-right: 1px solid #C8C8B4 !important;
}
table.forum-post-table td.forum-cell-actions
{	text-align : right !important;
}
/*** Форма ответов ***/
div.forum-reply-form
{	background-color: #b2b29E !important; 
	border: 2px solid #006450 !important; 
	border-top: none !important;
}
/*** по ссылкаи BBCode, Правила, Просмотр ***/
div.forum-help,
div.forum-rules,
div.forum-post-preview
{	background-color: #DCDCC8 !important; 
	border: 2px solid #006450 !important; 
	border-top: none !important;
}
/*** служебные надписи и подписи ***/
div.forum-reply-form,
div.forum-reply-header,
div.forum-reply-fields
{	color: #000000 !important;
	font-size: 11px !important;
	font-weight: normal !important;
}
/*** там такая муть... ***/
div.bxlhe-frame
{	background-color: #DCDCC8 !important; 
	color: #000000 !important;
	font-size: 12px !important;
	font-family: "Verdana", "Arial", "Helvetica", sans-serif !important;
	line-height: 150% !important;
}
table.bxlhe-frame-table,
table.bxlhe-frame-table td.bxlhe-editor-buttons div.lhe-stat-toolbar-cont,
table.bxlhe-frame-table td.bxlhe-editor-cell,
div.feed-add-post-form-but-wrap
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	border: none !important;
}
table.bxlhe-frame-table td.bxlhe-editor-cell
{	background-color: #C8C8B4 !important;
}
/*** фон и выоота поля с иконками ***/
table.bxlhe-frame-table td.bxlhe-editor-buttons,
div.feed-add-post-form-but-wrap
{	background: #AAAAAA !important;
 	height: 22px !important;
}
/*** иконки поярче ***/
div.feed-add-post-form-but-wrap span.feed-add-post-form-but
{	opacity: 1 !important;
}
/*** цитата в форме ответов ***/
blockquote.bx-quote
{	background: #C8C8B4 none !important;
	margin: 5px 0.5% 5px 2.5% !important;
	padding: 5px 0.5% 5px 0.5% !important;
	border: 1px solid #DCDCC8 !important;
	color: #000000 !important;
	line-height: 150% !important;
}
/*** ответ в bbcode ***/
textarea.lha-textarea
{	background-color: #C8C8B4 !important;
	color: #000000 !important;
	font-size: 12px !important;
	font-family: "Verdana", "Arial", "Helvetica", sans-serif !important;
	line-height: 150% !important;
}
/*** после нажатия "Ответить" ***/
.forum-note-box,
.forum-note-success
{	background-color: #C8C8B4 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	border: none !important;
}

/*** Меню форума ************************************************************************/
/*** сначала "убить" три вредных тега ***/
th.forum-first-column,
th.forum-last-column 
{	background-color: #DCDCC8 !important;
	border-left: none !important;
	border-right: none !important;
}
td.forum-last-column
{	border-left: 1px solid #C8C8B4 !important;
	border-right: none !important;
}
/*** Обновления ****************************************************************************************************************/
/*** фильтр списка обновлений, а также поиска, пользователей... ***/
div.forum-filter
{	background: #C8C8B4 !important;
	margin: 0px 0px 0px 0px !important;
	border: none !important;
}
/*** Профиль *******************************************************************************************************************/
/*** по ссылке Подписки ***/
table.forum-subscribe-list thead th
{	background-color: #DCDCC8 !important;
	border-left: none !important;
	border-right: 1px solid #C8C8B4 !important;
	border-bottom: 1px solid #C8C8B4 !important;
	font-weight: bold !important;
	font-size: 12px !important;
}
table.forum-subscribe-list tr td
{	border-left: none !important;
	border-right: 1px solid #C8C8B4 !important;
	font-weight: bold !important;
	font-size: 12px !important;
}
/*** по ссылке Изменить профиль ***/
div.forum-tabs 
{	background-color: #DCDCC8 !important;
	height: 350px !important;
	padding: 20px 1% 20px 1% !important;
	border: 2px solid #006450 !important;
}
div.tab-container,
div.tab-container-selected
{	padding: 5px 0px 5px 10px !important;
	border: none !important;
	font-weight: bold !important;
	font-size: 12px !important;
}
div.tab-container
{	color: #0000DD !important;
}
div.tab-container-selected
{	border: 1px solid #006450 !important;
	border-right: none !important;
	color: #000000 !important;
}
div.forum-tabs-body div.forum-profile-edit
{	border: 1px solid #006450 !important;
}
div.forum-user-hide-action a
{	font-size: 11px !important; 
}
table.forum-user-private-info th.forum-last-column
{	background-color: #DCDCC8 !important;
	border: none !important;
	border-bottom: 1px solid #C8C8B4 !important;
}
/*** Сообщения **************************************************************************/
th.forum-column-foldername,
th.forum-column-message,
th.forum-column-action,
td.forum-column-foldername
{	background: #DCDCC8 !important;
	border-left: none !important;
	border-right: none !important;
	border-bottom: 1px solid #C8C8B4 !important;
	color: #000000 !important;
	font-weight: bold !important;
	font-size: 12px !important;
}
th.forum-column-foldername,
td.forum-column-foldername
{	border-right: 1px solid #C8C8B4 !important;
}
th.forum-column-action
{	width: 50% !important;
	border-left: 1px solid #C8C8B4 !important; 
}
/*** заполнение ящика ***/
div.out
{	border: 1px solid #006450 !important;	
}
div.out div.in
{	background-color: #000000 !important;
}
div.out1
{	top: -14px !important;
}
/*** по ссылке Новое сообщение ***/
div.forum-reply-field-title input,
div.forum-reply-field-author input
{	background-color: #DCDCC8 !important;
	border: none !important;	
	color: #000000 !important;	
	font-size: 12px !important;
}
/*** и там иконки без рамок ***/
div.lhe-button-cont img.lhe-button,
div.lhe-tlbr-smile-cont
{	border: none !important;
}
/*** по ссылке Новая папка ***/
.forum-create-folder
{	background-color: #DCDCC8 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 10px 1% 10px 1% !important;
	border: 2px solid #006450 !important;	
}
/*** Папки ******************************************************************************/
table.forum-pmessages thead th,
table.forum-pmessages tr td,
table.forum-pmessages td.forum-column-action
{	color: #000000 !important;
	font-weight: bold !important;
	font-size: 12px !important;
	border-left: none !important;
	border-right: none !important;
}
table.forum-pmessages thead th,
table.forum-pmessages tr td
{	border-right: 1px solid #C8C8B4 !important;
	border-bottom: 1px solid #C8C8B4 !important;
}
/*** содержание ЛС **********************************************************************/
/*** текст в ЛС ***/
table.forum-pmessages td.forum-pmessage-text
{	color: #000000 !important;
	font-weight: normal !important;
	font-size: 12px !important;
	line-height: 150% !important;
}
/*** слово "цитата" в ЛС ***/
table.forum-pmessages table.forum-quote th
{	background: #C8C8B4 !important;
	color: #777777 !important;
	font-weight: normal !important;
	font-size: 10px !important;
}
/*** цитаты в ЛС ***/
table.forum-pmessages table.forum-quote td
{	font-weight: normal !important;
}
/*** Поиск ******************************************************************************/
div.forum-block-inner div.forum-filter
{	background: #DCDCC8 !important;
 	margin: 0px 0px 0px 0px !important;
	padding: 15px 1% 5px 1% !important;
	border: none !important;
	border-bottom: 2px solid #C8C8B4 !important;
}
/*** Пользователи ***********************************************************************/
/*** заголовки колонок ***/
table.forum-users th.forum-column-username,
table.forum-users th.forum-column-posts,
table.forum-users th.forum-column-datereg,
table.forum-users th.forum-column-lastvisit
{	background: #DCDCC8 !important;
	border-right: 1px solid #C8C8B4 !important;
	border-bottom: 1px solid #C8C8B4 !important;
	color: #000000 !important;
	font-weight: bold !important;
	font-size: 12px !important;
}
table.forum-users th.forum-column-lastvisit,
table.forum-users tr td.forum-column-lastvisit
{	border-left: none !important;
	border-right: none !important;
}
/*** содержание колонок ***/
table.forum-users tr td.forum-column-username,
table.forum-users tr td.forum-column-posts,
table.forum-users tr td.forum-column-datereg,
table.forum-users tr td.forum-column-lastvisit
{	border-right: 1px solid #C8C8B4 !important;
	color: #000000 !important;
	font-weight: bold !important;
	font-size: 12px !important;
}

}
