css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("quickbase.com") {

/* FONTS */
body, body.stdBody, .MenuBar,
td#s4PageName, td.pagename,
td#s4PageName, td.pagename,
td.cdbInHeader,
#tablesMenuBar > li > a,
td#stdBreadCrumbs,
input[type="button"],
input, select, textarea {
	font: 13px normal 'Helvetica Neue', 'Calibri', 'Helvetica' 'Arial';
}

/* -------------------------------------------- */
/* FONT COLORS */
/* grey */
body, body.stdBody, .MenuBar,
td#s4PageName, td.pagename,
td.cdbInHeader,
td#stdBreadCrumbs {
	color:#888;
}
/* blue */
td#s4PageName, td.pagename, a,
td.primaryButton a,
td.primaryButton a:hover,
td#stdBreadCrumbs span.crdlm {
	color:#0072BC;
}
table.searchResults a,
table.dformDisplay td.shead {
	color:#0072BC !important;
}
/* white */
#tablesMenuBar > li > a:hover,
.IconStrip.Menu > li > a:hover,
input[type="button"] {
	color:#fff;
}
/* black */
#appMenuBar .MenuBar li a.SignInMenuLink,
#appBarLeftMenu > li.Selectable > a::after,
#signedInAsLink::after, #signedInAsLink::after {
	color:#000;
}

/* -------------------------------------------- */
/* BACKGROUND COLORS */
/* none */
#tablesMenuBar > li#appHomeButton > a {
	background:none;
}
/* white */
body, #appMenuBar {
	background:#fff;
}
/* menu blue */
#tablesMenuBar, .PopMenu a:hover {
	background:#0072BC;
}
/* menu hover blue */
#tablesMenuBar > li > a:hover,
.IconStrip.Menu > li > a:hover,
#tablesMenuBar > li.Open {
	background-color:#2C83EF;
}
/* grey */
#appMenuBar > .MenuBar > li > a.MyQBButtonAppMenuBar {
	background:#ccc;
}
/* menu yellow */
#tablesMenuBar > li#appHomeButton,
#appMenuBar > .MenuBar > li > a.MyQBButtonAppMenuBar:hover {
	background: #FFC40D;
}
/* menu hover yellow */
#tablesMenuBar > li#appHomeButton > a:hover {
	background:#fdd044;
}

/* -------------------------------------------- */
/* FONT WEIGHT */
/* condensed */
body, body.stdBody, .MenuBar,
td#s4PageName, td.pagename {
	font-weight: 300;
}
/* bold */
td#s4PageName, td.pagename,
td.cdbInHeader,
td#stdBreadCrumbs {
	font-weight:600;
}
#s4PageName {
	font-size:20px !important;
}

/* -------------------------------------------- */
/* OTHER STYLES */
#mainBodyDiv {
	overflow-x:hidden !important;
}
.CustomBrandLink {
	background:url(/*[[logoURL]]*/) no-repeat 0 0;
	display:block;
	height:44px;
	overflow:hidden;
	margin: 20px 0 0 15px;
}
.CustomBrandLink img {
	display:none;
}
hr {
	border:0;
	height:1px;
	color:#E1E1E8;
	background:#E1E1E8;
}
td#s4PageName, td.pagename,
td.cdbInHeader {
	padding:0;
}
td#divider1, td#divider2 {
	content: '\203A';
}
td#divider1 img, td#divider2 img {
	display:none;
}

body * {
	outline:none !important;
}

/* LINKS */
td.primaryButton a {
	cursor:pointer;
}
a:hover, td.primaryButton a:hover {
	text-decoration:underline;
}
table.searchResults a:hover {
	text-decoration:none;
}

#appMenuBar {
	padding:10px;
	position:relative;
}
#appMenuBar li {
	background:none !important;
	position:relative;
}
#appMenuBar .MenuBar > li > a:hover {
	text-decoration:underline;
}

/* MAIN MENU STYLES */

#tablesMenuBar {
	border:0;
	overflow:visible;
	height:40px;
}
#tablesMenuBar * {
	outline: none !important;
}
#tablesMenuBar > li#appHomeButton {
	border:0;
	margin:0 !important;
}

#tablesMenuBar > li#appHomeButton > a,
#tablesMenuBar > li > a,
#tablesMenuBar > li {
	border:0 !important;
	box-shadow: none !important;
}

#tablesMenuBar > li > a {
	text-transform:none;
	padding:5px 15px !important;
	line-height:30px;
	margin: 0 !important;
}
.PopMenu,
.Menu {
	background:#fff !important;
	border:0;
	box-shadow:0 5px 10px rgba(0, 0, 0, 0.2);
	-webkit-box-shadow:0 5px 10px rgba(0, 0, 0, 0.2);
	-moz-box-shadow:0 5px 10px rgba(0, 0, 0, 0.2);
	padding:0;
}
.Menu {
	display:none;
}
#tablesMenuBar .Menu {
	top:40px !important;left:0 !important;
}
.Menu .Title {
	margin:5px 0 0;
	padding:5px 0 0;
	border:0;
	border-top:1px solid #e5e5e5;
}
.PopMenu table {
	width:100%;
}
.PopMenu h4 {
	background-color:#f9f9f9;
	border-bottom:1px solid #e5e5e5;
	padding:5px 10px;
}
.PopMenu .hr {
	border-top:1px solid #e5e5e5;
}
.Menu .Title span {
	border:0;
}
.Menu > li {
	background:none !important;
	padding:0;
}
.PopMenu a,
.Menu > li > a,
.Menu > li > span {
	color:#000;
	padding:5px 10px !important;
}
.PopMenu a:hover {
	text-decoration:none;
}
#tablesMenuBar > li {
	display:block;
	float:left;
	position:relative;
}

#tablesMenuBar > li.Selectable > a {
	padding-right:25px !important;
}
#tablesMenuBar > li.Selectable > a::after,
#appBarLeftMenu > li.Selectable > a::after,
#signedInAsLink::after {
	content:"\203A";
	display:block;
	position:absolute;
	font-size: 1.4em;
	left: 100%;
	margin-left: -15px;
	top: 5px;
	-webkit-transform: rotate(90deg);
	-moz-transform: rotate(90deg);
	-ms-transform: rotate(90deg);
	-o-transform: rotate(90deg);
	transform: rotate(90deg);
}
.qbPersist.ToggleSectionIcon.SectionExpandedIcon,
.icr .ToggleSectionIcon.SectionExpandedIcon,
.lb .ToggleSectionIcon.SectionExpandedIcon,
.qbPersist.ToggleSectionIcon, .icr .ToggleSectionIcon, .lb .ToggleSectionIcon,
.Icon.BlueCaret {
	background:none !important;
	position:relative;
	-webkit-transform: none;
	-moz-transform: none;
	-o-transform: none;
	transform: none;
}
.qbPersist.ToggleSectionIcon.SectionExpandedIcon::after,
.icr .ToggleSectionIcon.SectionExpandedIcon::after,
.lb .ToggleSectionIcon.SectionExpandedIcon::after,
.qbPersist.ToggleSectionIcon::after,
.icr .ToggleSectionIcon::after,
.lb .ToggleSectionIcon::after,
.Icon.BlueCaret::after {
	background:none;
	content:"\203A";
	color:#000;
	display:inline-block;
	position:absolute;
	font-size: 1.4em;
	left: 0;
	margin-top:-5px;
	top:0;
}
.Icon.BlueCaret::after {
	margin-top:-7px;
	left:100%;
}
.qbPersist.ToggleSectionIcon.SectionExpandedIcon::after,
.icr .ToggleSectionIcon.SectionExpandedIcon::after,
.lb .ToggleSectionIcon.SectionExpandedIcon::after,
.Icon.BlueCaret::after {
	-webkit-transform: rotate(90deg);
	-moz-transform: rotate(90deg);
	-ms-transform: rotate(90deg);
	-o-transform: rotate(90deg);
	transform: rotate(90deg);
}
#appBarLeftMenu > li.Selectable,
#signedInAsLink {
	padding-right:20px;
}
#signedInAsLink::after {
	margin-left:-10px;
}
#signedInAsLink img {
	display:none;
}
#appBarRightMenu {
	max-height:auto;
}
#appMenuBar > .MenuBar > li {
	margin:0;
}

/* BUTTON STYLES */
#appMenuBar > .MenuBar > li > a.MyQBButtonAppMenuBar {
	color:#000;
	padding: 5px 10px;
	margin: 0;
	text-decoration:none;
}
#appMenuBar > .MenuBar > li > a.MyQBButtonAppMenuBar > span {
	display:block;
}
input[type="button"], input[type="submit"] {
	border:0 !important;
	cursor:pointer !important;
	color:#fff !important;
	font-variant:normal !important;
	height:auto !important;
	padding:5px 10px;
}
#formBotButtons input[type="button"] {
	color:#000 !important;
}
#formBotBar a[id*=Button],
#formTopBar a[id*=Button],
#displayButton, #formBotButtons input[type="submit"],
tr#formTopBar a,
table.searchResults a.stdLinkButton, a.stdLinkButton {
	background-color: #00A300 !important;
	border:0 !important;
	color:#fff;
	font-variant:normal;
	text-transform:lowercase;
}
#displayButton:hover, #formBotButtons input[type="submit"]:hover {
	background-color: #19BB19 !important;
}
.linkButton a[class*=Button],
#formTopBar a[id*=Button] {
	font-family:'Helvetica Neue', 'Calibri', 'Helvetica', 'Arial', sans-serif;
	background-color:#0072BC !important;
	color:#fff;
}
.linkButton a[class*=Button] {
	
}
#formBotBar a,
#formTopBar a {
	font-variant:normal;
	text-transform:lowercase;
	padding:5px 10px !important;
}
.linkButton a[class*=Button]:first-letter,
#formTopBar a:first-letter {
	text-transform:uppercase !important;
}
#formTopBar a[id*=Button]:hover,
.linkButton a[class*=Button]:hover {
	background-color:#2D89EF !important;
}
#saveButton {
	background-color: #00A300 !important;
}
#saveAsButton, tr#formTopBar a {
	background-color: #00A300 !important;
}
a.EditRecordIcon,
a.ViewRecordIcon {
	background:#00A300 !important;
	text-indent:0 !important;
	color:#fff !important;
	font-weight:400;
	font-size:10px;
	height:auto !important;
	line-height:1;
	padding:3px 5px;
	text-align:center;
	text-decoration:none;
}
a.EditRecordIcon:hover {
	background:#19BB19 !important;
}
a.ViewRecordIcon,
#formBotButtons input[type="button"] {
	background:#ccc !important;
}
a.ViewRecordIcon:hover,
#formBotButtons input[type="button"]:hover {
	background:#eee !important;
}
a.EditRecordIcon::after,
a.ViewRecordIcon::after {
	content: 'Edit';
	color:#fff;
}
a.ViewRecordIcon::after {
	content: 'View';
}
table.searchResults a.stdLinkButton, a.stdLinkButton {
	background-color:#603CBA !important;
	color:#fff !important;
	padding:5px 10px !important;
	font: 11px normal 'Open Sans', 'Helvetica Neue', 'Calibri', 'Arial' !important;
}

/* TABLE STYLES */
table {
	border-collapse: collapse;
}
table table {
	background:none !important;
}
tr.std_title_bar {
	background:#EAEAEA;
}
tr.std_title_bar td {
	color:#555;
}
#trTitleBar {
	border:0 !important;
}
tr.std_title_bar td, tr.grey_title_bar td {
	padding:10px;
}
table.std_box td.bot,
table.std_box td.mid {
	background:#FBFBFC;
	border:1px solid #E1E1E8;
	padding:10px;
}
table.std_box td.mid {
	background:#fff;
}
td#mainCell {
	border:0;
}
td.primaryButton {
	background:none;
	position:relative;
}
td.primaryButton a {
	padding-left:15px;
}
td.primaryButton a::before {
	content:"\203A";
	display:block;
	position:absolute;
	font-size: 1.1em;
	line-height:1;
}
table.searchResults tr.hd td:hover {
	background-color:#FBFBFC !important;
}
.Divider {
	background:none !important;
	border-top:1px solid #E1E1E8;
}
tr.Toolbar {
	background:#FBFBFC !important;
	border:0 !important;
}
tr.Toolbar td, table.searchResults td {
	padding:5px 10px !important;
	border:0 !important;
}
table.searchResults, .TableContainerTD {
	border:0 !important;
}
table.searchResults > tbody > tr > td {
	border-bottom:1px solid #ddd !important;
	border-right:1px solid #ddd !important;
	box-sizing: border-box;
}
table.searchResults > tbody > tr.hd > td {
	border-bottom:0 !important;
}
table.searchResults td.icr:last-child {
	border:0 !important;
}
tr.ev td, tr.evtlp td {
	background-color: #f9f9f9 !important;
}
.Toolbar .Separator {
	border-right:1px solid #E1E1E8;
}
table.dformDisplay table.LabelsAbove td.lc,
table.dformDisplay table.LabelsAbove td.cc {
	padding:5px 10px !important;
	font-weight:normal !important;
	line-height:1.5;
}
table.dformDisplay table.LabelsAbove td.cc {
	font-size:12px !important;
}
table.dformDisplay table.LabelsAbove td.lc {
	color:#000;
	font-weight:bold !important;
}
table.dformDisplay td.shead,
#dbFormContainer > table td.shead {
	font: 18px normal 'Open Sans', 'Helvetica Neue', 'Calibri', 'Arial' !important;
	padding:0 0 10px !important;
}
tr#formTopBar td, tr#formBotBar td {
	height:auto;
}
#dbFormContainer > table,
#dbFormContainer > table .dformDisplay,
#sect_s3 table,
table.std_box {
	width:100%;
}

#dbFormContainer > table td,
#sect_s3 table td {
	background:none;
	padding:0 !important;
}
table.LabelsAbove td > * {
	padding:10px 10px 0 0 !important;
}
table.LabelsAbove td > table {
	padding:0 !important;
}
#sect_s3 table td + td > img,
img[src*="/i/clear2x2.gif"] {
	display:none !important;
}
td.lc {
	font-size:14px !important;
}
#sect_s3 table td.cc > div {
	background:none !important;
	color:#888 !important;
	padding:5px 5px 0 0 !important;
	text-indent:-0.5em;
}
#dbFormContainer > table td > hr {
	margin:15px 0;
}
#pageTitleCell {
	padding:0 10px !important;
}
#pageTitleCell td {
	vertical-align:baseline;
}
.LabelsAbove,
.LabelsAbove table,
.TableReportDisplay {
	width:100%;
}
table.searchResults {
	border:1px solid #ECECF0 !important;
}
#dbFormContainer table.searchResults td {
	padding:10px !important;
}
.ui-tabs-panel .hd td,
#dbFormContainer .TableContainerTD .hd td,
.verdana .hd,
.TableContainerTD .hd> td {
	background: #FBFBFC !important;
	border:1px solid #ECECF0 !important;
	color:#555;
	font:13px 'Helvetica Neue', 'Calibri', 'Helvetica', 'Arial', sans-serif !important;
	vertical-align: top !important;
	text-align:left;
	padding:10px !important;
}
#dbFormContainer .TableContainerTD .hd td:hover,
.verdana .hd:hover {
	background-color:#e8f8fd !important;
}
#dbFormContainer .TableContainerTD .hd td .hd-div {
	padding:0 !important;
}
.HeaderBorder {
	height:0px !important;
	overflow:hidden;
}
.HeaderBorder td {
	padding:0 !important;
	height:0;
}
#dbFormContainer > table td.cminfo {
	padding:5px 5px 0 0 !important;
	font-size:11px;
}
td.searchWarning {
	padding-top:0;
}
#todo .hd-div,
td.searchWarning {
	font:12px 'Helvetica Neue', 'Calibri', 'Helvetica', 'Arial', sans-serif;
}
.verdana {
	border:1px solid #ECECF0 !important;
	margin-bottom:10px;
}
.verdana td {
	border-right:1px solid #ececf0;
}
#mainBodyDiv > table {
	width:100% !important;
}
#dashboardSections .Text.Section {
	width:100% !important;
}
#dashboardSections .Section .Title {
	background-color:#eaeaea;
	border-bottom:1px solid #E1E1E8;
	padding:0;
}
#dashboardSections .Section .Title .sectionTitle > * {
	padding: 5px 10px;
}
#dashboardSections .Section .Contents {
	border:0 !important;
}
.Contents a {
	color:#2D89EF !important;
	font:12px 'Helvetica Neue', 'Calibri', 'Helvetica', 'Arial', sans-serif;
	text-transform:lowercase;
}
.Contents a:hover {
	text-decoration:none;
}
.Contents a:first-letter {
	text-transform:uppercase;
}
#yourproj .verdana {
	margin:-16px 0 0 8px;
}
#yourproj .verdana .hd {
	font-size:12px !important;
	padding:6px 10px !important;
}
.TableContainerTD .hd td {
	font-size: 12px !important;
	padding:5px 10px !important;
}
.TableContainerTD .hd td td {
	padding:0 !important;
}
/* UI TABS STYLES */
.ui-tabs {
	border-radius:0;
	border:0;
}
.ui-tabs .ui-tabs-nav {
	background: #f8f8f8;
	border:0;
	border-radius:0;
	padding:0;
	margin-bottom:-1px;
}
.ui-tabs .ui-tabs-nav li {
	background:none;
	border:0;
	border-radius:0;
	top:0;
}
.ui-tabs .ui-tabs-nav li a {
	font:12px 'Helvetica Neue', 'Calibri', 'Helvetica', 'Arial', sans-serif;
	color:#1e1e1e !important;
	text-transform:lowercase;
}
.ui-tabs .ui-tabs-nav li a:first-letter {
	text-transform:uppercase;
}
.ui-tabs .ui-tabs-nav li.ui-tabs-selected a {
	color:#2D89EF;
}
.ui-tabs .ui-tabs-nav li:first-child {
	margin-left:10px;
}
.ui-tabs .ui-tabs-nav li.ui-tabs-selected {
	background:#fff;
	border:1px solid #ECECF0;
}
.ui-tabs .ui-tabs-panel {
	border:1px solid #ECECF0;
	border-radius: 0;
}
.Clear + table td hr {
	display:none;
}
/* QTIP Styles */
.qtip-contentWrapper {
}
.qtip-content {
	color:#555 !important;
	padding:10px !important;
}

}
