css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://forums.station.sony.com/eq2/"), url-prefix("https://forums.station.sony.com/eq2/") {
    #content .pageContent {
        background-image: url('http://eq2wire.com/eq2forums/bg.png') !important;
        padding-bottom: 0px !important;
    }
    body {
        background-image: url('http://eq2wire.com/eq2forums/top.jpg') !important;
        background-repeat: no-repeat;
        background-position: 50% 215px;
    }
    .mceContentBody {
        background-image: none !important;
    }
    .ugc a:link, .ugc a:visited {
        color: #EF4F24 !important;
    }
    .ugc a:hover, .ugc a:focus {
        color: #FFA800 !important;
    }
    .breadcrumb {
        background-image: none !important;
    }
    .breadBoxBottom {
        border: 1px solid #BC9479 !important;
        background-color: #E5DDD8 !important;
        width: 900px !important;
        margin-left: 47px !important;
    }
    .breadBoxBottom .breadcrumb {
        padding-left: 4px !important;
    }
    .breadcrumb .crust a.crumb {
        color: #344FD9 !important;
    }
    .footer {
        background-image: url('http://eq2wire.com/eq2forums/content_bottom.png') !important;
    }
    .PageNav .pageNavHeader {
        font-weight: bold !important;
        padding-right: 6px !important;
    }
    .pageNavLinkGroup:after {
        content: "." !important;
        display: block !important;
        height: 1% !important;
        clear: both !important;
        visibility: hidden !important;
    }
    .PageNav a.currentPage {
        color: black !important;
        font-weight: bold !important;
    }
    .pageNavLinkGroup .SelectionCountContainer {
        color: white !important;
        background-color: rgb(176,81,49) !important;
		text-decoration: none !important;
		padding: 2px 8px 2px 0px !important;
        border: 1px solid transparent !important;
        border-radius: 3px !important;
        -webkit-border-radius: 3px !important;
        -moz-border-radius: 3px !important;
        -khtml-border-radius: 3px !important;
        text-align: center !important;
    }
    .pageNavLinkGroup .SelectionCountContainer a {
        color: white !important;
        padding-left: 0px !important;
    }
    #AccountMenu {
        background-color: #B6CBDE !important;
    }
    #ConversationsMenu {
        background-color: #B6CBDE !important;
    }    
    #AlertsMenu {
        background-color: #B6CBDE !important;
    }
	.sharePage {
        display: none;
	}
	.titleBar {
	    padding-bottom: 0px !important;
	    margin-bottom: -16px !important;
	}
    .titleBar h1 span {
        font-family: Gill Sans, Gill Sans MT, Helvetica, Arial, sans-serif;
        color: #2F0B00;
    }
    #titleBar_Helper h1 {
        background-image: none !important;
        margin: 0px !important;
        padding: 0px !important;
    }
    #titleBar_Helper h1 span{
        background-image: none !important;
        margin: 0px !important;
        padding: 0px !important;
    }
    #pageDescription:before {
        content: ">> ";
    }
    .threadAlerts {
        background-color: #F2B375 !important;
    }
    .threadAlerts dt {
        color: black !important;
    }
    .nodeIcon {
        background-image: url('http://eq2wire.com/eq2forums/node-sprite.png') !important;
    }
    .nodeList .categoryStrip {
        background-image: url('http://eq2wire.com/eq2forums/category-strip.png') !important;
        margin: 0px 0px 22px 0px !important;
        padding: 0px 0px 12px 0px !important;
        height: 30px !important;
        border-radius: 3px !important;
        -webkit-border-radius: 3px !important;
        -moz-border-radius: 3px !important;
        -khtml-border-radius: 3px !important;
    }
    .nodeList .categoryStrip .nodeTitle {
        padding-left: 0px !important;
        font-family: Gill Sans, Gill Sans MT, Helvetica, Arial, sans-serif !important;
        font-size: 18px !important;
    }
	.node .nodeTitle, .node .nodeText .nodeTitle {
        font-family: Gill Sans, Gill Sans MT, Helvetica, Arial, sans-serif !important;
        font-size: 16px !important;
	}
    .nodeList .categoryStrip .categoryText {
        padding-top: 8px !important;
        height: 30px !important;
    }
	.node .nodeLastPost
	{
		background: none !important;
	}
	.pairsInline dd {
		font-weight: bold;
	}
	.discussionList .sectionHeaders {
    	background: url('http://eq2wire.com/eq2forums/discussion-bar.png') !important;
        height: 24px !important;
        padding-top: 6px !important;
        padding-bottom: 6px !important;
    }
    .sectionHeaders dd {
        font-size: 13px !important;
    }
    .sectionHeaders dd .title {
        padding-left: 22px !important;
    }
    .sectionHeaders dd .postDate {
        display: none !important;
    }
    .discussionListItems .unread .title a {
        color: black !important;
    }
    .discussionListItem .itemPageNav {
        visibility: visible !important;
    }
    .discussionListItem .itemPageNav a {
        background-color: #EEE9AC !important;
        border: 1px solid #B37A3E !important;
    }
    .messageUserBlock {
        padding: 24px 0px 0px 10px !important;
    }
    .messageUserBlock div.avatarHolder {
        padding: 0px !important;
        border: none !important;
    }
    .messageUserBlock h3.userText {
        padding: 6px 0px 0px 0px !important;
    }
    .messageUserBlock a.username {
        font-size: 15px !important;
        font-weight: bold !important;
        line-height: normal !important;
    }
    .messageUserBlock .userTitle {
        color: #4E2302 !important;
    }
    .messageList .message {
        padding: 0px 0px 0px 0px !important;
    }
    .message .messageInfo {
        padding: 17px 14px 20px 17px !important;
        margin: 0px 0px 0px 135px !important;
        background-color: #F5EFDB !important;
    }
    .messageUserBlock .arrow {
        display: none !important;
    }
    .message .signature {
        background-color: transparent !important;
        margin-right: 0px !important;
        padding: 0px !important;
        padding-top: 5px !important;
        border: none !important;
        border-top: 1px solid #303030 !important;
        border-radius: 0px !important;
        -webkit-border-radius: 0px !important;
        -moz-border-radius: 0px !important;
        -khtml-border-radius: 0px !important;
    }
    .message .messageInfo.primaryContent {
        border-bottom: none !important;
    }
    .message .likesSummary {
        background-color: transparent !important;
        padding: 10px 5px 5px 26px !important;
        margin: 20px 0px 0px 0px !important;
        border: none !important;
        border-top: 1px solid #CDBCA2 !important;
        background-image: url('http://eq2wire.com/eq2forums/thumbsup.png') !important;
        background-repeat: no-repeat !important;
    }

    .bbCodeBlock {
        border: 1px solid #5A3A1E !important;
        margin: 8px 20px 16px 20px !important;
    }
    .bbCodeQuote {
    	color: #3A200B !important;
        background-color: #E6DBC3 !important;
    }
    .bbCodeQuote .attribution {
    	font-size: 14px !important;
    	font-weight: bold !important;
        background-color: #D4C6B0 !important;
        background-image: none !important;
        border-bottom: none !important;
    }
    .bbCodeQuote blockquote {
    	font-style: italic;
        font-size: 9pt;
        padding: 10px;
        border-radius: 4px; -webkit-border-radius: 4px; -moz-border-radius: 4px; -khtml-border-radius: 4px;
    }
    .privateControls .item {
        margin-top: 6px !important;
    }
    .privateControls .author:before {
        content: "Posted by ";
    }
    .privateControls .datePermalink {
        font-style: italic !important;
    }
    .privateControls .edit {
        margin-top: 4px !important;
        background-color: #F3E6B2 !important;
		text-decoration: none !important;
		padding: 2px 6px 2px 6px !important;
        border: 1px solid #905632 !important;
        border-radius: 3px !important;
        -webkit-border-radius: 3px !important;
        -moz-border-radius: 3px !important;
        -khtml-border-radius: 3px !important;
        text-align: center !important;
    }
    .privateControls .report {
        margin-top: 4px !important;
        background-color: #F3E6B2 !important;
		text-decoration: none !important;
		padding: 2px 6px 2px 6px !important;
        border: 1px solid #905632 !important;
        border-radius: 3px !important;
        -webkit-border-radius: 3px !important;
        -moz-border-radius: 3px !important;
        -khtml-border-radius: 3px !important;
        text-align: center !important;
    }
    .publicControls .ShowHistory {
        margin-top: 4px !important;
    }
    .publicControls .postNumber {
        margin-top: 6px !important;
    }
    .publicControls .LikeLink {
        margin-top: 4px !important;
        background-color: #F3E6B2 !important;
		text-decoration: none !important;
		padding: 2px 6px 2px 6px !important;
        border: 1px solid #905632 !important;
        border-radius: 3px !important;
        -webkit-border-radius: 3px !important;
        -moz-border-radius: 3px !important;
        -khtml-border-radius: 3px !important;
        text-align: center !important;
    }
    .publicControls .ReplyQuote {
        font-size: 13px !important;
        font-weight: bold !important;
        background-color: #F3E992 !important;
		text-decoration: none !important;
		padding: 3px 9px 3px 9px !important;
        border: 2px solid #6A3616 !important;
        border-radius: 3px !important;
        -webkit-border-radius: 3px !important;
        -moz-border-radius: 3px !important;
        -khtml-border-radius: 3px !important;
        text-align: center !important;
    }
}
