css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("depositfiles.com") {
  /* 適当に各種サイズ調整 */

  /* cut space on around main */
  /* mainの周囲の余白一部解除 */
  div#main {
    padding-top: 63px !important;
    padding-bottom: 0px !important;
  }

  /* hide logo image and cut space */
  /* ロゴイメージを非表示・表示領域詰め */
  a#logo,a#logo>span {
    background: none !important;
    height: 0px !important;
    margin: 0px !important;
    display: inline !important;
    left: 0px !important;
    bottom: 0px !important;
  }
  
  /* move Anmelden top */
  /* ログインリンクを上部に移動 */
  div.login_block { margin: 0px !important; }
  
  /* compact menu icon */
  /* メニューアイコンの表示を詰める */
  div.top_menu { height: 0px !important; }
  ul.df_menu>li {
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  }

  /* cut space around drop upload file area */  
  /* アップロードファイルのドロップエリア周囲の余白詰め */
  div.index_upload {
    margin-top: 10px !important;
    margin-bottom: 0px !important;
  }



  div.gold_speed_promo_block.hide_download_started {
    top: 0px !important;
  }

  /* 表示サイズが０のフレームを削除(通信の発生回避) */
  iframe#ads_z8_frame { display: none; }

  /* 上部広告バナー(主にDownloadダミーが表示される箇所の非表示
     http://ads.depositfiles.com/ad.php?...
  */
  div.banner1 { display: none; }

  /* 右部広告非表示 */
  iframe#ads_z4_frame { display: none; }
  iframe#ads_z5_frame { display: none; }
}
