css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://ssl.reddit.com), 
               url-prefix(http://www.reddit.com),
               url-prefix(https://ssl.reddit.com),
               url-prefix(https://www.reddit.com)
{
/* Content */ 

.link.odd {  
    overflow:hidden !important; 
    margin:0px !important;
    padding:5px !important;
    border:1px solid #EAEAEA !important;
    border-bottom:0px !important;
    background-color:#FAFAFA !important;
    
    min-height: 70px !important;
    
}


.link.even {
    overflow:hidden !important;
    margin:0px !important;
    padding:5px !important;
    border:1px solid #EAEAEA !important;
    border-bottom:0px !important;
    background-color:#f5f5f5 !important;    
    min-height: 70px !important;
    
}



.keyHighlight {
    min-height: 100% !important;
}

    
.link .tagline {
    margin-left:25px !important;
    opacity: 0.5 !important;
    
    -moz-transition-property : opacity; /* FireFox */
    -webkit-transition-property : opacity; /* Safari / Chrome */
    -o-transition-property : opacity; /* Opera */
    transition-property : opacity; /* W3C */
       
    -moz-transition-duration : 0.4s; /* FireFox */
    -webkit-transition-duration :  0.4s; /* Safari / Chrome */
    -o-transition-duration :  0.4s; /* Opera */
    transition-duration :  0.4s; /* W3C */
 }

.link .tagline:hover {
    margin-left:25px !important;
    opacity: 1 !important;
    
    -moz-transition-property : opacity; /* FireFox */
    -webkit-transition-property : opacity; /* Safari / Chrome */
    -o-transition-property : opacity; /* Opera */
    transition-property : opacity; /* W3C */
        
    -moz-transition-duration : 0.4s; /* FireFox */
    -webkit-transition-duration :  0.4s; /* Safari / Chrome */
    -o-transition-duration :  0.4s; /* Opera */
    transition-duration :  0.4s; /* W3C */
} 

.link .flat-list.buttons {
    margin-left:25px !important;
    opacity: 0.5 !important;
    
    -moz-transition-property : opacity; /* FireFox */
    -webkit-transition-property : opacity; /* Safari / Chrome */
    -o-transition-property : opacity; /* Opera */
    transition-property : opacity; /* W3C */
    
    -moz-transition-duration : 0.4s; /* FireFox */
    -webkit-transition-duration :  0.4s; /* Safari / Chrome */
    -o-transition-duration :  0.4s; /* Opera */
    transition-duration :  0.4s; /* W3C */
}

.link .flat-list.buttons:hover {
    margin-left:25px !important;
    opacity: 1 !important;
	
    -moz-transition-property : opacity; /* FireFox */
    -webkit-transition-property : opacity; /* Safari / Chrome */
    -o-transition-property : opacity; /* Opera */
    transition-property : opacity; /* W3C */
    
    -moz-transition-duration : 0.4s; /* FireFox */
    -webkit-transition-duration :  0.4s; /* Safari / Chrome */
    -o-transition-duration :  0.4s; /* Opera */
    transition-duration :  0.4s; /* W3C */
}



	.link .midcol .score {
		opacity: 0.4 !important;

	}

.link.last-clicked {
    background: rgba(255,255,255,0.5) !important;
}


.thumbnail  {
        border: 3px solid #FFF !important;
        box-shadow: rgba(0,0,0,0.2) 0 0 8px !important;
        right: 0px !important;
		opacity: 0.5 !important;
        max-height: 50px !important;
		float: right !important;

		
   }
   
 .link:hover .thumbnail {
		opacity: 1 !important;
		-moz-transition-property : opacity; /* FireFox */
		-webkit-transition-property : opacity; /* Safari / Chrome */
		-o-transition-property : opacity; /* Opera */
		transition-property : opacity; /* W3C */
    
    
		-moz-transition-duration : 0.4s; /* FireFox */
		-webkit-transition-duration :  0.4s; /* Safari / Chrome */
		-o-transition-duration :  0.4s; /* Opera */
		transition-duration :  0.4s; /* W3C */
	}

 
a.madeVisible {
     border: 3px solid #FFF !important;
     box-shadow: rgba(0,0,0,0.2) 0 0 8px !important;
}

.thing .title.loggedin {
    
    font-size: 18px !important;
    line-height: 22px !important;
    text-indent: 155px;
    text-shadow: #FFF 0 1px 0 !important;
    transition: 0.5s ease all;
        }

.thing.spam {
    background-color: rgb(250, 128, 114)  !important;
}

p.title {
	width: 90%;
}

.link .rank {
    display:none;
}

.side{
	background-image: none !important;
	background: rgba(255, 255, 255, 0.9) !important;
	border: 1px solid #bbb !important;
	border-top: 0px !important;
	-webkit-border-radius-bottom: 3px !important;
	-moz-border-radius-bottom: 3px !important;
	border-radius-bottom: 3px !important;
	padding: 5px !important;
}

ul.content { 
	margin-left: 2px !important;
	margin-right: 2px !important;
}


}
