css:
@-moz-document url('https://www.tumblr.com/login'),
  url('http://www.tumblr.com/dashboard'),
  url-prefix('http://www.tumblr.com/dashboard/'),
  url-prefix('http://www.tumblr.com/new/'),
  url-prefix('http://www.tumblr.com/blog/'),
  url-prefix('http://www.tumblr.com/tagged/'),
  url-prefix('http://www.tumblr.com/drafts'),
  url-prefix('http://www.tumblr.com/queue'),
  url-prefix('http://www.tumblr.com/edit/') {
.new_post_label .new_post_label_icon {
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/') {
.new_post_label{display:none;}
ol#posts li.post.new_post {
background: #ffffff url('http://30.media.tumblr.com/tumblr_m0gyy8NEB21rqfh05o1_500.png') center center no-repeat;}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;}}

fixed!
} /* icons */
} /* all */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/') {
#dashboard_index,
#dashboard_inbox,
#dashboard_edit_post,
#dashboard_submissions,
#dashboard_drafts,
#dashboard_post_queue,
#dashboard_likes,
#dashboard_followers,
#dashboard_following,
#dashboard_tagged,
#dashboard_directory,
#dashboard_lookup_contacts,
#main_doc_page,
#dashboard_settings,
#dashboard_create_tumblelog,
#dashboard_members,
#main_tag,
#main_meetups,
#main_about,
#main_explore,
#dashboard_goodies,
#main_tagged,
#main_developers,
#main_themes,
#dashboard_manage_themes,
#main_edit_theme,
#main_jobs,
#main_policy,
#dashboard_tumblelog_settings {
background:#ecebeb url(http://25.media.tumblr.com/tumblr_mecybvOeXd1rh9ucdo1_500.png) no-repeat bottom right fixed! important }
#dashboard_index div.l-content,
#dashboard_inbox div.l-content,
#dashboard_submissions div.l-content,
#dashboard_drafts div.l-content,
#dashboard_post_queue div.l-content,
#dashboard_likes div.l-content,
#dashboard_tagged div.l-content,
#dashboard_followers div.l-content,
#dashboard_members div.l-content,
#main_tag div.l-content,
#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(0,0,0,0.1) !important }
.selection_nipple { border-bottom-color: rgba(0,0,0,0.1) !important }
.permalink {background: rgba(0,0,0,0.2) !important }
.avatar_frame {
background-color: rgba(255,255,255,0.1) !important; border-color: rgba(255,255,255,0.1) !important }
.notification {
background: rgba(0,0,0,0.1) !important;
border: rgba(255,255,255,0.1) !important;
background-image: none !important;
margin-bottom: 1px !important }
.notification a { border-bottom: 1px solid rgba(0,0,0,0.2) !important }
.notification a:hover { border-bottom: 1px solid rgba(0,0,0,0.2) !important }
.notification_type_icon { display: none !important }
.nipple { border-right-color: rgba(0,0,0,0.07) !important }
.controls_section li { background: rgba(0,0,0,0.1) !important }
.with_subtitle small { color: rgba(0,0,0,0.5) !important }
.recessed a { color: rgba(255,255,255,0.7) !important }
.radar { display: none !important }
* { text-shadow: none !important }
h1, .title { color: rgba(0,0,0,0.6) !important }
.no_posts_found, h1.dashboard_header { color: rgba(255,255,255,0.9) !important }
.dashboard_options_form {
background: rgba(0,0,0,0.1) !important;
border: none !important;
color: rgba(255,255,255,0.7) !important }
.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a { color: rgba(255,255,255,0.9) !important }
.blue_note, .blue_note a {
background: rgba(0,0,0,0.2) !important;
color: rgba(0,0,0,0.6) !important }
#footer, #footer a {
color: rgba(0,0,0,0.5) !important }
img[src*="http://static.tumblr.com/duegx7i/oKGm04vmm/000.png"] {
opacity: 0;
visibility: hidden }
.new_post {
background: #fff url(http://static.tumblr.com/duegx7i/oKGm04vmm/000.png) no-repeat center bottom }
.new_post_label {
-webkit-text-stroke:0 !important;
color: rgba(0,0,0,0.4) !important;
opacity:1 !important;
top: 0px !important;
padding-top: 80px }
.new_post_label:hover { color: rgba(0,0,0,0.7) !important }
#pagination a {
background: rgba(0,0,0,0.4) !important;
color: rgba(255,255,255,0.7) !important }
#pagination a:hover {
color: rgba(255,255,255,0.9) !important }
#dashboard_controls a, #dashboard_controls .control {
color: rgba(255,255,255,0.7) !important }
.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {
display: none !important }
#right_column a#dashboard_switch_blog_menu_current:hover {
background: url(http://i55.tinypic.com/34nlldd.png);}
#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
background: url(http://i55.tinypic.com/34nlldd.png);
}



}
