css:
/*
 *  Recycled - Sakura girly pink style (http://userstyles.org/styles/79462)
 *    from "Sakura girly pink style (2009-07) v1.0" (http://userstyles.org/styles/19472)
 *      by Q9R42 (http://userstyles.org/users/22827)
 *
 *    background: "The pink garden" by Q9R42 (http://q9r42.deviantart.com/art/The-pink-garden-127822588) 
 */
@-moz-document domain("youtube.com") {

.fm2-title-border-box-gray, .fm2-title-border-box-blue,  *[class="statModule-title statModule-title-open yt-rounded"], *[id="statmodules_inbox"], .homepage-block-heading, #bar, .nav-item {
  background: #fff url('http://q9r42.deviantart.com/art/The-pink-garden-127822588') repeat-x left top !important;
}

* {
  font-size: 13px !important;
  font-family: calibri !important;
  color: #CF74B1 !important;
}

.searchField, #comment_entry_box_text, #user_videos-query, input[type="text"], #itsalltext_generated_id_comment_1 {
  -moz-appearance: none!important;
  border: 1px !important;
}

body:before {/*+++*/
  content: "";
  position: fixed; width: 100%; height: 100%;
  opacity: 0.4;
  background: url('http://fc07.deviantart.net/fs46/f/2009/181/a/b/The_pink_garden_by_Q9R42.png') fixed;
  background-size: cover;
}

body{
  background: transparent !important;/*+++*/
  color: #71024B !important;
}
#masthead-container {background:transparent !important}/*+++*/
.video-title {
  color: #DFA7CF !important;
}

.hLink {
  color: #DFA7CF !important;
}

DIV#chrome-promo,
A.chrome-link,
#side-announcement-box {
  display: none !important;
}

*[class="clear feedmodule-border-gray yt-rounded"],    *[class="clear feedmodule-border-blue yt-rounded"], 
 *[class="homepage-side-block"], .fm2-top-cap {
  border-top: none !important;
}

.feedmodule-normal {
  background: transparent !important;
}

.img-blue-bottom-cap {
  height: 1px !important;
  margin-bottom: 6px !important;
}

.feedmodule-data {
  border-top: none !important;
}

.feedmodule-upper-left-right-border, .feedmodule-upper-left-right-border-blue {
  border: 1px solid #bbb !important;
  -moz-border-radius-bottomleft: 6px !important;
  -moz-border-radius-bottomright: 6px !important;
  border-bottom-left-radius: 6px !important;/*+++*/
  border-bottom-right-radius: 6px !important;/*+++*/
}

*[class="statModule-title statModule-title-open yt-rounded"] {
  height: 24px !important;
  overflow: visible  !important;
  padding-bottom: 8px !important;
  position: relative  !important;
  background-position: -4px -2px !important;
}

*[class="module-item-wrapper-inner yt-rounded"] {
  border-top: none !important;
}

.homepage-block-heading {
  background: #fff url('http://q9r42.deviantart.com/art/The-pink-garden-127822588') repeat-x left top !important;
  margin-top: -11px !important;
  margin-left: -16px !important;
  -moz-border-radius: 8px 8px 0 0 !important;
  border-radius: 8px 8px 0 0 !important;/*+++*/
  width: 278px !important;
  height: 19px !important;
  color: #000 !important;
  border-top: #BFBFBF 1px solid !important;
  border-left: #BFBFBF 1px solid !important;
  border-right: #BFBFBF 1px solid !important;
  background-position: 2px 8px  !important;
  height: 32px   !important;
  background-position: -4px -1px !important;
  border-bottom: none  !important;
}

/* - - - - - - - - */
#masthead-positioner {
  background: url('http://fc07.deviantart.net/fs46/f/2009/181/a/b/The_pink_garden_by_Q9R42.png')
   fixed;
  background-size: 100% auto;
}
DIV.yt-card,
DIV#yt-masthead-container,
DIV#masthead-appbar {
  background-color: transparent;
}

HTML>body:before {
  opacity: 1;
}

button.yt-uix-button-subscribe-branded,
button.yt-uix-button-subscribe-branded:hover/*,
.compact-shelf .yt-uix-button-shelf-slider-pager*/ {
  background: rgb(226, 154, 190);
}
button.yt-uix-button-subscribe-branded SPAN {
  color: #fff !important;
}

.yt-lockup-content {
  background: rgba(255,255,255,.3);
  border: thin solid rgb(226, 154, 190);
}
DIV.yt-lockup.yt-lockup-grid H3.yt-lockup-title {
  max-width: none;
  text-align: center;
}
.yt-lockup-content:hover .yt-lockup-byline,
.yt-lockup-content:hover .yt-lockup-meta {
  background-color: #fff;
}
DIV.yt-lockup-grid .yt-lockup-meta {
  max-width: none;
}

DIV.yt-lockup-content {
  background: transparent;
}
#appbar-nav {
  background: linear-gradient(90deg, rgba(255,255,255,0) 30%, rgba(255,255,255,.6) 40%,  rgba(255,255,255,.6) 60%,  rgba(255,255,255,0) 70%);
}
#appbar-nav:hover {
  background-image: linear-gradient(90deg, rgba(255,255,255,0) 20%, rgba(255,255,255,1) 40%,  rgba(255,255,255,1) 60%,  rgba(255,255,255,0) 80%);
}
button.yt-uix-button,
button.yt-uix-button:hover/*,
.compact-shelf .yt-uix-button-shelf-slider-pager*/ {
}
#yt-masthead .yt-uix-button,
#yt-masthead .yt-uix-button-default {
  border-color: rgb(226, 154, 190);
  background-color: #f0f0f0;
}

/* - - - */
#yt-masthead .yt-uix-button,
#yt-masthead .yt-uix-button-default {
  border-color: rgb(226, 154, 190);
  background-color: #F9E9F1;
}

DIV.all-comments, DIV.all-comments A {background-color: initial}
DIV.all-comments {color: #F9E9F1 !important}
DIV.all-comments * {color: inherit !important}

#watch-header .watch-title {
  color: #A66384 !important;
  text-shadow: #fff -2px 2px;
  font-size: xx-large !important;
}
#watch-header .yt-user-info a {
  color: #F9E9F1 !important;
}
#eow-description {
  background-color: #F9E9F1;
}

.content-wrapper {
  border: thin solid rgb(226, 154, 190);
}
.content-wrapper .title {
  background-color: #F9E9F1;
  text-align: center;
}
.content-wrapper:hover {
  background-color: #F9E9F1;
}

.comment-item .comment-text-content {
  background-color: #F9E9F1;
  display: inline;
  padding-left: 8px;
  padding-right: 8px;
  border-radius: 10px;
}
.comment-item .user-name {
  color: #F9E9F1 !important;
}

}
