css:
@-moz-document url-prefix("https://app.mysms.com/") {
body {

color: #777;
font-family: century gothic, "Droid Sans", Prelude, Verdana, sans-serif;
height: 100%;
background-color: #111;
}

.mainPanel {
max-width: 104em;
width: 100%;
margin: 0 auto;
box-shadow: 0 0 3em black;
-moz-box-shadow: 0 0 3em #333;
background-color: #000;
}

.GO2BGF-CEI {
font-weight: bold;
background-color: #000;
vertical-align: middle;
}
.dataPanel.conversation {
background-color: #000;
color:#FFF;
}

.messageComposePanel textarea {
margin: 0;
width: 100%;
display: block;
box-sizing: border-box;
-webkit-box-sizing: border-box;
-moz-box-sizing: border-box;
-ms-box-sizing: border-box;
min-height: 5.8em;

resize: vertical;
}
.dataPanel.conversation .container > div {
position: relative;
height: 7em;
color:#FFF;


background: url(data:image/svg+xml;base64,);
background: -moz-linear-gradient(top,  rgba(0,0,0,1) 0%, rgba(9,10,10,0.82) 89%, rgba(36,38,40,0.8) 100%); 
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,1)), color-stop(89%,rgba(9,10,10,0.82)), color-stop(100%,rgba(36,38,40,0.8))); 
background: -webkit-linear-gradient(top,  rgba(0,0,0,1) 0%,rgba(9,10,10,0.82) 89%,rgba(36,38,40,0.8) 100%); 
background: -o-linear-gradient(top,  rgba(0,0,0,1) 0%,rgba(9,10,10,0.82) 89%,rgba(36,38,40,0.8) 100%); 
background: -ms-linear-gradient(top,  rgba(0,0,0,1) 0%,rgba(9,10,10,0.82) 89%,rgba(36,38,40,0.8) 100%); 
background: linear-gradient(to bottom,  rgba(0,0,0,1) 0%,rgba(9,10,10,0.82) 89%,rgba(36,38,40,0.8) 100%); 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#000000', endColorstr='#cc242628',GradientType=0 ); 

}
.dataPanel.conversation .divider {
background-image: -webkit-gradient(linear, left top, right bottom, from(black), color-stop(0.5, #E0E0E0), to(black));
background-image: -moz-linear-gradient(left, black 0%, #E0E0E0 50%, black 100%);
background-image: -o-linear-gradient(left, black 0%, #E0E0E0 50%, black 100%);
}
.dataPanel.conversation.single .selected {
font-size:1.5em;
}

.dataPanel.conversation .container > div:hover {
height: 7em;
font-size:1.5em;
}
.contentPanel {
border-top: 0.1em solid #FFF;
background-color: #000;
}
.messageComposePanel {
background-color: #000;
}
.messageComposeView {
background-color: black;
}
.messageComposeView .recipientEditorWrapper {
background-color: black;
border-bottom: 1px solid #999;
with: 100%;
color: white;
}

.messageListView.top .touchScrollPanel {
background-color: #000;
color:#FFF;
}
.dataPanel.message .container > div.incoming > span.bubble {
background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, black), color-stop(1, #00252E));
background-image: -moz-linear-gradient(center top, black 0%, #00252E 100%);
background-image: -o-linear-gradient(top, black 0%, #00252E100%);
margin: 0.7em 0.4em 0.7em 0.8em;
}

.dataPanel.message .container > div > span.bubble {
background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, black), color-stop(1, #0B1020));
background-image: -moz-linear-gradient(center top, black 0%, #0B1020 100%);
background-image: -o-linear-gradient(top, black 0%, #0B1020 100%);
border: 0.1em solid #9E9E9E;
box-shadow: #00A3FF 0.1em 0.1em 0.3em;
-webkit-box-shadow: #02E1FF 0.1em 0.1em 0.9em;
-moz-box-shadow: #ccc 0.1em 0.1em 0.3em;

}
.outgoing .bubble 
{
border-radius: 1em 1em 0em 1em !important;
-moz-border-radius:  1em 1em 0em 1em !important;
}
.incoming .bubble 
{
border-radius: 1em 1em 1em 0em !important;
-moz-border-radius:  1em 1em 1em 0em !important;
}

.bubble:hover {
font-size:1.4em;
}
.dataPanel.message .header {
font-size: 1.4em;
font-weight: bold;
padding: 0.0em 0;
text-align: center;


background: rgb(0,0,0); 

background: url(data:image/svg+xml;base64,);
background: -moz-linear-gradient(left,  rgba(0,0,0,1) 0%, rgba(34,35,38,1) 50%, rgba(0,0,0,1) 100%); 
background: -webkit-gradient(linear, left top, right top, color-stop(0%,rgba(0,0,0,1)), color-stop(50%,rgba(34,35,38,1)), color-stop(100%,rgba(0,0,0,1))); 
background: -webkit-linear-gradient(left,  rgba(0,0,0,1) 0%,rgba(34,35,38,1) 50%,rgba(0,0,0,1) 100%); 
background: -o-linear-gradient(left,  rgba(0,0,0,1) 0%,rgba(34,35,38,1) 50%,rgba(0,0,0,1) 100%); 
background: -ms-linear-gradient(left,  rgba(0,0,0,1) 0%,rgba(34,35,38,1) 50%,rgba(0,0,0,1) 100%); 
background: linear-gradient(to right,  rgba(0,0,0,1) 0%,rgba(34,35,38,1) 50%,rgba(0,0,0,1) 100%); 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#000000', endColorstr='#000000',GradientType=1 ); 




}


body {
    background-image: none;
    background-color: rgb(46, 46, 46);
}
.dataPanel.conversation.single .container>div:hover {
background-color: rgb(29, 29, 29);
background-image: -webkit-linear-gradient(top, rgb(29, 29, 29) 0%, rgb(8, 51, 90) 100%);
background-image: -moz-linear-gradient(top, rgb(29, 29, 29) 0%, rgb(8, 51, 90) 100%);
background-image: -o-linear-gradient(top, rgb(29, 29, 29) 0%, rgb(8, 51, 90) 100%);
background-image: linear-gradient(to bottom, rgb(29, 29, 29) 0%, rgb(8, 51, 90) 100%);
}
.single {
color: #FFF;
      }
.emojiButton, .fileUpload,
 .messageComposePanel .buttonbar td.right,
  .messageListView .charCounter, .selected {
background-image: -webkit-linear-gradient(top, rgb(29, 29, 29) 0%, rgb(8, 51, 90) 100%);
background-image: -moz-linear-gradient(top, rgb(29, 29, 29) 0%, rgb(8, 51, 90) 100%);
background-image: -o-linear-gradient(top, rgb(29, 29, 29) 0%, rgb(8, 51, 90) 100%);
background-image: linear-gradient(to bottom, rgb(29, 29, 29) 0%, rgb(8, 51, 90) 100%);
border:#555 thin solid;
           color:#FFF!important; 
                  text-shadow: rgba(0,0,0.8) 0 0 8px;
}

.gwt-TextArea {
    background: rgb(39, 39, 39);
    color: #FFF;
    text-shadow: #000 0 -6px 6px,#000 0 10px 6px;
    box-shadow: #000 0 2px 12px inset;
           color:#FFF!important; 
           font-weight:bold;
}

.selected .bubble .message{
       color:#FFF!important; 
       text-shadow: #000 0 -6px 6px,#000 0 10px 6px;
}

.message {
    color:#FFF!important;
}
.messageComposeView .composeContainerWrapper {
background-color: rgb(17, 17, 17);
border-bottom: panelBorder;
box-shadow: 0 0 0.3em #ccc;
}
.dataPanel.conversation .divider {
height: 0;
bottom: 0;
left: 0;
right: 0;
border-top: 1px solid rgb(0, 0, 0);
border-bottom: 1px solid rgb(34, 34, 34);
}

input[type="text"], input[type="tel"], input[type="password"] {
background-color: transparent;
  color:#FFF;
text-shadow: #000 0 0px 6px,#000 0 10px 6px;
}

.recipientEditorPanel {
background-color: rgb(53, 53, 53);
color:#FFF;
}

* {
  -webkit-transition: all 0.2s ease-in-out;
  -moz-transition: all 0.2s ease-in-out;
  -o-transition: all 0.2s ease-in-out;
  transition: all 0.2s ease-in-out;

transition-delay: 0.1s;
}
  
}
