css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.tumblr.com/dashboard") {
body{

background: url('http://i.imgur.com/ORZqn.gif') center right #000000 repeat fixed !important;

}
    
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background color: #ffffff; !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {


    background: url('http://i.imgur.com/VBaZE.jpg') center right #000000 repeat fixed !important;

    -webkit-border-radius: 20px;

    -moz-border-radius: 20px;

    border-radius: 20px;

    margin: auto;

    padding: 20px 20px;

  }

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {

    background: none;


  }
  
.selection_nipple {
border-bottom: 12px #00cc00 solid !important;
}

#right_column:after {
    background: -moz-linear-gradient(center top , #00cc00 0px, rgba(44, 71, 98, 0) 100%) repeat scroll 0 0 transparent;
}






}
