css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.youtube.com"), url-prefix("https://www.youtube.com") {

.site-left-aligned #page, .site-left-aligned #yt-masthead, .site-left-aligned #alerts, .site-left-aligned #ad_creative_1, .site-left-aligned #footer-hh, .site-left-aligned #masthead-subnav, .site-left-aligned #masthead_child_div, .site-left-aligned #masthead-expanded-lists-container, .site-left-aligned #ticker .ytg-wide, .site-left-aligned #baseDiv { margin: 0 auto !important; }

.site-left-aligned.guide-enabled .watch7-playlist, .site-left-aligned.guide-enabled #watch7-video-container, .site-left-aligned.guide-enabled #watch7-main-container {padding-left: 25% !important; padding-right: 25% !important;}

#yt-masthead-container { width: 1003px !important; margin: 0 auto !important}

body #masthead-expanded-container { max-width: 1003px !important }

div#page.search-base {margin: 0 auto !important; width: 1003px !important}

.site-left-aligned #page.watch, .site-left-aligned #page.watch #guide, .site-left-aligned .watch7-playlist, .site-left-aligned #watch7-video, .site-left-aligned #watch7-main { left: -100px !important; margin: 0 auto !important; left: auto !important}

.site-left-aligned #page.watch #guide-container { left: -150px !important }
}
