css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cs-manager.com"){

a:hover{
color: #FF6600 !important
}

#header{
background: white !important
}

ul#friend li a span {
color: white !important;
}

ul#tab li span{
font-weight: normal !important;
color: #FFCC33 !important
}

ul#friend li a {
color: #FFCC33 !important;
background: #999999 !important
}

ul#friend a:hover {
font-weight: bold !important;
color: #FF6600 !important;
background: #999999  !important
}

h3 {
color: white !important;
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
background: #666666 !important
}

ul.menu li a {
color: #FFCC33 !important;
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
background: #999999 !important
}

ul.menu a:hover{
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
font-weight: bold !important;
background: #999999  !important;
color: #FF6600 !important
}

ul#tabtop li a {
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
text-decoration: underline !important;
color: #FFCC33 !important;
background: #999999 !important
}

ul#tabtop li a.logout {
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
text-decoration: underline !important;
color: #FFCC33 !important;
background: #999999 !important
}

ul.gameinfo li a {
color: #FFCC33 !important;
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
background: #999999 !important
}

ul#tabtop li a:hover {
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
color: #FF6600 !important;
background: #999999 !important
}

ul#tabtop li a.logout:hover {
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
color: #FF6600 !important;
background: #999999 !important
}

ul.gameinfo a:hover {
color: #FF6600 !important;
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
background: #999999 !important
}

form#search {
border-top: white !important;
border-bottom: white !important;
background: #999999 !important
}

form#search input, form#search select {
margin-bottom: 2px !important;
margin-top: 2px !important;
color: #FFCC33 !important; 
border-left: 1px solid white !important;
border-right: 1px solid white !important;
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
background: #999999 !important
}

input.button {
color: #FFCC33 !important;
border-width: 2px !important;
border-style: solid !important;
border-left: white !important;
border-right: white !important;
border-top: white !important;
border-bottom: white !important;
background: #999999 !important
}

table.dev td.head{
color: #FFCC33 !important;
background: #999999 !important
}

h2{
background: #999999 !important;
color: #FFCC33 !important;
border-top: 1px solid white !important;
border-bottom: 1px solid white !important;
border-left: 1px solid white !important;
border-right: 1px solid white !important
}

table.data td.head {
background: #999999 !important;
color: #FFCC33 !important
}

table.lineup td.head{
background: #999999 !important;
color: #FFCC33 !important
}

table.popup td.head{
background: #999999 !important;
color: #FFCC33 !important
}

table.popup input, table.popup select{
background: #999999 !important;
color: #FFCC33 !important;
border: 1px solid #999999 !important
}

table.dev input, table.dev textarea, table.dev select{
background: #999999 !important;
color: #FFCC33 !important;
border: 1px solid #999999 !important
}

table.mail td.head {
background: #999999 !important;
color: #FFCC33 !important;
border: 1px solid #999999 !important
}
}
