css:
@-moz-document url-prefix("http://www.youtube.com"), domain("youtube.com") {
body { background: #000; }
#yt-masthead-container.yt-masthead-hh, #yt-masthead-container  { 
background: #202020 !important;
border-bottom: 1px solid #444 !important;
}

body #footer-hh-container, body #footer-container {
background: #202020 !important;
border-top: 1px solid #444 !important;
}
#footer-hh-main { border-bottom: 1px solid #444;}
#footer-hh-links-primary a { color:#fff; }


.guide-item, .guide-header-item { 
color: #fff !important; 
}
.branded-page-v2-col-container, 
.branded-page-v2-primary-col { 
background-color: #252525 !important; 
border-left: none !important;
border-right: none !important;
  border-bottom: none !important;
}
.branded-page-v2-detached-top .branded-page-v2-primary-col {
border-top: 0 !important;
}

.feed-item-container .feed-item-main {
border-bottom: 1px solid #444;
}
.branded-page-v2-body .feed-item-container:hover {
background-color: #191919 !important;
}

ul.yt-uix-button-menu, .yt-uix-button-menu { background-color: #000
}
.yt-uix-button-menu .yt-uix-button-menu-item {
color:#fff
}
.yt-uix-button-menu { border: 1px solid #666} 
.yt-uix-button-menu-item.uploads.checked:hover, .yt-uix-button-menu-item.email-on-upload.checked:hover {
background-image: url(http://s.ytimg.com/yts/img/guide/checkmark-vflp4s9hS.png);
}
.yt-uix-button-menu-item.uploads.checked, .yt-uix-button-menu-item.email-on-upload.checked {
background-image: url(http://s.ytimg.com/yts/img/guide/checkmark_hovered-vflr04Evc.png);
}

.exp-new-site-width #guide + #content, .branded-page-v2-container {
width: 100% !important;
}

.feed-header .metadata, .feed-header .metadata a {
color:#fff;
}

.branded-page-v2-secondary-column-hidden .branded-page-v2-col-container{
background-image: none !important;
background-color: #202020 !important;
border-bottom: 1px solid #444 !important;
}
#subscription-manager-container .even td {
background-color: #303030 !important;
border-color:#444 !important;
}
#subscription-manager-container .subscription-count-header, .branded-page-v2-secondary-column-hidden .branded-page-v2-primary-col {
border-color:#444 !important;
}


.epic-nav-dropdown-group:hover, a.yt-uix-button-epic-nav-item:hover, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
color:#fff;
}

.feed-header-feed-filter .yt-uix-button-epic-nav-item.selected, .feed-header-feed-filter .yt-uix-button-epic-nav-item:hover {
color: #999 !important;
}

.feed-header .feed-channel-header-title a {
color: #AF2B26;
}

.feed-header { 
border: 0 !important; 
background-color: #333; 
margin-top: 0 !important;
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px; 
-khtml-border-radius: 10px;
}
.branded-page-v2-primary-col-header-container, .branded-page-v2-primary-col {
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px; 
-khtml-border-radius: 10px;
}
.branded-page-v2-col-container {
margin: 20px 0 0 0;
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px; 
-khtml-border-radius: 10px;
}

.branded-page-v2-col-container-bottom-border {
border: 0;
margin-right: 0;
}
#watch7-content { 
background-color: #202020; 
}
#watch-description { color: #fff; }
#watch7-discussion { color: #ccc; border: 1px solid #444 }
#watch7-secondary-actions .yt-uix-button { color: #fff; }
#watch7-sidebar .video-list-item .title { color: #fff; }

#watch7-notification-area {

-moz-border-radius: 10px 10px 0px 0px;
-webkit-border-radius: 10px 10px 0px 0px;
border-radius: 10px 10px 0px 0px; 
-khtml-border-radius: 10px 10px 0px 0px;
}
  
#watch7-headline {
-moz-border-radius: 10px 0px 0px 0px;
-webkit-border-radius: 10px 0px 0px 0px;
border-radius: 10px 0px 0px 0px;
-khtml-border-radius: 10px 0px 0px 0px;
}

li.guide-section h3 {
color:#d11820;
}

.context-playing.guide-context-item .title { color:#000 !important; }
.guide-context-item .title  {
color: #fff !important;
}


.yt-uix-button.guide-sort-button img {
opacity: 1;
}
.yt-uix-button.guide-sort-button img {
opacity: 0.6;
}

.yt-uix-form-input-select, .yt-uix-form-input-text, .yt-uix-form-textarea {
color:#fff;
}

.filter-match {
font-weight: bold;
color: #d11820;
}

#comments-view h4, #comments-view h4 a {
color: #fff;
}

#share-panel-buttons .yt-uix-button {
color: #d11820;
text-shadow: none;
}

#action-panel-addto .watch-playlists-drawer h3, #watch-actions-area .watch-playlists-drawer h3 {
color:#fff;
}


.guide-channels-content ::-webkit-scrollbar, 
.guide-module-content ::-webkit-scrollbar{
background:#000;
}

.guide-channels-content ::-webkit-scrollbar-track, 
.guide-module-content ::-webkit-scrollbar-track
{
border:0;
}

.guide-channels-content ::-webkit-scrollbar-track-piece,
.guide-module-content ::-webkit-scrollbar-track-piece {
background:#222;
margin: 0;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
border-radius: 5px; 
-khtml-border-radius: 5px;
}
.guide-channels-content ::-webkit-scrollbar-button,
.guide-module-content ::-webkit-scrollbar-button {
background:#000;
}

.category-header .category-title  {
color:#fff;
}

.subscribable-content-username {
color: #ddd;
}
.subscribable-content-rec{
width:175px;
}
.yt-subscription-button-subscriber-count-unbranded-horizontal, .yt-subscription-button-subscriber-count-unbranded-vertical  {
color:#661115;
}
.feed-item-actions-line .feed-item-owner{
color:#fff;
}
.guide-context-item .title{
color:#fff;
}
.guide-context-item:hover .title{
color:#000;
}
.guide-channels-content ::-webkit-scrollbar-thumb, 
.guide-module-content ::-webkit-scrollbar-thumb {
background: #AF2B26;
border: 0;
margin: 20px 0 0 0;
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px; 
-khtml-border-radius: 10px;
}

.guide-channels-content ::-webkit-scrollbar-thumb:hover, 
.guide-module-content ::-webkit-scrollbar-thumb:hover {
background: #661115;
}

.watch-playlists-drawer .playlist-addto-title-options label {
color:#fff;
}


.guide-channels-content ::-webkit-scrollbar-track, 
.guide-module-content ::-webkit-scrollbar-track {

}
.comment-list .comment a, a { color:#d11820;}
#comments-view a:hover, #comments-view .comment:hover .yt-uix-button, #comments-view:hover .comment-text a, a:hover {
color: #AF2B26;
}
.live-comments-setting { background-color:#000; border: 0; padding:12px 0;}


.yt-uix-form-input-select, .yt-uix-form-input-text, .yt-uix-form-textarea {
color:#000;
background-color:#fff;
}

.yt-uix-form-input-checkbox-container input:checked + .yt-uix-form-input-checkbox-element {
background-color:#fff;
}

.yt-uix-form-input-checkbox-container input + .yt-uix-form-input-checkbox-element {
background-color:#fff;
}

.share-panel, .watch-actions-stats, .action-panel-content {
color:#fff;
}

#watch-description-extra-info .metadata-info-title {
color:#d11820;
}

.feed-item-container .feed-item-main {
border-bottom: 0 !important;
}

.watch-playlists-drawer ul {
color:#000;
}

#watch7-sidebar .video-list-item:hover .title {
color:#d11820;
}

.guide-item .guide-count {
background: #AF2B26;
color: white;
background-image: -moz-linear-gradient(top,#AF2B26 0,#942422 100%);
background-image: -ms-linear-gradient(top,#AF2B26 0,#942422 100%);
background-image: -o-linear-gradient(top,#AF2B26 0,#942422 100%);
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#AF2B26),color-stop(100%,#942422));
background-image: -webkit-linear-gradient(top,#AF2B26 0,#942422 100%);
background-image: linear-gradient(to bottom,#AF2B26 0,#942422 100%);
font-weight: bold;

min-width: 12px;
text-align: center;
}

#share-panel-buttons .yt-uix-button:hover, #share-panel-buttons .yt-uix-button:active, #share-panel-buttons .yt-uix-button.yt-uix-button-active, #share-panel-buttons .yt-uix-button.yt-uix-button-toggled {
border-bottom-color: #fff;
}

body #masthead-expanded-container { 
background-color: #222; 
border-bottom: 1px solid #B00;
padding: 0;
}
#masthead-expanded .masthead-expanded-menu-item a { color: #fff; }
#masthead-expanded-menus-container { 
margin: 0;
padding: 12px;
background-color: #333;
border-left:2px solid #444;
}
.yt-uix-slider { 
margin-top:14px;
}

.branded-page-v2-secondary-col { display: none; }
.branded-page-v2-col-container { background-image: none; }

.comment-text, .comment-show-hide { color: #fff }
ul.comment-list li {
padding: 10px;
background: #222;
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px; 
-khtml-border-radius: 10px;
}

.comment-list .comment .content {
width: 98%;
}
#watch-discussion li.comment .yt-thumb-clip img { width: 88px !important; }
#watch-discussion .comment-body { display: block; }
#comment-video-info { color:#fff; margin: 20px 0 0;}
#watch-description-extras .title { color: #CCC; }

.feed-item-container {
width: 440px;
min-height: 200px;
float: left;
margin: 0px;
border: 1px solid transparent;
margin-right: 25px;

}
.feed-item-container:hover {
border-color: #333 !important;
}
.feed-load-more-container {
clear: both;
}
#feed li:first-child .feed-item-container, #feed li.feed-item-container:first-child { padding-top: 0; }
#header #ad_creative_1  {
padding: 0;
}

.compact-shelf .yt-uix-button-shelf-slider-pager {
background: #333 !important;
}
.yt-uix-slider-body, .yt-uix-shelfslider-body {
margin: 0 24px !important;
}

.guide-enabled #content { width: 100% !important; }


@media only screen and (max-height: 1040px) {
	.watch-medium #player-api {
		width: 1080px !important;
    	height: 658px !important;
	}
}
  
.branded-page-v2-masthead-ad-header.masthead-ad-expanded .branded-page-v2-primary-col {
	border-top: 2px solid #AF2B26;
}
.channel-header .branded-page-header-title .spf-link {
	color: #AF2B26 !important;
}

.epic-nav-dropdown-group:hover, a.yt-uix-button-epic-nav-item:hover, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
	color: #cc181e;  
}

/* Fixed the floating error. Thanks Duskwolf for submitting it. */
.feed-item-container:first-child {
  margin-top: 0px !important;
}
.feed-list {
	padding: 10px;  
}
/*Nofication Button */
#yt-masthead-user #sb-button-notify {
  background: #fff;  
  margin: 0 6px 0 0;
}
#yt-masthead-user .yt-uix-button-icon-bell {
  left: 4px;
}
#yt-masthead-user .sb-notif-on .yt-uix-button-content {
  bottom: -5px;
  top: inherit;
  left: 0px;
  width: 32px;
  border: none;
  
}
/*Display name (top bar) */
#yt-masthead-user-displayname {
 color: #cc181e;
  font-weight: bold;
}

#masthead-expanded .masthead-expanded-menu-header, #masthead-expanded .masthead-expanded-menu-item {
  color: #cc181e !important;
  font-weight: bold;
}
body #masthead-expanded-menu-account-info.email-only {
  color: #cc181e !important;
}

/*Side bar title, fix. */
#watch7-sidebar {
	-moz-border-radius: 0px 10px 10px 0px;
	-webkit-border-radius: 0px 10px 10px 0px;
	border-radius: 0px 10px 10px 0px; 
	-khtml-border-radius: 0px 10px 10px 0px; 
}
#watch7-sidebar .video-list-item .title {
    color: #B00;
}
#watch7-sidebar .watch-sidebar-section {
    margin: 0;
    padding: 10px 5px;    
}
  
  
/* New Comments Styles, now that it is an iframe we cannot style this element. So we need to make it look as good around it.*/
#watch-discussion { 
    background-color: #fff;
    padding: 2px 10px;
    -moz-border-radius: 0px 0px 10px 10px;
	-webkit-border-radius: 0px 0px 10px 10px;
	border-radius: 0px 0px 10px 10px; 
	-khtml-border-radius: 0px 0px 10px 10px;
}
#action-panel-details.action-panel-content {
	padding-bottom: 5px;
}
#watch-description.yt-uix-expander-collapsed #watch-description-content {
	height: 71px;
}
}
