css:
@-moz-document url-prefix("http://www.youtube.com"), url-prefix("https://www.youtube.com") {
.site-left-aligned #page, .site-left-aligned #yt-masthead, .site-left-aligned #alerts, .site-left-aligned #ad_creative_1, .site-left-aligned #footer, .site-left-aligned #masthead_child_div, 
.site-left-aligned #page, #masthead-expanded, #yt-masthead-container #yt-masthead, .exp-new-site-width #footer { width: 1003px !important; }

.exp-new-site-width #page.branded-page, #yt-masthead-container { width: 100% !important; }

.site-left-aligned #page, .site-left-aligned #page.watch, .site-left-aligned #yt-masthead, #yt-masthead-container #yt-masthead, .site-left-aligned #alerts, .site-left-aligned #ad_creative_1, .site-left-aligned #footer, #footer-container #footer, .site-left-aligned #masthead-subnav, .site-left-aligned #masthead_child_div, .site-left-aligned #masthead-expanded-lists-container, .site-left-aligned #ticker .ytg-wide, .site-left-aligned #baseDiv, .exp-new-site-width #page.branded-page, .site-left-aligned.guide-enabled .watch7-playlist, .site-left-aligned.guide-enabled #watch7-video-container, .site-left-aligned.guide-enabled #watch7-main-container, .site-left-aligned.sidebar-expanded #page, .site-left-aligned.sidebar-expanded #yt-masthead, .site-left-aligned.sidebar-expanded #alerts, .site-left-aligned.sidebar-expanded #ad_creative_1, .site-left-aligned.sidebar-expanded #footer, .site-left-aligned.sidebar-expanded #masthead-subnav, .site-left-aligned.sidebar-expanded #masthead_child_div, .site-left-aligned.sidebar-expanded #masthead-expanded-lists-container, .site-left-aligned.sidebar-expanded #ticker .ytg-wide, .site-left-aligned.sidebar-expanded #baseDiv, #masthead-expanded #masthead-subnav.ul { margin: 0 auto !important; }

#masthead-expanded-container {
padding: 10px 0px 10px 1px !important;
border-right-style: Solid;
border-right-color: #e2e2e2;
border-right-width: 1px;
}

.site-left-aligned.guide-collapsed #watch7-video-container, .site-left-aligned.guide-collapsed #watch7-owner-container, .site-left-aligned.guide-collapsed #player, .site-left-aligned.guide-collapsed #watch7-main-container { padding-left: 43px !important; }
.site-left-aligned.guide-collapsed .watch7-playlist { position: Relative !important; }
.site-left-aligned.guide-expanded .watch7-playlist  { position: Relative !important; }
.site-left-aligned #page.watch #guide-container, .site-left-aligned #page.watch #guide-main, .site-left-aligned #page.watch #watch-context-container {left: -50px !important; }
.guide-module-toggle-label span { opacity: 0; }
.collapsible-guide #guide-main .guide-module-content {background: White;}

@media screen and (min-width: 1350px) {

.site-left-aligned.guide-enabled #watch7-video-container, .site-left-aligned.guide-expanded #watch7-owner-container, .site-left-aligned.guide-collapsed #watch7-owner-container, .site-left-aligned.guide-enabled 
#player { margin-left: -50px !important; }
.site-left-aligned.guide-enabled .watch7-playlist, .site-left-aligned.guide-collapsed .watch7-playlist { padding-left: -20 !important;  }
.guide-module-toggle-label span { opacity: 1; }
#watch-owner-container, #watch7-owner-container { width: 945px !important; }
}

#footer, #footer-main, #footer-links, #footer-links-primary, #footer-links-secondary { display: table !important; margin-left: auto !important; margin-right: auto !important; }

.primary-col { width: Auto !important; }
#results { width: 640px !important; }

#watch7-container.watch-wide:before { 
content:"";
display: block;
width: 19998px; 
margin: 0 0 0 -9999px; 
height: 665px;
background-color: #000000;
position: Absolute;
top: -15px;
z-index: -3; 
}

#watch7-container:before { 
content:"";
display: Block;
width: 19998px; 
margin: 0 0 0 -9999px; 
height: 665px;
background-color: #000000;
position: Absolute;
top: -15px;
z-index: -3; 
}

#body-container {overflow:Hidden;}

.video-stream {
position:Absolute !important;
width: 100% !important;
    height: 100% !important;
    left: 0px !important;
}


#player-legacy, #player-api  {margin-top: -15px !important; }
#watch7-main-container {padding-top: 10px !important; }
#watch7-sidebar {margin-top: 0!important;}

#watch7-creator-bar { width: 600px; }
.watch-playlist #watch7-creator-bar, .watch-wide #watch7-creator-bar { width: 905px !important; }



#player-legacy, #player-api.player-width.player-height.off-screen-target.watch-content.player-api{ 
  width: 1100px  !important }
#player-legacy, #player-api.player-height.player-height.off-screen-target.watch-content.player-api { 
  height: 650px  !important }

.site-left-aligned.guide-expanded #watch7-video-container, .site-left-aligned.guide-expanded #watch7-owner-container, .site-left-aligned.guide-expanded #watch7-main-container, .site-left-aligned.guide-expanded #player { padding-left: 43px !important; }

.site-left-aligned #page.watch #guide-main, .site-left-aligned #page.watch #watch-context-container { left: -150px !important; }

#playlist {min-width: 800px; position:absolute;
left:393px;
top:660px;
}

.watch7-playlist-bar-left {position:absolute;
left:0px;
top:-34px;
  width: 600px !important;}

.watch7-playlist-bar {width: 800px !important;}

#watch7-playlist-tray-container {
height: 90px !important;}

#masthead-expanded {margin-left:auto;
margin-right:auto;}
}
