css:
/*Userstyles.org - Fluffy snowy orange v.4.3*/

/*Все мои стили здесь - http://userstyles.org/users/117267*/

/*All my styles here - http://userstyles.org/users/117267*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
body {
background: url("http://savepic.ru/3445617.jpg") no-repeat top !important;
background-color: #F6F7FC !important;
max-width: 1250px !important;
margin: 140px auto !important;
font: 85% Verdana, Helvetica, arial, sans-serif !important;
}

a {
color: #4DA2FA !important;
text-decoration: none !important;
}

a:hover {
color: #4DA2FA !important;
text-decoration: underline !important;
}

a:visited {
color: #F4993D !important;
text-decoration: underline !important;
}



/*==================== Home page - Домашняя страница  ===================*/

#main-header {
background-color: #4DA2FA !important;
margin-left: 0 !important;
}

#main-header > H1 {
color: #FFD8B7!important;
font: 170% Verdana, Helvetica, sans-serif !important;
}

#main-header > H1 > A {
color: #FFFFFF !important;
text-decoration: none !important;
text-transform: capitalize;
}

#left-sidebar > NAV {
background-color: #FFD8B7 !important;
margin-left: 0 !important;
}

#search {
background-color: #FFD8B7 !important;
margin-left: 0 !important;
}

#related {
background-color: #FFD8B7 !important;
margin-left: 0 !important;
}

#left-sidebar > NAV > UL > LI > A {
color: #4DA2FA !important;
font-size: 16px !important;
font-weight: bold;
text-decoration: none !important;
}

#left-sidebar > NAV > UL > LI {
color: #F6F7FC !important;
font-size: 16px !important;
font-weight: bold;
}

#left-sidebar > NAV > UL > LI:first-child > A:hover,
#left-sidebar > NAV > UL > LI:nth-child(2) > A:hover,
#left-sidebar > NAV > UL > LI:nth-child(3) > A:hover,
#left-sidebar > NAV > UL > LI:nth-child(4) > A:hover,
#left-sidebar > NAV > UL > LI:last-child > A:hover {
color: #F6F7FC !important;
font-size: 16px !important;
font-weight: bold;
}

#show-search-options {
color: #4DA2FA !important;
font-size: 14px !important;
font-weight: bold;
text-decoration: none !important;
}

#show-search-options:hover {
color: #F6F7FC !important;
font-size: 14px !important;
font-weight: bold;
}

#search-options {
color: #4DA2FA !important;
font-size: 12px !important;
font-weight: bold;
}

#related > H2 {
color: #4DA2FA !important;
font-size: 14px !important;
}

#related > UL > LI > A {
color: #4DA2FA !important;
font-size: 12px !important;
font-weight: bold;
}

.more {
color: #4DA2FA !important;
font-size: 14px !important;
font-weight: bold;
}

#related > UL > LI:last-child > A {
text-decoration: none !important;
}

#related > UL > LI:last-child > A:hover {
text-decoration: underline !important;
}

/*Facebook themes, Tumblr themes, Google themes, Firefox themes,
  Stylish for Firefox, Stylish for Chrome, browse by category,
  The Best, The Newest - More themes*/
#main-article > P:first-child > A:visited,
#main-article > P:nth-child(2) > A:visited,
#main-article > P:nth-child(3) > A:visited,
#front-page-best > UL > LI:last-child > A:visited,
#front-page-newest > UL > LI:last-child > A:visited {
color: #4DA2FA !important;
}

/*Contact the admin. - Связаться с администратором*/
A[rel="nofollow"][href="/contact"] {
color: #A2CCFB !important;
font-weight: bold !important;
font-size: 12px !important;
}

A[rel="nofollow"][href="/contact"]:hover {
color: #4DA2FA !important;
}




/*===================== Userstyles.org - Login  =====================*/

#login > P > A:visited {
color: #4DA2FA !important;
}




/*===================== Page of style - Страница стиля =====================*/

#overlay {
width: 100% !important;
}

/*Author name - Имя автора*/
#miscellaneous-info > TBODY > TR:first-child > TD > A,
#miscellaneous-info > TBODY > TR:first-child > TD > A:visited {
font-weight: bold !important;
color: #4DA2FA !important;
}

/*[Edit] [Delete] - Редактировать Удалить*/
#control-panel > A:visited {
color: #4DA2FA !important;
}

/* :) */
#front-page-best > H2, #front-page-newest > H2, #more-info > H2,
#discussions-area > H2, #discussions-area > H3, #main-article > H2,
#main-article > FORM > TABLE > TBODY > TR:first-child > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(2) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:last-child > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(3) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(4) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(5) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(8) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(10) > TH > LABEL,
#edit-style-options > THEAD > TR > TH:first-child,
#edit-style-options > THEAD > TR > TH:nth-child(2),
#edit-style-options > THEAD > TR > TH:last-child,
#main-article > FORM > TABLE > TBODY > TR:nth-child(5) > TH,
#main-article > FORM > TABLE > TBODY > TR:nth-child(6) > TH,
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TH,
#main-article > FORM > TABLE > TBODY > TR:nth-child(8) > TH,
#main-article > FORM > TABLE > TBODY > TR:nth-child(9) > TH,
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TD > TABLE > THEAD > TR > TH:first-child,
#main-article > FORM > TABLE > TBODY > TR:nth-child(9) > TD > TABLE > THEAD > TR > TH:first-child,
#main-article > FORM > TABLE > TBODY > TR:nth-child(9) > TD > TABLE > THEAD > TR > TH:nth-child(2),
#main-article > FORM > TABLE > TBODY > TR:nth-child(9) > TD > TABLE > THEAD > TR > TH:nth-child(3),
#main-article > FORM > TABLE > TBODY > TR:nth-child(9) > TD > TABLE > THEAD > TR > TH:last-child,
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TD > TABLE > THEAD > TR > TH:nth-child(2),
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TD > TABLE > THEAD > TR > TH:nth-child(3),
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TD > TABLE > THEAD > TR > TH:last-child {
color: #4DA2FA !important;
}

/*Markers in the form of a circle on different pages of the site - 
Маркеры в виде кружка на разных страницах сайта*/
#main-article > UL,#front-page-best > UL, #front-page-newest > UL, #discussions > LI:last-child {
color: #4DA2FA !important;
}

/*Font field "Show code" - Шрифт поля "Показать код"*/
code {
font: 120% Verdana, Helvetica, arial, sans-serif !important;
line-height: 0 !important;
}

/*Background field with the code style - Фон поля с кодом стиля*/
#view-code {
background-color: #DDEEF6 !important;
}

/*Background field around picture - Фон поля вокруг картинки*/
#screenshots {
background-color: #FFFFFF !important;
}

/*Thank yetisquatch for creating this style by donating via PayPal,
Author's Style Table Styled by "your nick name" is licensed... and
"See older discussions", "Ask a new question or add a review for this style.",
See more discussions*/
#donate > P > A,
#license > P > A,
#no-discussions > A,
#start-discussion,
#discussions > LI > A {
color: #4DA2FA !important;
}



/*============== Page style editor - Страница редактора стиля ===========*/

/*The size and font of the text box in the Style Editor - 
  Размер и шрифт текстового поля в редакторе стиля*/
textarea {
min-height: 150px !important;
max-width: 855px !important;
font: 95% Verdana, Helvetica, arial, sans-serif !important;
}

#search-terms{
width: 68% !important;
}

#search-submit {
width: 46px !important;
}

#style_long_description:focus {
min-height: 230px !important;
}
#style_additional_info:focus {
min-height: 230px !important;
}

#css:focus {
min-height: 600px !important;
}

input[name="style[screenshot_url_override]"] {
width: -moz-calc(100% - 22em);
}

.form-controls td > dl > dd > img {
margin: 10px 0 5px !important;
}

.form-controls td > dl > dd > input[type="checkbox"] {
margin: 0 0 6px -21px;
}

#main-article > FORM > TABLE > TBODY > TR:last-child > TD > P > A:first-child {
color: #4DA2FA !important;
}




/*=================== My Account - Моя Учетная запись ===================*/

/*Accounts menu - Меню учётной записи*/
#main-article > UL > LI > A {
color: #4DA2FA !important;
}

/*Contact - Контакт*/
#main-article > DL > DT {
color: #4DA2FA !important;
margin-left: 41px !important;
}

#main-article > DL > DT::after {
content: " with the author:" !important;
color: #4DA2FA !important;
}

#main-article > DL > DD > A {
margin-left: 1px !important;
color: #4DA2FA !important;
}

/*   Table styles in My Account - Таблица стилей в Моей Учетной записи   */

/*Выравнивание текста по центру ячеек счетчиков*/
.numeric-value {
text-align: center !important;
}

/* border width table cell */
#main-article > TABLE > THEAD > TR,
.author-styles td,
.author-styles th {
border: 0 solid #F6F7FC;
height: 28px !important;
}

#main-article > TABLE > THEAD > TR > TH:first-child {
border-top-left-radius: 5px !important;
}

/*The "Style name" in the empty cell  - "Имя стиля" в пустой ячейке*/

#main-article > TABLE > THEAD > TR > TH:first-child::before {
content: "Style Name" !important;
color: #FFFFFF !important;
}

#main-article > TABLE > THEAD > TR > TH:first-child {
background: #4DA2FA !important;
}


/*Heading of the table, cell, style name - Заголовок таблицы, ячейки, название стиля*/

#main-article > TABLE > THEAD > TR > TH:nth-child(2),
#main-article > TABLE > THEAD > TR > TH:nth-child(3),
#main-article > TABLE > THEAD > TR > TH:nth-child(4),
#main-article > TABLE > THEAD > TR > TH:nth-child(5),
#main-article > TABLE > THEAD > TR > TH:last-child {
background: #4DA2FA !important;
color: #FFD8B7 !important;
}

/*=======*/
.author-styles tr > td:first-of-type {
background: #FFD8B7}

/*Background of remote styles - Фон удалённых стилей*/
.author-styles .obsolete {
background-color: #FCE2CB !important;
}

#main-article > TABLE > TBODY > TR:last-child > TD:first-child {
border-bottom-left-radius: 5px !important;
}

#main-article > TABLE > TBODY > TR:last-child > TD:last-child {
border-bottom-right-radius: 5px !important;
}

#main-article > TABLE > THEAD > TR > TH:last-child {
border-top-right-radius: 5px !important;
}

#main-article > TABLE > TBODY > TR:first-child > TD:last-child {
border-top-right-radius: 5px !important;
}

.author-styles tr > td.date-value, .author-styles tr > TD.numeric-value, 
.author-styles tr >  td.image-value, .author-styles tr >  td, 
.author-styles tr >  tr{
background: #FFD8B7 !important;
}


/*Цвет названия стиля и ссылок редактирования и удаления стилей*/
#main-article > TABLE > TBODY > TR > TD > A {
color: #4DA2FA !important;
font-size: 14px !important;
}




/*============================= Categories ==============================*/

#main-article > P:nth-child(4) > A {
color: #4DA2FA !important;
}



/*== Page number of the site and forum - Номер страницы сайта и форума ==*/

/*Номер страницы сайта*/
#main-article > DIV:last-child > A,
.gap {
color: #4DA2FA !important;
font-size: 14px !important;
border: 8px solid #FFD8B7;
font-weight: bold;
background: #FFD8B7 !important;
}

#main-article > DIV:last-child > A:hover {
color: #FFD8B7 !important;
background: #4DA2FA !important;
border: 8px solid #4DA2FA;
text-decoration: none !important;
}

.current {
color: #FFD8B7 !important;
background: #4DA2FA !important;
border: 8px solid #4DA2FA;
font-size: 14px !important;
font-weight: bold;
}

.disabled.prev_page, .disabled.next_page {
display:none
}


/*Number of the page forum - Номер страницы форума*/

#PagerBefore > SPAN:first-child,
#PagerBefore > SPAN:last-child {
display:none
}

#PagerBefore > A,
#PagerBefore > SPAN {
color: #4DA2FA !important;
}

#PagerBefore > A:hover {
background: #4DA2FA !important;
color: #FFD8B7 !important;
text-decoration: none !important;
}

.Highlight {
background: #FFD8B7 !important;
}
}
