@-moz-document url-prefix("http://www.youtube.com"), url-prefix("https://www.youtube.com") {

.site-left-aligned #page, .site-left-aligned #yt-masthead, .site-left-aligned #alerts, .site-left-aligned #ad_creative_1, .site-left-aligned #footer-hh, .site-left-aligned #masthead-subnav, .site-left-aligned #masthead_child_div, .site-left-aligned #masthead-expanded-lists-container, .site-left-aligned #ticker .ytg-wide, .site-left-aligned #baseDiv {margin: 0 auto !important; }

.site-left-aligned.guide-enabled .watch7-playlist, .site-left-aligned.guide-enabled #watch7-video-container, .site-left-aligned.guide-enabled #watch7-main-container {padding-left: 24% !important; padding-right: 24% !important;}

#yt-masthead-container { padding: 0 23% !important; margin: 0 auto !important}
.site-left-aligned { padding: 0 !important;}
}
div#page.search-base {margin: 0 auto !important; width: 1003px !important}
 /* video resize */
#watch7-player, #watch7-video {width:1015px!important;  height:603px!important;  margin-left:auto!important;  margin-right:auto!important;margin-left:0px!important;}
#watch-video-container,#watch7-video-container, #watch-headline-container {background:none!important; background-color:#252525!important;}
