css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);



/* Tab close button */
.tab-close-button > .toolbarbutton-icon {
  -moz-margin-end: 0px !important;
  margin-top: 1px;
}


.tab-close-button.close-icon > .toolbarbutton-icon {
  -moz-image-region: rect(0px, 64px, 16px, 48px) !important;
  -moz-margin-end: 3px !important;
  border: none !important;
  padding: 0px !important;
  list-style-image: url("data:image/png;base64,") !important;
}


.tab-content[selected="true"][visuallyselected="true"]>.tab-close-button.close-icon>.toolbarbutton-icon,
.tab-close-button.close-icon:hover  > .toolbarbutton-icon{
 list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0px, 32px, 16px, 16px) !important;
}

.tab-close-button.close-icon:hover:active,
.tabbrowser-tab[selected="true"] > .tab-close-button.close-icon:hover:active {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0px, 32px, 16px, 16px) !important;
}

.tabbrowser-tab[selected="true"] > .tab-close-button.close-icon {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0px, 16px, 16px, 0px) !important;
  /* Make this button focusable so clicking on it will not focus the tab while
     it's getting closed */
  -moz-user-focus: normal !important;
}

.tab-close-button:focus {
  outline: none !important;
}
