css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('https://mydramalist.info') {

     /*   
	 Theme by http://dear-poop.tumblr.com
     Enjoy~ (✿◠‿◠)
	 */


* {color: #857f73 !important;}
BODY {background-image: url('http://img15.hostingpics.net/pics/152673wooden.png') !important;
background-repeat:repeat !important;
background-position: center top !important;
background-attachment:fixed !important;
}

a:link, a:active, a:visited {color: #471d1f !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}
a:hover {color: #805737 !important; text-decoration:none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}

.name {text-transform: normal !important;}

header {background:#805737 !important;border-bottom: 3px solid white !important;}
section, #content > div {background: #f1f1e5 !important; }

img:hover {opacity:0.6 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}   

.heading td, .grad, #forums td {background:#fff !important;}

/* Menu de gauche */
h4 {border: none !important;background: #c4ae85 !important; color:white !important;}
aside > div > ul > li {background: #fff !important;}
aside > div > ul > li:hover {
background: -moz-linear-gradient(left, #e6d9ad, #ece9e2) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left, #e6d9ad, #ece9e2) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left, #e6d9ad, #ece9e2) repeat scroll 0 0 transparent !important;}
aside > div > ul {border: none !important;}


h3, h2, th {background: #845d3e !important; color:white !important; border: none !important; }


input, select, textarea, button {background: #c6ac79 !important; border: none !important;}


/* Boutons barre du haut */
#realms > li > a > img, .buttons > li > img, .buttons > span > li > a > img {
background: #c6ac79 !important; border: none !important; }

#options {background: #f1f1e5 !important; border: none !important;}

#instant {border: #f1f1e5 !important;}


.spoiler > .content {background: #ddcfaa !important; padding:10px!important;}

#compatibility .bar, .bar {
background: -moz-linear-gradient(left top , #e6d9ad, #ece9e2) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left top , #e6d9ad, #ece9e2) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left top , #e6d9ad, #ece9e2) repeat scroll 0 0 transparent !important;}

.pieLabelBackground, .pieLabel {background: #f1f1e5 !important;}

.synopsis img {box-shadow:none !important;}

.comments > div > div:nth-of-type(odd) {background: #ddcfaa !important;}

/* Flèches pour parcourir dramas sur le profil */
#navigation-buttons span.active, #navigation-buttons span:hover {
background: none !important;}
.navigation > span {background: #fff !important;}
.navigation > span:hover, .navigation > div > div:hover {background: #815936 !important;}

/*couleurs une fois sur deux dans la recherche */
.items > div:nth-child(1), .items > div:nth-child(4), .items > div:nth-child(5), .items > div:nth-child(8), .items > div:nth-child(9) 
{background: none repeat scroll 0 0 #dcceb0 !important;}

/*couleurs une fois sur deux dans la liste dramas */
#list > table > tbody > tr:nth-child(even), #stats tr:nth-child(even)
{background: none repeat scroll 0 0 #dcceb0 !important;}

/*couleurs une fois sur deux dans la liste des groupes */
.episodes tr:nth-child(even), .items tr:nth-child(even)
{background: none repeat scroll 0 0 #dcceb0 !important;}

#listCount {background: #dcceb0 !important;}

.mainbox, #roles > div > div, .roles > div > div {background: #dcceb0 !important;}
.infobox {background: none repeat scroll 0 0 rgba(220, 206, 176, 0.8) !important;}
.mainboxfeat {background: none repeat scroll 0 0 rgba(255, 255, 255, 0.8) !important; }

#rating_stats .aggregate table td:nth-of-type(1), #rating_stats .aggregate table td:nth-of-type(2), #rating_stats > div 
{background: #dcceb0 !important; border: 1px solid white !important;}


 }
