css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('https://mydramalist.info') {

     /*   
	 Theme by http://dear-poop.tumblr.com
     Enjoy~ (✿◠‿◠)
	 */


* {color: #f7e4be !important;}
BODY {background-image: url('http://img15.hostingpics.net/pics/738061Pattern.jpg') !important;
background-repeat:repeat !important;
background-position: center top !important;
background-attachment:fixed !important;
}

a:link, a:active, a:visited {color: #f0b49e !important;}

header {background:#413e4a !important;border-bottom: 2px solid #b38184 !important;}
section, #content > div {background: #b38184 !important; }
#tabs li, #content > div > div {background: #73626e !important; }

/* Menu de gauche */
h4 {border: none !important;background: #73626e !important; color: #f7e4be !important;}
aside > div > ul > li {background: #413e4a !important;}
aside > div > ul > li:hover {
background: -moz-linear-gradient(left, #413e4a, #b38184) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left, #413e4a, #b38184) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left, #413e4a, #b38184) repeat scroll 0 0 transparent !important;}
aside > div > ul {border: none !important;}


h3, h2, th {background: #413e4a !important; color: #f7e4be !important; border: none !important; }


input, select, textarea, button {background: #b38184 !important; border: none !important;
color: #413e4a !important;}


/* Boutons barre du haut */
#realms > li > a > img, .buttons > li > img, .buttons > span > li > a > img {
background: #b38184 !important; border: none !important; }

#options {background: #b38184 !important; border: none !important;}

#instant {border: #b38184 !important;}




#compatibility .bar, .bar {
background: -moz-linear-gradient(left top , #413e4a, #b38184) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left top , #413e4a, #b38184) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left top , #413e4a, #b38184) repeat scroll 0 0 transparent !important;}

.pieLabelBackground, .pieLabel {background: #73626e !important;}

.synopsis img {box-shadow:none !important;}

.comments > div > div:nth-of-type(odd) {background: #b38184 !important;}

/* Flèches pour parcourir dramas sur le profil */
#navigation-buttons span.active, #navigation-buttons span:hover {
background: none !important;}
.navigation > span {background: #fff !important;}
.navigation > span:hover, .navigation > div > div:hover {background: #f0b49e !important;}

/*couleurs une fois sur deux dans la recherche */
.items > div:nth-child(1), .items > div:nth-child(4), .items > div:nth-child(5), .items > div:nth-child(8), .items > div:nth-child(9) 
{background: none repeat scroll 0 0 #413e4a !important;}

/*couleurs une fois sur deux dans la liste dramas */
#list > table > tbody > tr:nth-child(even), #stats tr:nth-child(even)
{background: none repeat scroll 0 0 #413e4a !important;}

/*couleurs une fois sur deux dans la liste des groupes */
.episodes tr:nth-child(even), .items tr:nth-child(even)
{background: none repeat scroll 0 0 #413e4a !important;}

#listCount {color: #413e4a !important;}

.mainbox {background: #73626e !important;}
.infobox {background: none repeat scroll 0 0 rgba(65, 62, 74, 0.8) !important;}
.mainboxfeat {background: none repeat scroll 0 0 rgba(255, 255, 255, 0.8) !important; }
#roles > div > div, .roles > div > div {background: #413e4a !important;}

#rating_stats .aggregate table td:nth-of-type(1), #rating_stats .aggregate table td:nth-of-type(2), #rating_stats > div 
{background: #73626e !important; border: 1px solid #413e4a !important;}


 }
