a, a:hover, a:visited, a:link, .wm-list-message .date,  #search #tabs li.active a {
color: #bDd845;
}

.wm-list-message.narrow .subject{
     color: #CCCCCC;
}


.wm-list-message span.abstract, #gadget-6 .g-cnt p strong.district, .email-feed .gagdet-cont .title .edit .log{
    color: #AB9C4E;
}


#wm-user-name, #wm a, #head #info a, #search #tabs li a, #gadget-5 .g-cnt #times a{
     color: #fff;
}


.email-feed .table .td a{
     color: #DDD;
}


 #wm-top #wm-welcome .welcomeInfoBox p  {
     color: #CCFFFF;
}

#wm a {
     color: #fff;
     text-decoration: none;
     text-shadow: 0px 1px 1px black;
}


#wm-header #wm-login-box a{
        color: #DED;
}

#gadget-22 {
    display: none;
}

#wm .wm-popup a.popup-item {
    color: #FEF;
}

#wm .wm-list-message.unread .name, #wm .wm-list-message.unread  .subject, #wm-message-text blockquote
{
animation-name: white;
animation-duration: 1s;
animation-timing-function: linear;
animation-delay: 0s;
animation-iteration-count: infinite;
animation-direction: alternate;
animation-play-state: running;
    
    -moz-animation-name: white;
    -moz-animation-duration: 1s;
    -moz-animation-timing-function: linear;
    -moz-animation-delay: 0s;
    -moz-animation-iteration-count: infinite;
    -moz-animation-direction: alternate;
    -moz-animation-play-state: running;

    
    -webkit-animation-name: white;
    -webkit-animation-duration: 1s;
    -webkit-animation-timing-function: linear;
    -webkit-animation-delay: 0s;
    -webkit-animation-iteration-count: infinite;
    -webkit-animation-direction: alternate;
    -webkit-animation-play-state: running;

    
    -o-animation-name: white;
    -o-animation-duration: 1s;
    -o-animation-timing-function: linear;
    -o-animation-delay: 0s;
    -o-animation-iteration-count: infinite;
    -o-animation-direction: alternate;
    -o-animation-play-state: running;
}

#wm .wm-list-message .name, #wm .wm-list-message .subject, #wm .wm-list-message .abstract, .email-feed .table .tr.newMes a
{
animation-name: gray;
animation-duration: 1s;
animation-timing-function: linear;
animation-delay: 0s;
animation-iteration-count: infinite;
animation-direction: alternate;
animation-play-state: running;
    
    -moz-animation-name: gray;
    -moz-animation-duration: 1s;
    -moz-animation-timing-function: linear;
    -moz-animation-delay: 0s;
    -moz-animation-iteration-count: infinite;
    -moz-animation-direction: alternate;
    -moz-animation-play-state: running;

    
    -webkit-animation-name: gray;
    -webkit-animation-duration: 1s;
    -webkit-animation-timing-function: linear;
    -webkit-animation-delay: 0s;
    -webkit-animation-iteration-count: infinite;
    -webkit-animation-direction: alternate;
    -webkit-animation-play-state: running;

    
    -o-animation-name: gray;
    -o-animation-duration: 1s;
    -o-animation-timing-function: linear;
    -o-animation-delay: 0s;
    -o-animation-iteration-count: infinite;
    -o-animation-direction: alternate;
    -o-animation-play-state: running;
}

p, a, strong, td, tr, a href, li, ul, h1, h2, h3, h4, span,
.gagdet-cont .title a, .email-feed .links a, .wm-list-message.narrow .name,
.gagdet-cont .title .text  , .email-feed .table .td.etime span{
