css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('https://mydramalist.info') {

     /*   
	 Theme by http://dear-poop.tumblr.com
     Enjoy~ (✿◠‿◠)
	 */


* {color: #fcf9e1  !important;}
BODY {background-image: url('http://img15.hostingpics.net/pics/264347bl17.jpg') !important;
background-repeat:repeat !important;
background-position: center top !important;
background-attachment:fixed !important;
}

a:link, a:active, a:visited {color: #ffdbc4 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}
a:hover {color: #fff !important; text-decoration:none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}

header {background:#635248 !important;border-bottom: 0px solid #b38184 !important;}
section, #content > div {background: #948f72 !important; }
#tabs li, #content > div > div {background: #635248 !important; border: 1px solid #948f72 !important; }


img:hover {opacity:0.6 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}   

.heading td, .grad, #forums td {background:#635248 !important;}



/* Menu de gauche */
h4 { border: none !important;background: #eba294 !important; }
aside > div > ul > li {background: #635248 !important;}
aside > div > ul > li:hover {
background: -moz-linear-gradient(left, #948f72, #fcf9e1) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left, #948f72, #fcf9e1) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left, #948f72, #fcf9e1) repeat scroll 0 0 transparent !important;}
aside > div > ul {border: none !important;}


h3, h2, th {background: #eba294 !important; color: #635248 !important; border: none !important; }


input, select, textarea, button {background: #ffdbc4 !important; border: none !important;
color: #635248 !important; text-shadow: 0px 0px 3px #000 !important;}


/* Boutons barre du haut */
#realms > li > a > img, .buttons > li > img, .buttons > span > li > a > img {
background: #ffdbc4 !important; border: none !important; }

#options {background: #635248 !important; border: none !important;}

#instant {border: #ffdbc4 !important;}


.spoiler > .content {background: #948f72 !important; padding:10px!important;}


#compatibility .bar, .bar {
background: -moz-linear-gradient(left top , #948f72, #eba294) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left top , #948f72, #eba294) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left top , #948f72, #eba294) repeat scroll 0 0 transparent !important;}

.pieLabelBackground, .pieLabel {background: #635248 !important;}

.synopsis img {box-shadow:none !important;}

.comments > div > div:nth-of-type(odd) {background: #948f72 !important;}

/* Flèches pour parcourir dramas sur le profil */
#navigation-buttons span.active, #navigation-buttons span:hover {
background: none !important;}
.navigation > span {background: #fff !important;}
.navigation > span:hover, .navigation > div > div:hover {background: #635248 !important;}

/*couleurs une fois sur deux dans la recherche */
.items > div:nth-child(1), .items > div:nth-child(4), .items > div:nth-child(5), .items > div:nth-child(8), .items > div:nth-child(9) 
{background: none repeat scroll 0 0 #948f72 !important;}

/*couleurs une fois sur deux dans la liste dramas */
#list > table > tbody > tr:nth-child(even), #stats tr:nth-child(even)
{background: none repeat scroll 0 0 #948f72 !important;}

/*couleurs une fois sur deux dans la liste des groupes */
.episodes tr:nth-child(even), .items tr:nth-child(even)
{background: none repeat scroll 0 0 #948f72 !important;}

#listCount {color: #413e4a !important;}

.mainbox {background: #635248 !important;}
.infobox {background: none repeat scroll 0 0 rgba(148, 143, 114, 0.8) !important;}
.mainboxfeat {background: none repeat scroll 0 0 rgba(255, 255, 255, 0.8) !important; }
#roles > div > div, .roles > div > div {background: #948f72 !important;}

#rating_stats .aggregate table td:nth-of-type(1), #rating_stats .aggregate table td:nth-of-type(2), #rating_stats > div 
{background: #948f72 !important; border: 1px solid #fcf9e1 !important;}


 }
