css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);



/*black*/

html,body{background:none rgba(0,0,0,1) !important;}

*{
background-color:rgba(0,0,0,0) !important;
color:rgba(159,159,159,1) !important;
text-shadow:none !important;
border-color:rgba(127,127,127,0.2) !important;
outline-color:rgba(255,0,0,1) !important;
}



/*  ,*[id*=""]   or  ,*[class*=""]   */

input,textarea
{-moz-appearance: none !important;}/* Firefoxでcssを反映させる用 */

input[type="submit"]
,input[type="submit"] *
,textarea
,textarea *
,select *
,option
{background: rgba(0, 0, 0,1) !important;
background: -moz-linear-gradient(rgb(0, 0, 0), rgb(15, 15, 15)) repeat scroll 0% 0% rgba(0, 0, 0,1) !important;
background: -webkit-linear-gradient(rgb(0, 0, 0), rgb(15, 15, 15)) repeat scroll 0% 0% rgba(0, 0, 0,1) !important;
/*margin: 0px !important;padding:0px !important;*/}

div.main-content
{background:none !important;}

*[class*="wrapper"]
,*[class*="Wrap"]
,*[id*="wrap"]
,*[class*="container"]
,*[id*="Area"]
,*[class*="body"]
,header
{background-image:none !important;}

ul[class*="dropdown-menu"]
,*[class^="popover"]
,*[class*="Flyout"]
,div.menu
{background-color:rgba(0,0,0,0.9) !important;}

button
,input[type*="button"]
,input[type*="hidden"]
{background-image:none !important;background-color:rgba(255,255,255,0.7) !important;color:rgba(0,0,0,1) !important;-moz-appearance: none !important;}

*[class*="link"]
{color:rgba(159,159,159,1) !important;}

div[class*="-new"]
{background: rgba(2, 113, 191,1) !important;
background: -moz-linear-gradient(rgb(45, 173, 220), rgb(2, 113, 191)) repeat scroll 0% 0% rgba(2, 113, 191,1) !important;
background: -webkit-linear-gradient(rgb(45, 173, 220), rgb(2, 113, 191)) repeat scroll 0% 0% rgba(2, 113, 191,1) !important;
color:rgba(0,0,0,1) !important;}

div[class*="-new"] *
{color:rgba(0,0,0,1) !important;}

/* nicovideo.jp */

@-moz-document domain("nicovideo.jp") {
#searchFormWrap,#NewServiceList h5,#NewServiceList li,.title_index,.title_light,.title_uad,#enjoy_prev.on,#enjoy_prev,#enjoy_next.on,#enjoy_next,.title_blomaga_rank
{background-image:none !important;}
ul#siteHeaderLeftMenuContainer,ul#siteHeaderRightMenuContainer
{background-color:rgba(0,0,0,0.9) !important;}
div#today_contents_wrap>div#today_foot
{background-image:none !important;}
#playerCommentPanel,
#leftPanel,
#nicommendPanel .contentHeader.default,
#nicommendPanel .contentHeader.assist,
#searchResultExplorerExpand,
.nicommendHeader
{
background: -webkit-linear-gradient(top, #000000 0%, #000000) !important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #000000), color-stop(100%, #000000)) !important;
}
}



/* yomiuri.co.jp */

@-moz-document domain("yomiuri.co.jp") {
div.mainBtm div.defHds,div.article-def div#sbtns,div#mainContentsInr div#electionmenu,#electionmenu #elecnav ul li a
{background-image:none !important;}
}



/* google.co.jp */

@-moz-document domain("google.co.jp") {
table[class="gstl_0 gssb_c"]
,li#ab_ctl_ss>div#ab_ss /* セーフサーチドロップダウン */
,.rg_h, .rg_hp, .rg_hv /* image search */
,div#hdtbSum>div#hdtb_msb>div#hdtb_more_mn /* もっと見るドロップダウン */
,div#top_nav>div#hdtb>div#hdtbSum /* 検索ツールドロップダウン */
,li#ab_ctl_opt>div#ab_options /* オプションドロップダウン */
,div#gbqfqw>div[class="goog-menu goog-menu-vertical"]
,div.subscription-folders-menu
,body>div[class="goog-menu goog-menu-vertical"]
,div#settings-button-menu /* 設定ドロップダウン */
,body>div[class="goog-menu lhn-menu goog-menu lhn-menu-vertical"]
,div#top_nav>div#hdtb>div#hdtbMenus
,div[class="media-strip-image-item-tooltip hover-popup"]
{background-color:rgba(0,0,0,0.9) !important;}
.gbqfba-hvr,.gbqfba-hvr:active,.gbqfba /* Google検索/I'm Feeling Lucky ボタン */
,.ksb, .ab_button /* セーフサーチ ボタン */
{background-image: -moz-linear-gradient(center top , rgb(21, 21, 21), rgb(31, 31, 31)) !important;}
}

/* drive.google.com */

@-moz-document domain("drive.google.com") {
div#gb>div#gbx1
,div[class="goog-menu goog-menu-vertical upload-settings-menu"]
{background-color:rgba(0,0,0,0.9) !important;}
div[class="goog-inline-block goog-toolbar-item-viewpane-sort goog-flat-menu-button"]
,div[class="goog-inline-block goog-toolbar-item-viewpane-sort goog-flat-menu-button"] *
,div[class="goog-inline-block jfk-button jfk-button-action"]
,div[class="goog-inline-block jfk-button jfk-button-action"] *
,div[class="goog-inline-block jfk-button jfk-button-action jfk-button-hover"]
{color:#000000 !important;}
}

/*googlePlay購入ボタン*/

span.buy-button-price
{color:rgba(159,159,159,1) !important;}
div.filter-selector
,div.detroit-settings-menu
,div.detroit-sort-menu
{background-color:rgba(0,0,0,0.9) !important;}

/* plus.google.com */

@-moz-document domain("plus.google.com") {
body.wIa.LP.ad>div.U-L
,body.wIa.LP.ad>div.gf.ir>div.ht.jr>div.pp.gr
,body.wIa.LP.ad>div.a-q.a-q-pd.kw
,body.wIa.LP.ad>div.ORa>div.Hoa.n0b.lla.mDa
,body.wIa.LP.ad>div.ORa>div#content.wB.G8a.Lqa>div.LPb.Zaa>div.g0a.ldb>div.idb
,body.wIa.LP.ad>div#notify-widget-pane.MRa>div.kc.By>div.a-f-e>div.zc
{background-color:rgba(0,0,0,0.9) !important;}
}

/* news.google.co.jp */

@-moz-document domain("news.google.co.jp") {
div[class="media-strip-video-item-tooltip hover-popup"]
,html.news-normal div.goog-menu
{background-color:rgba(0,0,0,0.9) !important;}
a[class="goog-inline-block jfk-button jfk-button-action esc-fullcoverage-button"]
,div[id="view-filter-select"] *
,div[id=":1n"] *
{color:#000 !important;}
}

/* maps.google.co.jp */

@-moz-document domain("maps.google.co.jp") {
div.gmnoprint div.gmnoprint
,div#mv-secondary-container
,div.kd-menulist.unselectable
,div#panelarrow2.arrow-small
{background-color:rgba(0,0,0,0.9) !important;}
}

/* www.youtube.com */

@-moz-document domain("www.youtube.com") {
ul#upload-button-menu
,div[id="branded-page-body"]
,ul[class="yt-uix-button-menu yt-uix-button-menu-feed-item-action-menu"]
,ul[class="yt-uix-button-menu yt-uix-button-menu-default yt-uix-button-menu-external"]
{background-color:rgba(0,0,0,0.9) !important;}
a.yt-uix-button
{background-image:none !important;background-color:rgba(255,255,255,0.1) !important;}
.yt-uix-button-hh-default
, .yt-uix-button-subscription
, .yt-uix-button-panel:hover .yt-uix-button-hh-text
, body .yt-uix-button-hh-default[disabled]
,.yt-uix-button-subscribe-unbranded:hover
, .yt-uix-button-subscribe-unbranded:focus
,.yt-uix-button-subscribe-unbranded
{background-color: rgb(21, 21, 21) !important;background-image: linear-gradient(to bottom, rgb(31, 31, 31) 0px, rgb(21, 21, 21) 100%) !important;}
#guide-container .guide-item.guide-item-selected span
,a.start.end
,button[class=" yt-uix-button yt-uix-button-hh-primary"] span[class="yt-uix-button-content"]
,button[class="yt-uix-close feed-promo-dismissal yt-uix-button yt-uix-button-hh-primary"] span[class="yt-uix-button-content"]
{color:rgba(0,0,0,1) !important;}
}



/* twitter.com header */

@-moz-document domain("twitter.com") {
div.profile-card-inner
,form#global-nav-search>div.dropdown-menu /* プロフィール */
{background-color:rgba(0,0,0,0) !important;}
div.profile-card-inner * /* プロフィール　フォント */
{color:rgba(255,255,255,1) !important;text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5) !important;}
div.follow-card,div.follow-bar
{background-image:none !important;}
.front-signin
,.front-signup /* サインインフォーム */
,div.modal-content
,div.dropdown-inner
{background-color:rgba(0,0,0,0.9) !important;background-image:none !important;}
a.btn
{background-image: linear-gradient(rgb(0, 0, 0), rgb(31, 31, 31)) !important;}
}



/* s.movatwi.jp */

@-moz-document domain("s.movatwi.jp") {
#header .action-list li
{background-image:none !important;}
div.contentbox>div.pop-preview>p.photo-wrap
{background-color:rgba(0,0,0,0.9) !important;}
}



/* facebook.com */

@-moz-document domain("facebook.com") {
.slim #blueBar.fixed_elem
,.fbTimelineStickyHeader .back
,div#uizpwhf1
{background-color:rgba(0,0,0,0.9) !important;}
/* timeline */
.fbTimelineUnit
{background: linear-gradient(to bottom, rgb(0, 0, 0) 0px, rgb(2, 2, 2) 100%) repeat scroll 0% 0% rgb(175, 43, 38) !important;}
.fbTimelineFeedbackActions
{background-color:rgba(11,11,11,0.9) !important;}
div.bottomBorder,div.topBorder,.fbTimelineTopSectionBase .topSectionBottomBorder
{background-image:none !important;}
div.bottomBorder
{border-left: 1px solid rgba(127,127,127,0.2) !important;
border-right: 1px solid rgba(127,127,127,0.2) !important;
border-bottom: 1px solid rgba(127,127,127,0.2) !important;}
div.topBorder
{border-left: 1px solid rgba(127,127,127,0.2) !important;
border-right: 1px solid rgba(127,127,127,0.2) !important;
border-top: 1px solid rgba(127,127,127,0.2) !important;
}
.fbTimelineCapsule .topBorder
{width:409px !important;}
.fbTimelineCapsule .fbTimelineOneColumn .topBorder
{width: 849px !important;}
.fbTimelineCapsule .bottomBorder
{width:409px !important;}
.fbTimelineCapsule .fbTimelineOneColumn .bottomBorder
{width: 849px !important;}
/* profile card */
DIV._k5
{background-color:rgba(0,0,0,0.9) !important;}
}



/* amazonm.co.jp */

@-moz-document domain("amazonm.co.jp") {
div#nav-cross-shop div#welcomeRowTable
,.boxFill .slicedBoxTop/* https://www.amazon.co.jp/gp/yourstore/home?ie=UTF8&ref_=topnav_ys */
{background-image:none !important;}
.nav-submit-button .nav-submit-input
{color:#000000 !important; border-radius:2px !important; background-color:rgba(255,255,255,0.5) !important;}
}



/* userstyles.org */

@-moz-document domain("userstyles.org") {
div.install-status
{color:rgba(0,0,0,0.9) !important;}
}



/* www.ninja.co.jp */

@-moz-document domain("www.ninja.co.jp") {
div#contents>div#primary>div.tool>ul>li
,body>div#container>div#contents>div#contents_head
,body>div#container>div#contents>div#contents_body
,body>div#container>div#contents>div#contents_body>div#secondary
,body>div#container>div#contents>div#contents_body>div#secondary>div#secondary_foot
,body>div#container>div#contents>div#contents_body>div#primary
,body>div#container>div#contents>div#contents_foot
{background-image:none !important;}
}



/* www.hatena.ne.jp */

@-moz-document domain("www.hatena.ne.jp") {
html.windows>body>div#header
{background-image:none !important;}
}



/* calendar.live.com */

@-moz-document domain("calendar.live.com") {
DIV.monthGrid TD.today A.dateCellTitle
{background-color: rgb(255, 213, 141) !important;
color: rgb(68, 68, 68) !important;
border-top: 1px solid rgb(255, 213, 141) !important;}
.monthGrid .backgroundRow TD.today
{border-right: 1px solid rgb(255, 213, 141) !important;
border-top: medium none !important;
border-bottom: 1px solid rgb(255, 213, 141) !important;}
.monthGrid TD.today
{border-right-color: rgb(255, 213, 141) !important;}
.monthGrid .headerRow TD.yesterday
{border-right: 1px solid rgb(255, 213, 141) !important;}
.monthGrid .backgroundRow TD.yesterday
{border-right: 1px solid rgb(255, 213, 141) !important;}
.monthGrid TD.wbt
{border-bottom-color: rgb(255, 213, 141) !important;}
.selectionManager .selectionBar
{background-color: rgb(188, 217, 254) !important;}
}



/* skydrive.live.com */

@-moz-document domain("skydrive.live.com") {
.c-SetItemTile .isSelected .selectionTab {
    border-top: 42px solid rgb(9, 74, 178) !important;
    border-left: 42px solid transparent !important;
}
.c-SetItemTile .isSelected .selectionBorder {
    border: 3px solid rgb(9, 74, 178) !important;
}
.c-ItemTile .tileContent.folder, .c-ItemTile .tileContent.folder .namePlate {
    background: none repeat scroll 0% 0% rgb(9, 74, 178) !important;
}
.c-ItemTile .title, .c-ItemTile .count {
    color: rgb(255, 255, 255) !important;
}
.NotificationBase.ModalDialog .OuterContainer {
    background-color:rgba(0,0,0,0.9) !important;
}
}



/* buzztter.com/ja */

@-moz-document domain("buzztter.com") {
.status,div.notice,div#buzzphrases{background: -webkit-gradient(linear, left top, left bottom, from(#000000), to(#333333)) !important;}
}



/* soundcloud.com */

@-moz-document domain("soundcloud.com") {
DIV#dropdownMenuId.dropdownMenu.g-z-index-header.dropdownMenu__open
,footer[class="about g-dark g-transition-transform sc-selection-disabled sc-browsers-enable-gpu showAbout"]
{background-color:rgba(0,0,0,0.9) !important;}
}



/* twitpic.com */

@-moz-document domain("twitpic.com") {
#navbar-wrap
,#media-overlay
{background-color:rgba(0,0,0,0.9) !important;}
}



/* instagram.com */

@-moz-document domain("instagram.com") {
#home-bg
,.content .tab
,.profile-user
,.user-stats
{background-image:none !important;}
[type="submit"], .button, .button-light
,.button-gray, .avatar-action .button, .avatar-action .requested
,.button-blue, .avatar-action .edit-profile-button, .avatar-action .following-false, .following-false
{background-color:transparent !important;background-image:none !important;}
.photo-feed .photo .bg 
,.more-photos a
{background:none !important;}
section.media-comments,p.media-caption
{background-color:rgba(0,0,0,0.9) !important;}
}

/*-----------------------------------------------------*/



/* image */

img{opacity:1 !important;}
img:hover{z-index:100000 !important;
opacity:1 !important;/*border-radius:0px;*/box-shadow:0px 0px 50px 0px rgba(255,255,255,0.5) !important;}



/* video */

embed,noembed,object,video,div[id *="player"]{z-index:10000 !important;
opacity:1 !important;border-radius:3px;box-shadow:0px 0px 0px 0px rgba(255,255,255,0.5);}
