css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {

body
{
color:red;
background:#E6E6E6;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:#B3B3B3;
}

div.l-header .selection_nipple
{
border-bottom:12px solid #B3B3B3;
}

a
{
color:#0000C5;
}

#posts .post blockquote
{
border-left:solid 4px blue;
}

#posts .post blockquote blockquote
{
border-left:solid 4px #0000E4;
}

#posts .post blockquote blockquote blockquote
{
border-left:solid 4px #0000B3;
}

#posts .post blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000096;
}

#posts .post blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #00006F;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000042;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000042;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000042;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000042;
}

#posts .notification
{
background-color:red;
}

#posts .notification.single_notification
{
border:solid 1px red;
}

#posts .notification a:not(.avatar_frame)
{
color:white;
border-bottom:1px solid #001FFF;
}

#logo a
{
background-image:url('http://i.imgur.com/8Mh3E.png');
}

#posts .post .post_question_nipple
{
width:0px;
height:0px;
border-left:11px solid transparent;
border-right:11px solid transparent;
border-top:11px solid #0014FF;
}

#posts .post .post_question
{
background:#0014FF;
color:#09F;
}

#auto_pagination_loader
{
background:#E6E6E6;
color:red;
}

#auto_pagination_loader a
{
color:red;
}

}
