css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imgur.com") {
    body{
        
        background-color: #E667AF !important;
    }
    .panel{
        background-color: #E6399B !important;
    }
    .textbox{
        background-color: #CD0074 !important;
    }
    .arrows{
        background-color: #CD0074 !important;
    }
    input[type="text"]:focus, input[type="password"]:focus, textarea:focus {
        background-color: #992667 !important;
        border-color: #B9F73E !important;
        box-shadow: 0px 0px 3px 0px #39F73F!important;
    }
    input[type="text"], input[type="password"], textarea, #domain, select {
        background-color: #85004B !important;
        border-color: #86B32D !important;
    }
    .report-icon {
        background-color: #CD0074 !important;
    }
    #social, .icons {
        background-color: #CD0074 !important;
    }
    input.button-medium[type="button"]:hover, input.button-medium[type="submit"]:hover, button.button-medium:hover {
        background: linear-gradient(to bottom, #85004B 0px, #CD0074 0px, #E6399B 100%) repeat scroll 0% 0% transparent !important;
    }
    input.button-medium[type="button"], input.button-medium[type="submit"], button.button-medium {
        background: linear-gradient(to bottom, #E6399B 0px, #CD0074 100%) repeat scroll 0% 0% transparent !important;
        border: 2px solid #9FEE00 !important;
    }
    .carousel-button {
        background-color: #CD0074 !important;
    }
    .thumbs-carousel ul li img:hover, .thumbs-carousel ul li img:focus, .thumbs-carousel ul li img.selected {
        border-color: #9FEE00 !important;
    }
    .thumbs-carousel ul li img {
        border: 3px solid #992667 !important;
    }
    .green {
        color: #9FEE00 !important;
    }
    #comment-sort {
        background-color: #992662 !important;
    }
    a:link, a:active, a:visited, a strong a:hover {
        color: #E667AF !important;
    }
    .stat {
        color: #C9F76F !important;
    }
    #topbar {
        background-color: #E6399B !important;
        border-bottom: 4px solid #CD0074 !important;
    }

}
