css:
/* NORMAL INPUT */
input, textarea, select  {
	-webkit-transition-duration: 0.3s;
	-webkit-transition-timing-function: ease;
	-webkit-transition-property: border-color, box-shadow;
	border: 1px solid #AAA;
	border-radius: 5px;
	padding: 2px;
}
input[type="file"] {
	padding: 3px;
	border: none;
}
input[type="search"] {
	-webkit-appearance: initial;
}
input[type="range"] {
	-webkit-appearance: none;
	padding: 0;
	border: none;
	height: 5px;
	background-color: #BBB;
	opacity: 0.8;
}
input[type="range"]::-webkit-slider-thumb {
	-webkit-appearance: none;
	border-radius: 2px;
	background-color: #555;
	width: 5px;
	height: 16px;
}
input[type="radio"], input[type="checkbox"] {
	-webkit-appearance: initial;
	padding: 0;
	margin: 3px;
	vertical-align: -3px;
	background-color: #FFF;
	background-image: url("data:image/svg+xml;base64,");
	background-size: 100%;
}
input[type="radio"] {
	min-width: 12px;
	min-height: 12px;
	width: 12px;
	height: 12px;
	border-radius: 10px;
}
input[type="checkbox"] {
	min-width: 13px;
	min-height: 13px;
	width: 13px;
	height: 13px;
	border-radius: 2px;
}
select {
	background-color: #EEE;
}
input[type="submit"], input[type="reset"], input[type="button"], button, input[type="file"]::-webkit-file-upload-button {
	-webkit-transition: 0.3s ease border-color;
	background: -webkit-gradient(linear, center top, center bottom, from(#EEE), to(#DDD));
	border: 1px solid #AAA;
	border-radius: 3px;
	padding: 2px 6px;
}
input[type="image"] {
	border: none;
	padding: 3px;
}

/* FOCUS INPUT */
input:focus, button:focus, textarea:focus, select:focus, input[type="file"]:focus::-webkit-file-upload-button {
	outline: none;
	border: 1px solid #05F;
	/*box-shadow: 0px 0px 5px #05F;*/
}
input[type="file"]:focus {
	border: none;
}
input[type="image"]:focus {
	padding: 2px;
}
input[type="range"]:focus {
	height: 3px;
}

/* HOVER INPUT */

/* ACTIVE INPUT */
input[type="submit"]:active, input[type="reset"]:active, input[type="button"]:active, button:active, input[type="file"]:active::-webkit-file-upload-button {
	background: -webkit-gradient(linear, 50% 0%, 50% 100%, from(#DDD), to(#EEE));
}
input[type="range"]:hover, input[type="range"]:active {
	opacity: 1;
}

/* CHECKED INPUT */
input[type="radio"]:checked {
	background-image: url("data:image/svg+xml;base64,");
}
input[type="checkbox"]:checked {
	background-image: url("data:image/svg+xml;base64,");
}
input[type="checkbox"]:checked, input[type="radio"]:checked {
	background-repeat: no-repeat;
	background-size: auto 100%;
	background-position: 50% 50%;
}

/* VALIDATE INPUT */
input:focus:invalid, select:focus:invalid, textarea:focus:invalid {
	box-shadow: 0px 0px 6px red;
}

/* DISABLED INPUT */
input:disabled {
	color: #333;
	background-color: #F0F0F0;
	border-color: #A0A0A0;
}
input[type="checkbox"]:disabled, input[type="radio"]:disabled {
	background-color: #DDD;
}

/* OTHER ATTRIBUTES */
input[readonly] {
	color: #000;
	background-color: #F5F5F5;
	border-color: #A0A0A0;
}
input:focus::-webkit-input-placeholder {
	-webkit-transition: 0.3s ease opacity;
	opacity: 0.8;
}
