css:
@-moz-document domain("youtube.com") { 
body {
  background: #242424;
}

.guide-item {
  color: #EEEEEE !important;
}

#watch7-sidebar .video-list-item .title {
  color: #D1CECE !important;
}

#page-container { 
  margin: auto; 
  width: intrinsic; 
} 

html body.site-left-aligned #page.watch { 
  margin: 0px 0px 0px 45px !important; 
} 

html body.site-left-aligned #page.watch #guide { 
  margin: 0px 0px 0px -210px !important; 
} 

html body.site-left-aligned #page.watch #watch7-video, html body.site-left-aligned #page.watch #watch7-main, html body.site-left-aligned #page.watch .watch7-playlist { 
  margin: 0 auto !important; 
} 

#watch7-main-container, #watch7-video-container, .site-left-aligned.guide-enabled .watch7-playlist, .site-left-aligned.guide-collapsed .watch7-playlist, .site-left-aligned.guide-expanded .watch7-playlist { 
  padding-left: 0px !important; 
}

.guide-context-item .title {
  color: #EEEEEE;
}

.guide-context-item.context-playing .title {
  color: black !important;
}

/* Vevo shit */
.sidebar-expanded #watch7-sidebar {
  background-color: transparent !important;
}

#watch7-branded-banner {
  display: none;
}

#watch7-video-container {
  background: transparent !important; 
}

.watch-branded #watch7-sidebar {
  padding-top: 80px;
}

.watch-branded #watch7-sidebar .watch-sidebar-head, .watch-branded #watch7-sidebar .watch-sidebar-foot {
  color: #EEEEEE !important;
} 

#page.watch #guide-container.branded {
  background: transparent !important;
}
}
