css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
    #yt-hitchhiker-feedback {
        /*destroy the feedback button*/
        display:none;
    }
    #watch7-sidebar {
        /*move the sidebar up*/
        height:1700px;
        margin-top: -635px !important;
        overflow:auto;
        width:100%        
    }
    #watch7-main-container {
        margin-top:60px !important;
        padding-left:30px !important;
    }
    #watch7-main {
        /*increase overall width of page*/
        width: 1620px !important;
    }
    #watch7-content {
        /*increase width of description+comments*/
        width: 1300px !important;
        position:relative !important;
    }
    .comments-post-container {
        /*make the comments box shorter*/
        width: 617px !important;
    }
    
    /*HIDE VIDEO RESPONSES BEGIN*/
    .comments-section-description {
        display:none;
    }
    .comments-section-see-all {
        display:none;
    }
    .video-response {
        display:none;
    }
    /*HIDE VIDEO RESPONSES END*/
    
    /*VIDEO DESCRIPTION BOX CHANGES BEGIN*/
    #watch-description-extra-info {
        /*useless picture+channel link*/
        display:none;
    }
    #watch7-action-panels {
        /*make the div containing the panels fit*/
        width:640px;
    }
    #action-panel-details .action-panel-content {
        /*make the description div fit*/
        width:600px;
    }
    #watch7-action-buttons {
        /*move the description tabs over*/
        width:604px;
    }
    #watch7-headline {
        width:600px;
    }
    #watch7-user-header {
        width:600px;
    }
    .yt-uix-button-panel {
        width:640px;
        /*position:absolute;
        top:0;
        left:0;*/
    }
    
    /*Moving comments*/
    #watch7-discussion {
        position:absolute;
        top:0;
        right:0;
        /*margin-left:641px !important;
        margin-top:-150px !important;*/
    }
    #comments-view {
        height:1177px;
        float:left;
        overflow:auto;
        width:100%;
    }
    
    /*player positioning*/
    #watch7-video {
        padding-top:40px !important;
        padding-left:100px !important;
        padding-bottom:17px !important;
    }
    #watch7-video-container {
        /*get rid of VEVO backgrounds*/
        background:none !important;
    }
}
