css:
@-moz-document domain("teracod.com") {

.row-1, .row-2{
background: white!important;
}

body{
font-family: Arial, Verdana !important;
color: black !important;
}

a, td, tr, font, span{
color: black !important;
}

tr[style="background-color:#303030"], .ui-widget-header{
background: #DBDBDB!important;
}

tr[style="background-color:#202020"], .ui-widget-content, .ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active, div[class="userbox_content"] {
background: #EBEBEB !important;
}

.logo{
display: none !important;
}

#header .nav li a{
background: url(http://eladotraktor.hu//images/apple/menubg.png) repeat-x !important;
color: white !important;
}

#header .nav li a:hover{
background: url(http://eladotraktor.hu//images/apple/menubg_hover.png) repeat-x !important;
color: white !important;
}


.row-2, #header{
height: auto !important;
}

input, option, select, textarea{
background: white !important;
border: 1px solid gray !important;
color: black !important;
}

tr[style="background-color:#6f6f6f;"], .userbox_header, .toolbar{
background: #BFBFBF !important;
color: black !important;
}

tr[style="background-color: black"]{
background: yellow !important;
}

.toolbar a{
background: white !important;
}

a:hover, h3{
color: green !important;
}

#content, #footer .bdt{
border: none !important;
}

#search-form, .submit{
background: white !important;
color: black !important;
}

span[class="notification_top"]{
display: none !important;
}

.userbox_content, .userbox_content h2.userbox_header{
border: 1px solid #B3B3B3 !important;
}

.userbox_wrapper{
border-color: white !important;
}

#footer{
background: #C9C9C9 !important;
}

}
