css:
@-moz-document url-prefix(http://www.google.com/search?),
  url-prefix(http://www.google.com/webhp?),
  url-prefix(http://www.google.com/#sclient),
  url-prefix(http://www.google.co.jp/search?),
  url-prefix(http://www.google.co.jp/webhp?),
  url-prefix(http://www.google.co.jp/#hl) {
/* please change url-prefix for your country */

/* ----- fixed header ----- */

/* top line */
#mngb {
  position: fixed !important;
  width: 100% !important;
  top: 0 !important;
  left: 0 !important;
}

/* logo and form */
#searchform {
  position: fixed !important;
}

.ksfccl {
  position: relative !important;
}
  #sflas {
    position: fixed !important;
    margin-left: -80px !important;
    margin-top: 30px !important;
  }

#subform_ctrl > div {
  position: fixed !important;
  margin-top: 30px !important;
  width: 45% !important;
  text-align: right;
}
  #sbfrm_l, #resultStats {
    text-align: left !important;;
  }

/* left sidebar */
#leftnavc {
  position: fixed !important;
  top: 116px !important;
}

/* body */
#center_col {
  margin-top: 60px !important;
}

/* ----- generate counter ----- */
body {
  counter-reset: head;
}

h3.r:before {
  counter-increment: head;
  content: "#" counter(head);

  margin-right: 5px;
  padding: 0px 4px;
  color: #666;
  border: 1px solid #666;
  -moz-border-radius: 4px;
  text-decoration: none !important;
}

/* ----- for jAutoPagerize ----- */
#autopagerize_icon + div { 
  z-index: 9999 !important;
}

}
