css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://bitfinex.com")
{
html, body {
    background-color: #c2dcf3 !important;
    }
}
h1, h2, h3 {
    margin: 1px 0 !important;
}
h1 {
    display: block;
    font-size: 50% !important;
    font-weight: bold;
}
.tagline h3 {
    color: #555555;
    font-family: museo-slab,serif;
    font-size: 10px !important;
    font-weight: 300;
    line-height: 15px;
    margin-top: 10px;
    text-shadow: 1px 1px 2px #FFFFFF;
}
.container-center {
    background-color: #85abce  !important;
    border-radius: 5px 5px 5px 5px !important;
    min-width: 970px;
    overflow: hidden;
    padding: 1px 2px !important;;
  
}



.row > .content {
    background-color: #d2e6f8 !important;
    border-radius: 5px 5px 5px 5px !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.5) inset, 0 2px 2px 0 rgba(255, 255, 255, 0.5);
    float: left;
    min-width: 740px;
    padding: 2px !important;
    width: 70%;
}
.row > .content, .row > .sidebar {
    min-height: 500px;
}


form.order .quantity .input-prepend .add-on {
    font-size: 60% !important;
    height: 10px !important;
    padding: 10px !important;
}
form.order .quantity input#amount {
    width: 50px !important;
}
form.order .quantity input {
    font-size: 60% !important;
    font-weight: bold;
    height: 60% !important;
}
h3 {
    display: block;
    font-size: 50% !important;
    font-weight: bold;
}
.row > .sidebar {
    float: left !important;
    }
.row > .content, .row > .sidebar {
    min-height: 500px;
}
.sidebar {
    border-left: 1px solid #EEEEEE;
    margin-left: 0;
    padding-left: 1px !important;
}
