css:
@-moz-document regexp("http://(www\\.)?youtube\\.com/watch\\?.*v=([a-zA-Z0-9]+).*") {
#watch7-container #player {
  background: #1b1b1b !important;
  padding-left: 0 !important;
}

#watch7-container #player-api {
  width: 945px !important;
  height: 557px !important;
  margin: 0 auto !important;
  background: #1b1b1b !important;
}

#watch7-container #page.watch #guide {
  visibility: hidden;
}

#watch7-container .watch7-playlist {
  padding-left: 0 !important;
  margin: 0 auto !important;
}

#watch7-container .watch7-playlist-bar {
  width: 945px !important;
  margin: 0 auto !important;
}


#watch7-container #watch7-main-container {
  padding-left: 0 !important;
}

#watch7-container #watch7-main {
  margin: 0 auto !important;
  width: 945px !important;
}

#yt-masthead-content {
  margin: 0 auto !important;
  max-width: 945px;
  padding-right: 50px;
}

#guide span, #guide li a {
  color: #FFF;
}

#guide #watch-context-item-list li a:hover, #guide #watch-context-item-list li.context-playing a  {
  background: #444;
  color: #fff;
  background: -moz-linear-gradient(top,#444 0,#333 100%);
  background: -ms-linear-gradient(top,#444 0,#333 100%);
  background: -o-linear-gradient(top,#444 0,#333 100%);
  background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#444),color-stop(100%,#333));
  background-image: -webkit-linear-gradient(top,#444 0,#333 100%);
  background-image: linear-gradient(to bottom,#444 0,#333 100%);
}

#guide #watch-context-container {
  background: #1b1b1b !important;
}

#guide ::-webkit-scrollbar-thumb, #guide ::-webkit-scrollbar, #guide ::-webkit-scrollbar-track {
  border: none !important;
}
}
