css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dragon-soldier.net") {
/* General background settings */
body {
background: #000000 !important;
background-image: none !important;
background-repeat: no-repeat !important;
background-position: 0px 10px !important;
}

/* defaults for all links */
a:link {color: #0066FF !important;}
a:visited {color: #0099FF !important;}
a:hover {color: #00FFFF !important;}
a:active {color: #FF0000 !important;}

/* Chat, Mail, Forum text area color settings */
input, textarea {background-color:#333333 !important; 
color: #0000BB !important; }
#content, #chatmsg, #chatuserlist {background-color:#333333 !important;}
#chatwindow {background-color:#333333 !important;
border-color: #003377 !important;}


/* General tag color settings */
size, span, center, color, div, li, tr, u, i { color: #0000FF !important;}
b, font { color: #00BBFF !important;}
blockquote { color: #000000 !important;}
th, td { color: #0000FF !important;
background-color: transparent !important; 
border-color: #003377 !important;}
table {color: #0000FF !important;
border-color: #003377 !important; }
}
