css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lwn.net") {

  body {
    font-family: source sans pro, helvetica, arial, sans-serif;
  }

  .Page {
    max-width: 60em;
    margin: 0 auto;
  }

  .LeftColumn {
    background: none !important;
    border-right: 1px solid #ccc;
    font-size: 9pt;
    padding: 1ex;
    line-height: 1.5;
  }

  h1 {
    margin: 2em 0 0 !important;
  }

  a:link {
    color: #37e !important;
  }

  a:visited {
    color: #739 !important;
  }

  a:hover {
    text-decoration: underline !important;
  }

  .TopNavigation {
    border-bottom: 1px solid #eee;
  }

  .NavLink {
    background: none !important;
  }

  .Current, .Current:visited {
    background-color: #78e !important;
    padding: 0 1ex;
    margin: 0 -1ex;
    color: white !important;
  }

  .SideBox .Current, .SideBox .Other {
    display: block;
  }

  .SideBox .Current + br, .SideBox .Other + br {
    display: none;
  }

  .ArticleText {
    line-height: 1.6;
    margin: 3em 4em !important;
    font-size: 12pt;
  }

  .ArticleText p {
    margin: 0;
  }
  .ArticleText p + p {
    margin-top: 1.5em;
  }

  .ArticleText h2 {
    font-size: 22pt !important;
    text-decoration: none !important;
    text-align: center;
    margin: 3em 0 0 !important;
  }

  .ArticleText h4 {
    font-size: 18pt !important;
    margin: 2em 0 0.5em;
  }

  .FeatureByline {
    border: none !important;
    background: none !important;
    padding: 0 !important;
    float: none !important;
    text-align: center;
    margin: 0 auto 2em !important;
    color: #999;
  }
  .FeatureByline br {
    display: none;
  }

  .FeatureByline b {
    margin-right: 1ex;
  }

  .FeatureByline b:after {
    content: "â€”";
    font-weight: normal;
    margin-left: 1ex;
  }

  .BigQuote {
    border-left: 1ex solid #eee;
    margin: 1em;
    padding: 1ex 1em;
    color: #555 !important;
  }

  .ArticleText span {
    background-color: inherit !important;
    color: inherit !important;
    font-style: italic !important;
  }


  /* Comments */

  .MidColumn > .CommentBox {
    margin: 1em 0 0 2em;
  }

  .CommentBox {
    border: none !important;
    margin: 0 0 0 4em;
  }

  .CommentBox {
  }

  .CommentBox P.CommentTitle {
    background: none !important;
    font-weight: 600;
    font-size: 1.1em;
  }

  /* hide titles for child comments */
  .Comment P.CommentTitle {
    display: none;
    padding: 0;
  }

  /* Turn off all useless comment titles (comment out the following one line if DO you want the first one...) */
  DIV.CommentBox P.CommentTitle {
    display: none;
  }

  P.CommentPoster {
    opacity: 0.5;
  }

  .CommentPoster {
    visibility: hidden;
  }

  .CommentPoster b,
  .CommentPoster a {
    float: left;
    visibility: visible;
  }

  .CommentPoster a {
    padding: 0 12px;
  }

  .CommentReplyButton input {
    border-width: 0px !important;
    background: white !important;
    padding: 0 !important;
    color: #37e;
    cursor: pointer;
    margin: -1em 0 0;
    /* gross hack to truncate to reply */
    /*
    width: 6ex;
    */
  }

  .CommentReplyButton input:hover {
    text-decoration: underline;
  }

}
