css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
	/* Core changes */
	body {
		background: url("http://themebin.files.wordpress.com/2010/03/galaxy.jpg") no-repeat center center fixed !important; 
		-webkit-background-size: cover !important;
		-o-background-size: cover !important;
		background-size: cover !important;
		color: #DDD !important;
		/* Remove any faggotry from Moot --- Does inline CSS have more power than outline CSS? Guess we'll have to find out one day. */
		transform: none !important;
		-ms-transform: none !important;
		-moz-transform: none !important;
		-webkit-transform: none !important;
		-o-transform: none !important;
	}
	/* End core changes */

	/* Hide things we don't need */
	img[title="Toggle announcement"], #globalMessage, div[style="text-align: center; margin-top: 10px;"], #boardNavDesktop, .absBotText, .thread-stats, .stylechanger, .navLinks, tr.rules, .passNotice, hr, .middlead, .boardBanner, .globalMessage, .postingMode, #boardNavDesktopFoot { display: none !important; }

	/* Font colors */
	.globalMessage, .absBotText, #boardNavDesktop { color: #EEE !important; }  
	.summary { color: #DDD !important; }
	a, a.quotelink, a.replylink, .useremail .name, .useremail .postertrip { color: #E01B6A !important; }
	/* End font colors */

	/* Replies -- Posting mode: Thread */
	.post.reply {
		background: none !important;
		background-color: rgba(10, 10, 10, .8) !important;
		border-radius: 4px;
		border-color: rgba(10, 10, 10, .1) !important;
	}
	.reply.highlight {
		background-color: rgba(5, 5, 5, .9) !important;
		-webkit-box-shadow: 0px 0px 30px rgba(255, 154, 159, 1) !important;
		box-shadow: 0px 0px 30px rgba(255, 154, 159, 1) !important;
		z-index: 600 !important;
	}

	.linkfade { font-size: 110% !important; color: red !important; opacity: 1 !important; }
	/* End replies */

	/* Post form */
	table#postForm { background-color: rgba(10,10,10,.8) !important; color: #DDD !important; padding: 15px !important; border-radius: 5px; }
	table.postForm > tbody > tr > td:first-child {
		background: none !important;
		border: none !important;
		color: #DDD !important;
	}
	/* End post form */

	/* Navigaytion -- We need this though, this is actually NOT the board listing. */
	.navLinks.navLinksBot {
		display: block !important;
		right: 0px !important;
		bottom: 0px !important;
		position: fixed !important;
	}
	/* Should we fix this on the page like the 4chan Galaxy board listing? */
	.pagelist { background: none !important; border: none !important; color: #DDD !important; background-color: rgba(10, 10, 10, .9) !important; border-radius: 4px; display: block !important; }
	/* End navigation */

	/* Native extension CSS */
	.extPanel {
		background: rgba(10, 10, 10, .5) !important;
		-webkit-box-shadow: 0px 0px 21px rgba(0, 0, 0, 1);
		box-shadow:         0px 0px 21px rgba(0, 0, 0, 1);
		border: solid 1px rgba(10, 10, 10, .01) !important;
		border-radius: 4px !important;
		padding: 5px !important;
	}

	#settingsMenu .extPanel { background: rgba(5, 5, 5, .9) !important; }

	.drag.postblock {
		background: none !important;
		border: none !important;
		color: #DDD !important;
	}

	.post.reply.preview, .post.preview {
		background-color: rgba(5, 5, 5, .9) !important;
		-webkit-box-shadow: 0px 0px 21px rgba(0, 0, 0, 1);
		box-shadow:         0px 0px 21px rgba(0, 0, 0, 1);
		border: solid 1px rgba(10, 10, 10, .01) !important;
	}

	.newPostsMarker:not(#quote-preview) {
		-webkit-box-shadow: 0px 0px 30px rgba(255, 154, 159, 1) !important;
		box-shadow: 0px 0px 30px rgba(255, 154, 159, 1) !important;
		z-index: 600 !important;
	}
	/* End native extension CSS */

	/* Change buttons */
	a img[alt="G"] {
		background-image: url(data:image/png;base64,) !important;
		width: 14px !important;
		display: inline-block !important;
		padding: 14px 0 0 0 !important;
		height: 0px !important;
		height: 14px;
		margin-right: 4px !important;
	}

	a img[alt="I"] {
		background-image: url(data:image/png;base64,) !important;
		width: 14px !important;
		display: inline-block !important;
		padding: 14px 0 0 0 !important;
		height: 0px !important;
		height: 14px;
	}

	img[data-cmd="report"] {
		background-image: url(data:image/png;base64,) !important;
		width: 14px !important;
		display: inline-block !important;
		padding: 14px 0 0 0 !important;
		height: 0px !important;
		height: 14px;
	}

	img[data-cmd="hide-r"], img[data-cmd="hide"] {
		background-image: url(data:image/png;base64,) !important;
		background-repeat: no-repeat;
		width: 14px !important;
		display: inline-block !important;
		padding: 14px 0 0 0 !important;
		height: 0px !important;
		height: 14px;
	}
	/* End buttons */

	/* 4chan Galaxy extension CSS */
	.fourchangalaxynav {
		position: fixed !important;
		top: 0px !important;
		right: 0px !important; 
	}

	.fourChanGalaxyImageHighlight {
		background-color: rgba(200, 100, 100, 0.9) !important;
		padding: 2px !important;
		border-radius: 4px !important;
	}
	/* End 4chan Galaxy extension CSS */
}

@-moz-document regexp(".*boards.4chan.org/.*/res/.*") {
	/* Core changes while posting mode = reply -- Important !! */
	html, body { margin: 0px !important; padding: 0px !important; }
	body { margin-left: 25% !important; text-align: left !important; }
	/* End core changes */

	/* Delete form - Should this be fixed on the screen? */
	.deleteform {
		bottom: 0px !important;
		left: 0px !important;
		z-index: 500 !important;
		position: fixed !important;
	}
	/* End delete form */

	/* Original post[er] (OP) */
	.post.op .fileText { font-size: 71% !important; display: block !important; }
	.post.op .dateTime:before { content: "Posted: " !important; }
	.post.op .dateTime { display: block !important; }
	div.post.op div.file .fileThumb img { width: 64px !important; height: 64px !important; }
	.postContainer.opContainer {
		width: 25% !important;
		height: 100% !important;
		left: 0px !important;
		top: 0px !important;
		position: fixed !important;
		background: rgba(10, 10, 10, .5) !important;
		-webkit-box-shadow: 5px 0px 8px rgba(10, 10, 10, 0.75);
		box-shadow:         5px 0px 8px rgba(10, 10, 10, 0.75);
		border-right: solid 1px rgba(10, 10, 10, .5) !important;
	}
	.post.op .postMessage {
	   max-height: 80% !important;
	   width: 70% !important;
	   overflow-y: scroll;
	}
	/* End original post[er] (OP) */

	/* Replies - Posting mode: reply */
	.post.reply.preview, .post.preview {
		width: auto !important;
		background-color: rgba(5, 5, 5, .9) !important;
		-webkit-box-shadow: 0px 0px 21px rgba(0, 0, 0, 1);
		box-shadow: 0px 0px 21px rgba(0, 0, 0, 1);
		border: solid 1px rgba(10, 10, 10, .01) !important;
	}
	.post.reply { width: 90% !important; }
	/* End replies */

	/* Post form - Posting mode: reply */
	table#postForm {
		margin: 0 auto !important;
		top: 0px !important;
		margin-bottom: 20px !important;
		border-radius: 0px !important;
		-webkit-border-bottom-right-radius: 8px !important;
		border-bottom-right-radius: 8px !important;
		-webkit-border-bottom-left-radius: 8px !important;
		border-bottom-left-radius: 8px !important;
	}
	/* End post form */

	/* Turns out we don't need this. 4chan Galaxy extension adds better navigation. - Keep anyway. */
	#boardNavDesktop {
		width: 75% !important;
		margin-left: 26% !important;
	}
	/* End navigaytion */

	/* Entire thread. Important. */
	.thread {
		max-width: 96% !important;
		margin: 0 auto !important;
	}
	/* End thread */
}
