css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("basso.fi") {

/*
Basso Clean

2012©villek (aturturin pohja)
*/

/* Basso Quick Fix by aturtur */
#topbanner, div.framebanners,div.framebanners_post, div.framebanners_messageindex {
    display: none !important;
}
.wrapper, .bassowrapper {
    margin-left: auto !important;
    margin-right: auto !important;

}

/*
.catbg,table,div.windowbg,div.windowbg2 {
    width: 1000px !important;
}
*/

#boardindex_table,#messageindex,#forumposts {
    min-width: 1000px !important;
}

/*Runko*/
body {
    background: url('http://koti.mbnet.fi/rautio/bgs/bg_body.jpg') !important;
    font-family: Verdana !important;
    color: #444 !important;
}

/*Haku*/
.search_box {
    position: relative !important;
    top: -15px !important;
    left: 100px !important;
}
/*Header*/
#bassoheader {
    border: 0px !important;
    background: #444 !important;
    margin: 0px !important;
    padding: 0px !important;
    height: 117px !important;
}
.bassowrapper {
    margin-left: auto !important;
    margin-right: auto !important;
    width: 1000px !important;
}
#logo {
    border: 0px !important;
    background: #444 !important;
    display: block;
    padding-right: 20px !important;
    padding-top: 32px !important;
}
#logo img {
    width: 150px;
}
div#bhtop .bhtop-col {
    border: 0px !important;
    padding-left: 10px !important;
    padding-top: 20px !important;
    padding-bottom: 0px !important;
}
div#bhtop .bhtop-col-2 {
    border: 0px !important;
    padding-bottom: 0px !important;
}
#bhtop {
    border: 0px !important;
    background: #444 !important;
    margin: 0px !important;
    padding: 0px !important;
    font-family: verdana !important;
    height: 55px !important;
}
#bhbottom {
    border: 0px !important;
    background: #444 !important;
    margin: 0px !important;
    padding: 0px !important;
    border-top: 1px dashed #aaa !important;
    width: 770px !important;
}
#bhbottom ul.reset {
    padding: 0px !important;
}
#bhbottom a:link{
    font-size: 11px !important;
}

/*Sisältö*/
.wrapper {
    margin-left: auto !important;
    margin-right: auto !important;
}
/*Postaukset*/
#topic_header {
    background: #444 !important;
    width: 960px !important;
}
.poster a:link{
    color: #444 !important;
}
.stars {
    display: none !important;
}
.windowbg, .windowbg2 {
    background: #fff !important;
    border: 0px solid #aaa !important;
}
.quote_button a:link, .modify_button a:link, .remove_button a:link, .split_button a:link{
    color: #111 !important;
}
.quote_button a:visited, .modify_button a:visited, .remove_button a:visited, .split_button a:visited{
    color: #111 !important;
}
.quote_button a:hover, .modify_button a:hover, .remove_button a:hover, .split_button a:hover{
    color: #00ced3 !important;
}
.poster {
    border-right: 1px dashed #aaa !important;
    margin-right: 15px !important;
    width: 150px !important;
}
.inner {
    padding-left: 0px !important;
    width: auto !important;
}
.post {
    padding-left: 3px !important;
    margin-left: 0px !important;
    width: auto !important;
}
.moderatorbar {
    padding-left: 10px !important;
}
/*Quote*/
.quoteheader {
    color: #444 !important;
}
.bbc_standard_quote {
    background: #f5feff !important;
    border: 1px solid #a2d5da !important;
}
.bbc_alternate_quote {
    background: #f5fff6 !important;
    border: 1px solid #a2dab7 !important;
}
/*Foorumi*/
.info a:link{
    color: #444 !important;
}
.lastpost a:link {
    color: #111 !important;
}
#board_11_children a:link {
    color: #111 !important;
}
#board_9_children a:link {
    color: #111 !important;
}
#board_4_children a:link {
    color: #111 !important;
}
#board_2_children a:link {
    color: #111 !important;
}
/*Muuta*/
.description {
    background: #fff !important;
    border: 0px solid #aaa !important;
}
.catbg th, .first_th, .lefttext, .lefttext last_th {
    background: #444 !important;
    color: #fff !important;
}
.catbg th a:link, .first_th a:link, .lefttext a:link, .lefttext last_th a:link {
    color: #fff !important;
}
.catbg th a:hover, .first_th a:hover, .lefttext a:hover, .lefttext last_th a:hover {
    color: #00ced3 !important;
}
#board_2_children, #board_4_children, #board_11_children, #board_9_children, #board_31_children {
    border-top: 1px solid #aaa !important;
}
/*Kategoriat*/
.topic_table td.stickybg2, td.icon1, td.icon2, .topic_table td.subject p, .topic_table td.stats   {
    background: #fff !important;
    border: 1px !important;
}
/*Info*/
#board_19, #board_3, #board_24, #board_15, #board_21, #board_34, #board_33, #board_20, #board_32, #board_25, #board_30, #board_18, #board_23, #board_17, #board_12 {
    border-bottom: 1px dashed #aaa !important;
}
/*Shoutbox*/
#sb-sidebar {
    background: #ccc !important;
    Border: 0px !important;
}
.sb-msg {
    background: #fff !important;
    color: #444 !important;
    border-bottom: 1px dashed #000 !important;
    border-top: 0px !important; 
}
#sb-msg-holder {
    border: 0px !important;
}
.timestamp {
    color: #444 !important;
}
#sb-msg-holder a {
    color: #444 !important;
}
#sb-msg-holder a:hover {
    color: #00ced3 !important;
}
/*Radio*/
#radioplayer {
    background: #444 !important;
}
/*Mainokset veke*/
#topbanner {
    display: none !important;
}
#footer_section .reset {
    display: none !important;
    height: 250px !important;
}
#footer_section div {
    display: block !important;
    height: 50px !important;
}
.pagesection a:hover{
    color: #00ced3 !important;    
}
/*Etusivu*/
.toimitus {
    background: #fff !important;
    border: 1px solid #aaa !important;
    padding: 5px !important;
}
.box {
    background: #fff !important;
    border: 1px solid #aaa !important;
    padding: 5px !important;
}
/*Muut sivut*/
.bassowrapper, .bassocontent, .bassosidebar, .bassosidebar h2 {
    background: #fff !important;
}

/*Tiivistys*/
table.table_list tbody.divider {
	height: 0px !important;
	padding: 0px !important;
}
.table_list tbody.content td.info a.subject {
	line-height: 16px !important;
	height: 17px !important;
}
h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg, h3.titlebg
{
	height: 12px !important;
	font-size: 19px !important;
	padding-bottom: 10px !important;
}
#forumposts h3.catbg {
    padding-top: 15px !important;
    padding-bottom: 15px !important;
}
/*Social + jako -napit*/
#icon-social {
    display: none !important;
}
.addthis_toolbox {
    display: none !important;
}
/*Profiili*/
.navigate_section {
    margin: 0px !important;
}
.navigate_section li{
    padding: 3px !important;
    color: #444 !important;
    margin: 0px !important
}
.navigate_section li a{
    color: #444 !important;
}
.navigate_section li a:hover{
    color: #00ced3 !important;
}
#dropdown_menu_1 {
    border-bottom: 1px solid #aaa !important;
    background: #fff !important;
    padding: 2px !important;
    margin: 0px !important
}
}
