css:
@-moz-document url-prefix("http://cheese.formice.com/forum") {
body {
font: .8em "Arial", sans-serif !important;
background-color: #1e1815 !important;
background-repeat: no-repeat !important;
background-position: top !important;
background-image: url("http://www.transformice.com/wp-content/themes/Themice/images/fond.jpg") !important;
}

a {
color: #6a7495 !important;
}

.footer .pageContent {
background: #738343 !important;
color: #fff !important;
}

.footer .pageContent a {
color: #fff !important;
}

.navTabs {
background-color: #738343 !important;
border: 1px solid #4c5a20 !important;
border-bottom: 0 !important;
}

.navTabs .navTab.PopupClosed:hover{background-color:#9ba96f !important }

.navTab a {
color: #fff !important;
}

.navTabs .navTab.selected .navLink {
background: #2a1a12 !important;
border: 0 !important;
text-shadow: 0 0 0 transparent, 0px 0px 0px #F0F7FC;
}

.tabLinks {
background-color: #2a1a12 !important;
background-image: none !important;
top: 26px !important;
height: 26px !important;
}


.titleBar h1 {
font-weight: bold !important;
letter-spacing: -1px !important;
}

li.navTab a {
font-weight: bold !important;
}

.navTabs .navTab.selected .tabLinks a {
text-shadow: 0 0 0 !important;
}

.navTabs .navTab.selected .tabLinks a:hover,
.navTabs .navTab.selected .tabLinks a:focus{
background: #4e3428 !important;
background-image: none !important;
-moz-border-radius: 0 !important;
-webkit-border-radius: 0 !important;
text-shadow: 0 0 0 !important;
border: 0 !important;
padding-top: 2px !important;
padding-bottom: 2px !important;
}

.breadcrumb {
background: #6a7495 !important;
border: 1px solid #4e597e !important;
}

.breadcrumb .crust a.crumb {
background: #6a7495 !important;
}

.breadcrumb .crust a.crumb:hover {
text-decoration: underline !important;
}

.breadcrumb .crust .arrow {
border-left-color: #4e597e !important;
}

.breadcrumb .crust .arrow span {
border-left-color: #6a7495 !important;
}

.breadcrumb .crust a {
color: #fff !important;
}

header, #header, #headerMover, #headerProxy {
background: transparent !important;
}

.sectionMain {
border: 1px dotted #6a7495 !important;
}

h3.nodeTitle, h3.title, a.username {
font-size: 1em !important;
}

.avatarList a.username {
font-weight: bold !important;
font-size: 1.2em !important;
}

.avatarList li {
border-bottom: 1px dotted #ccc !important;
padding-bottom: 5px !important;
margin-bottom: 5px !important;
}

.staffOnline h3, .membersOnline h3 a, .statsList h3, .sharePage h3 , .todayBirthday h3 a{
color: #000 !important;
}

a.avatar img { 
border: 1px solid #ccc !important;
}

.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
border-bottom: 1px dotted #6a7495 !important;
}

.node .nodeLastPost {
background: #f8f8f8 !important;
border: 1px solid #ccc !important;
}

.nodeList .categoryStrip {
background: #6a7495 !important;
background-image: none !important;
border-bottom-color: #434d6f !important;
border-top: 0 !important;
}

.discussionList .sectionHeaders a:hover {
background: #858eac !important;
}

.discussionList .sectionHeaders {
border-top: 0 !important;
}

.categoryText h3 {
font-weight: bold !important;

}

.categoryText h3 a {
color: #fff !important;
}

.PageNav a, .itemPageNav a  {
background: #f8f8f8 !important;
border: 1px dotted #ccc !important;
}

.PageNav a:hover, .itemPageNav a:hover {
background: #e5e5e5 !important;
border: 1px solid #ccc !important;
}

div.secondaryContent { 
background: #f8f8f8 !important;
border: 1px dotted #ccc !important;
}

.discussionList .sectionHeaders {
background: #626ca8 !important;
border-bottom: 1px solid #4e597e !important;
color: #fff !important;
}

.discussionList .sectionHeaders a { 
color: #fff !important;
text-transform: uppercase !important;
}

.profilePage .primaryUserBlock {
border: 0 !important;
}

.profilePage .primaryUserBlock .lastActivity {
border-top: 1px dotted #ccc !important;
}

.profilePage .tabs.mainTabs {
background: #f8f8f8 !important;
}

.tabs { border-bottom: 1px solid #ccc !important; }

.profilePage .mast { border-right: 1px solid #ccc !important; }

li.secondaryContent, dl.secondaryContent { 
background: #f8f8f8 !important;
border-bottom: 1px dotted #ccc !important;
}

.messageSimple .comment, .messageSimple .secondaryContent {
padding: 10px 5px !important;
}

.primaryContent { border-bottom: 1px dotted #bbb !important; }

.profilePage .mast .section.infoBlock {
border: 1px solid #ccc !important;
}

.participants h3, .secondaryContent h3 { color: #000 !important; }

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
border-bottom: 1px solid #ccc !important;
}

h3.textHeading { border-bottom: 1px solid #ccc !important; }

.discussionListItem {
background: #f8f8f8 !important;
border-bottom: 1px dotted #ccc !important;
}

.discussionListItem .posterAvatar, .discussionListItem .stats {
background: #f8f8f8 !important;
border: 0 !important;
}

.discussionListItem .stats dl {
border-left: 1px dotted #ccc !important;
border-right: 1px dotted #ccc !important;
}

.sticky {
border-bottom: 1px solid #F6D8CE !important;
}

.sticky, .sticky .posterAvatar, .sticky .stats {
background: #FBF5EF !important;
}

.sticky a {
color: #F78181 !important;
}

.sticky .stats dl {
border-left: 1px dotted #F6D8CE !important;
border-right: 1px dotted #F6D8CE !important;
}

.messageUserBlock {
background: #f8f8f8 !important;
border: 1px solid #ccc !important;
}

.messageUserBlock .arrow {
border-left-color: #ccc !important;
}

.messageList .message {
border-top: 1px solid #ccc !important;
}

.message .signature {
border-top: 1px dotted #ccc !important;
}

.bbCodeBlock, .bbCodeQuote {
border: 1px dotted #6a7495 !important;
}

.bbCodeQuote .attribution {
background: #afb6cd !important;
border-bottom: 1px dotted #6a7495 !important;
}

.bbCodeQuote blockquote {
background: #d4d8e5 !important;
}

.imageList .categoryStrip {
background: #6a7495 !important;
border: 0 !important;
border-bottom: 1px solid #4e597e !important;
}

.categoryStrip h3 {
color: #fff !important;
}

.forum_list .thumbnailHolder .infobar {
background: #f8f8f8 !important;
border: 1px solid #ccc !important;
box-shadow: 0 0 0 !important;
webkit-box-shadow: 0 0 0 !important;
moz-box-shadow: 0 0 0 !important;
}

.descriptionUserBlock {
background: #f8f8f8 !important;
border: 1px solid #ccc !important;
}

.descriptionUserBlock .arrow {
border-left-color: #ccc !important;
}

.thumbnailHolder .boxModelFixer {
border: 1px solid #ccc !important;
}

.thumbnailHolder .infobar .item {
border: 1px solid #ccc !important;
}

.Menu {
border: 1px solid #9ba96f !important;
border-top: 5px solid #9ba96f !important;
}

.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
background: #9ba96f !important;
text-shadow: 0 0 0 !important;
}

.blockLinksList a:hover {
background: #e5e5e5 !important;
}

.sectionFooter {
background: #e5e5e5 !important;
border-bottom: 1px solid #aaa !important;
}

.navPopup .PopupItemLinkActive {
border-bottom: 1px dotted #ccc !important;
padding: 5px !important;
}

.navPopup .PopupItemLinkActive:hover {
background: #e5e5e5 !important;
}

.Menu .secondaryContent {
background: #f8f8f8 !important;
}

#DiscussionListOptionsHandle a {
border: 1px dotted #6a7495 !important;
border-top: 0 !important;
}

.DiscussionListOptions {
border: 1px solid #ccc !important;
}

form.secondaryContent {
background: #f8f8f8 !important;
}

.Popup .PopupControl:hover, .Popup.PopupContainerControl:hover {
background: #9ba96f !important;
}

a.secondaryContent {
background: #e5e5e5 !important;
border-bottom: 1px solid #ccc !important;
}

.accountSideBar a:hover {
background: #f8f8f8 !important;
}

.subHeading {
background: #d4d8e5 !important;
border-color: #aaa !important;
color: #6a7495 !important;
font-weight: bold !important;
}

.heading, .xenForm .formHeader {
background: #6a7495 !important;
}

.larger.textHeading, .xenForm .sectionHeader {
color: #000 !important;
}

.textHeading, .xenForm .sectionHeader {
border-bottom: 1px dotted #ccc !important;
}

.xenForm fieldset, .xenForm fieldset + .ctrlUnit, .xenForm .submitUnit {
border-top: 1px dotted #ccc !important;
}

.textWithCount.subHeading .text {
color: #6a7495 !important;
}

.profilePage .tabs.mainTabs li a:hover {
background: #e5e5e5 !important;
}

.profilePage .tabs.mainTabs li
a {
background: #f8f8f8 !important;
border-color: #ccc !important;
}

.sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading {
color: #000 !important;
}

.question h2 { 
color: #000 !important; 
font-weight: bold !important;
font-size: 1.3em !important;
letter-spacing: -1px;
}

.pollBlock .pollOptions, .pollBlock .pollResults {
border-color: #ccc !important;
}

.pollBlock .pollOption label:hover {
background: #e5e5e5 !important;
}

#logo { 
padding-left: 10px !important;
font-size: 2.4em !important;
font-weight: bold !important;
letter-spacing: -2px !important;
color: #fff !important;
text-shadow: 0 2px 1px #6a7495;
}

#logo:before { content: "CheeseForMice Forum"; }
#logo img { display: none !important; }

.xenOverlay.memberCard a { color: #fff !important; }
}
