css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
#inbox_button {

    padding-right: 10px !important;
}

#inbox_button {

    padding-right: 2px !important;
    padding-left: 15px !important;
}

#inbox_button a {
    height: 0 !important;
    width: 0 !important;
    top: 5px !important;
    padding-left: 50px !important;
    padding-right: 25px !important;
    padding-top: 30px !important;   
    background: url('http://media.tumblr.com/tumblr_mew01aCEfp1rvjxrx.png') !important;}

#inbox_button .tab_notice {
    left: 38px !important;
    right: auto !important;
}

a.post_avatar[style*='anonymous_avatar_64.gif'],
a.post_avatar[style*='default_avatar_64.gif'] {
  background-image: url("http://media.tumblr.com/tumblr_mew7agu2P61rvjxrx.jpg") !important;
  background-size: 64px 64px !important;
  background-repeat: no-repeat !important;
  background-color: rgba(255,255,255,0) !important;
  box-shadow: none !important;
}
}

@-moz-document url-prefix("http://starfightercomic.com/chapter_01_comments.php"), url-prefix("http://starfightercomic.com/chapter_02_comments.php"), url-prefix("http://starfightercomic.com/chapter_03_comments.php") {

div#dsq-textarea-wrapper {
  background: url("http://media.tumblr.com/908be8de19bda77a88b540ddfe1ad36b/tumblr_inline_mhtb1kyS2n1rvjxrx.png") no-repeat center bottom !important;
  background-color: rgba(255,255,255,1) !important;
}
}
