css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
    opacity: 0 !important;
}

#posts .post.new_post .new_post_label{
padding-top: 70px !important;
padding-bottom: 0px !important;
  font-size:0px !important;
background-color: none !important;}

#new_post_label_text,
#new_post_label_photo,
#new_post_label_quote,
#new_post_label_link,
#new_post_label_chat,
#new_post_label_audio,
#new_post_label_video {
  background: url("http://media.tumblr.com/tumblr_mevwmatavw1rvjxrx.png") no-repeat !important;
}


#new_post_label_photo {
  background-position: -73px 0px !important;
}
#new_post_label_quote {
  background-position: -140px 0px !important;
}
#new_post_label_link {
  background-position: -220px 0px !important;
}
#new_post_label_chat {
  background-position: -285px 0px !important;
}
#new_post_label_audio {
  background-position: -355px 0px !important;
}
#new_post_label_video {
  background-position: -430px 0px !important;
}

#new_post_label_text:hover {
  background-position: 0px -95px !important;
}
#new_post_label_photo:hover {
  background-position: -73px -95px !important;
}
#new_post_label_quote:hover {
  background-position: -140px -95px !important;
}
#new_post_label_link:hover {
  background-position: -220px -95px !important;
}
#new_post_label_chat:hover {
  background-position: -285px -95px !important;
}
#new_post_label_audio:hover {
  background-position: -355px -95px !important;
}
#new_post_label_video:hover {
  background-position: -430px -95px !important;
}

a.post_avatar[style*='anonymous_avatar_64.gif'],
a.post_avatar[style*='default_avatar_64.gif'] {
  background-image: url("http://media.tumblr.com/tumblr_mew7agu2P61rvjxrx.jpg") !important;
  background-size: 64px 64px !important;
  background-repeat: no-repeat !important;
  background-color: rgba(255,255,255,0) !important;
  box-shadow: none !important;
}
}

@-moz-document url-prefix("http://starfightercomic.com/chapter_01_comments.php"), url-prefix("http://starfightercomic.com/chapter_02_comments.php"), url-prefix("http://starfightercomic.com/chapter_03_comments.php") {
div#dsq-textarea-wrapper {
  background: url("http://media.tumblr.com/908be8de19bda77a88b540ddfe1ad36b/tumblr_inline_mhtb1kyS2n1rvjxrx.png") no-repeat center bottom !important;
  background-color: rgba(255,255,255,1) !important;
}
}
