css:
@-moz-document url-prefix('http://jsfiddle.net') {

#header {
  background: #434343 !important;
}
#branding {
  padding-right: 5px !important;
}
#home {
  background-position: -40px 50% !important;
  margin-left: -6px;
  margin-top: -1px;
  width: 85px !important;
}
.actionCont:first-child {
  left: -11px !important;
  position: relative !important;
}
.actionCont:first-child:after {
  background: #363636;
  border-left: 1px solid #4d4d4d;
  bottom: -8px;
  content: '';
  position: absolute !important;
  right: -11px;
  top: -8px;
  width: 1px;
}
.actionCont:first-child .actionItem a {
  display: block !important;
  position: relative !important;
}
.actionCont:first-child .actionItem a:after {
  bottom: 0 !important;
  content: 'â‰¡';
  font-size: 16px;
  left: 0 !important;
  line-height: 28px !important;
  position: absolute !important;
  right: 1px !important;
  text-align: center !important;
  top: 0 !important;
}
.actionCont:only-child .actionItem a:after {
  content: '';
}
.actionCont:nth-child(2) .actionItem:nth-child(2) {
  display: none;
}
.actionItem a {
  border-radius: 0 3px 3px 0;
}
.actionItem a:hover,
.actionItem a:focus {
  box-shadow: inset 0 0 0 50px hsla(0,0%,100%,.05);
}
.actionItem:first-child a {
  border-radius: 3px 0 0 3px;
}
.actionCont:nth-child(2) .actionItem:first-child a,
.actionItem:only-child a {
  border-radius: 3px;
}
#actions li.actionItem .aiButton,
#actions li.actionItem .aiButton span,
#actions ul.dropdown li.actionItem a.aiButton {
  background-image: none !important;
}
#actions li.actionItem a.aiButton {
  background-color: transparent !important;
  border-color: #777 !important;
}
#actions li.actionItem a.aiButton span {
  padding: 0 10px !important;
}
#actions #toggleSidebar {
  border-width: 1px !important;
}
#sidebar {
  background-color: #efefef !important;
  border-right: 1px solid #d5d5d5;
  box-shadow: inset -1px 0 0 hsla(0,0%,100%,.25);
  margin-top: -1px;
  padding-bottom: 10px;
}
#sidebar .toggler.active {
  cursor: default;
}
#sidebar .toggler:hover {
  background-color: hsla(0,0%,100%,.5);
}
#sidebar .toggler.active:hover {
  background-color: hsla(0,0%,100%,0);
}
#content .window {
  border-color: #d5d5d5 !important;
}

}
