@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://userstyles.org/"), url-prefix("https://kinda-static.userstyles.org/") {
/* url-prefix("http://userstyles.org.nyud.net/") */

html { background: #234 !important; }
body { box-shadow: 0 0 16px #000 !important; }
.ad, #social, #post-install-ad { display: none !important; }

th, mark { background: none !important; }
body, #main-header, #left-sidebar > nav, #left-sidebar > footer, #left-sidebar > div, .listing-no-screenshot, pre, blockquote { background: rgba(20,20,20,0.5) !important; }
#left-sidebar > form, form[action="/styles/create"]:before, form[action="/styles/update"]:before { background: rgba(20,20,20,0.95) !important; }
#obsoletion-message, .notice, .errorExplanation { background: rgba(130,10,10,0.3) !important; }
option, #view-code, #lightbox > div, /*login*/a.btn-auth { background: rgba(30,30,30,0.99) !important; }
article.good-rating { background: rgba(10,130,10,0.1) !important; }
article.ok-rating, .style-warning { background: rgba(130,130,10,0.1) !important; }
article.bad-rating { background: rgba(130,10,10,0.1) !important; }
article.no-rating { background: rgba(100,100,100,0.1) !important; }
#main-article > article { border-radius: 7px !important; }
#screenshots img { background: #555 !important; }

.install-status.install { background-color: #151 !important; }
.install-status.no-install { background-color: #511 !important; }
.install-status.install-info { background-color: #135 !important; }
.install-status.install:hover { background-color: #171 !important; border-color: #0f0 !important; }
.install-status.no-install:hover { background-color: #711 !important; border-color: #f00 !important; }
.install-status.install-info:hover { border-color: #09f !important; }

* { color: inherit !important; border-color: #555 !important; }
html { color: #def !important; }
a, h1, h2 { color: #9cf !important; text-shadow: none !important; text-decoration: none !important; }
a:visited { color: #5af !important; }
a:hover { color: #0ff !important; }

hr { height: 0 !important; border-style: solid !important; border-width: 1px 0 0 !important; }
::-moz-selection { background: #9cf !important; color: #000 !important; }
::selection { background: #9cf !important; color: #000 !important; }

#view-code { border-color: #555 !important; }
code, pre { border-color: #09f !important; }
.author-styles > tbody > tr:hover { outline: 1px solid #555 !important; }
#main-header > h1:before { color: #4be !important; background: #111 !important; }

input[type="submit"], input[type="button"], input[type="file"], input[type="reset"], button,
.install-status, #show-button { color: #fff !important; background: rgba(0,0,0,0.5) !important; border: 1px solid #555 !important;
border-radius: 7px !important; box-shadow: none !important; text-shadow: none !important;
background-image: linear-gradient(rgba(255,255,255,0.2),rgba(125,125,125,0.2),rgba(0,0,0,0.2)) !important; }

input[type="submit"]:hover, input[type="button"]:hover, input[type="file"]:hover, input[type="reset"]:hover, button:hover, #show-button:hover
{ background-color: #135 !important; color: #fff !important; border-color: #09f !important; }

button, input, select, textarea { -moz-appearance: none !important; }
input[type="radio"], input[type="checkbox"] { color: #9cf !important; background: #222 !important; border-color: #444 !important; }

input, select, textarea, input[type="text"] { background: rgba(20,20,20,0.6) !important; color: #fff !important; border: 1px solid #555 !important; padding: 2px !important; }
input[type] { border-width: 1px !important; border-style: solid !important; }
textarea:hover, input:hover { border-color: #999 !important; }
textarea:focus, input:focus { border-color: #09f !important; }
select > button { background-color: transparent !important; border: none !important; box-shadow: none !important; }
html:root option:checked { background: #9cf !important; color: #000 !important; }


html { background-image: url("data:image/png;base64,") !important; }

}
