css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("extratorrent.cc"){
body {background-color:black!important;background-image:none!important;color:white!important;}
div.blog_content {border: 1px dashed white!important;}
div.top_pic img  {border: 1px solid #45AB34!important;}
div.menul {background:none!important;width: 0!important;}
div.top_torr {background-color:#2E383D; margin: 2px 6px 15px 1px!important;}
div.top_cat {width: 206px;background: none green;}
div.blog_top {border-radius: 6px 6px 0 0;border: 1px solid white;padding: 7px 8px 9px 20px!important;background:#0067A9!important;}
div.chat_msg, td.tabledata0, td.tabledata_num, table.tl th table tr, table.tl th table tr:hover 	{background-color:black!important;}
td.tabledata1 {background-color: #0C1418!important;border: 1px solid #0E89E0!important;}
td.td_menu {background: #0067A9!important;border-radius: 6px;height: 30px;}

#MarketGidComposite536, #FFN_IM_Chat_Container, #FFN_IM_Chat, #a18db347, #a4g-floating-ad,
span .grey div a img, .ads2, .blog_left, .blog_right, .menur, .top_icon,
img[src="/images/menu_right.gif"],
img[src="/images/menu_left.gif"],
img[src="http://static.extratorrent.com/images/menu_right.gif"],
img[src="http://static.extratorrent.com/images/menu_left.gif"],
img[alt="BetExpert.me sport tips"]
{display:none!important;}

#hot_torrents{background: none!important;}
a.menu {color:white!important;}
tr.tlr, tr.tlz {color:black;}
tr.tlr {background-color: #2E383D!important;}
tr.tlz {background-color: #131314!important;}
b.pager_no_link, a.pager_link {border-radius: 6px;}
a img, table {border-radius: 20px 20px 20px 20px;}

/* Forum stuff */
table.fm  {border: 1px solid #000000!important;}
table.fm th {background: #0C1418!important;border-radius: 6px;}
table.fm td {background-color: #2E383D!important;}
table.fm td.fme {background-color: #2E383D!important;}
table.fm td.fmo {background-color: #131314!important;}
table.fm td.stp{background:none!important}
table.fm td div.top, table.fm td.top {background: #0067A9!important;}
div.usr {background-color: white;border-radius: 3px;padding: 1px;}

table.wrap{color:#002A46!important;}
}
