css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.hardware.fr") {
	html, body{
		margin: 0px !important;
	}
	body{
		padding-top: 5px !important;
	}
	body > table:first-child{
		position: fixed !important;
		top: -21px !important;
		left: 0px !important;
		right: 0px !important;
		border-bottom: 5px solid black !important;
		height: 25px !important;
	}
	body > table:first-child:hover{
		top: 0px !important;
		border-bottom: 2px solid black !important;
	}
	body > table:first-child > tbody > tr:last-child > td:last-child > table:first-child > tbody > tr:first-child,
	body > table:first-child > tbody > tr:first-child,
	tr.sujet td[colspan="10"],
	td.tdmenu,
	body > br,
	div.cadreonglet .beforongletsel,
	div.cadreonglet .afterongletsel,
	div.cadreonglet .beforonglet,
	div.cadreonglet .afteronglet,
	div.arbo img,
	div.arbo br{
		display: none !important;
	}
	body > center{
		margin-top: -10px !important;
	}
	body > table:first-child td{
		height: 10px !important;
		padding: 0px 3px !important;
		border: none !important;
	}

	body > center embed{
		margin-top: 25px !important;
	}

	div.cadreonglet .afteronglet:last-child,
	div.cadreonglet .afterongletsel:last-child,
	div.cadreonglet .beforonglet:first-child,
	div.cadreonglet .beforongletsel:first-child{
		display: block !important;
	}
	div#mesdiscussions{
		width: 80% !important;
	}
	table.messagetable{
		margin: 3px 0px !important;
		border: 1px solid black !important;
		padding: 2px;
		background-color: white;
	}
	table.messagetable tr,
	table.messagetable th,
	table.messagetable td{
		border: none !important;
	}
	table.messagetable tr.message{

	}
	div.arbo{
		margin: 0px !important;
	}
	div.arbo h1,
	div.arbo h2,
	div.arbo h3,
	div.arbo h4{
		display: inline !important;
		margin-left: -4px !important;
		font-weight: bold !important;
	}
	div.arbo h1:after,
	div.arbo h2:after,
	div.arbo h3:after{
		content: " >" !important;
	}
	div.sondage{
		margin-top: -32px !important;
	}
	textarea.reponserapide{
		width: 800px !important;
		height: 200px !important;
	}
	div.copyright{
		color: black !important;
		width: 300px !important;
		height: 50px !important;
		margin: 0px auto !important;
		padding: 5px !important;
		background-color: #ffffff !important;
		position: fixed !important;
		bottom: -37px !important;
		left:-287px !important;
		border: 2px solid black !important;
	}
	div.copyright a{
		color: black !important;
	}
	div.copyright:hover{
		border: 2px solid black !important;
		bottom: 0px !important;
		left: 0px !important;
	}
	body > center:last-child{
		position: fixed;
		right: -777px;
		bottom: -57px;
		background-color: white !important;
		border: 2px solid black;
		width: 800px;
		padding-bottom: 20px;
		height: 60px;
	}
	body > center:last-child:hover{
		border: 2px solid black !important;
		right: 0px !important;
		bottom: 0px !important;
	}
	div.legende{
		color: black !important;
		background-color: #ffffff !important;
		padding: 5px !important;
		position: fixed !important;
		right:-487px !important;
		bottom: 50px !important;
		overflow: hidden !important;
		width: 500px !important;
		border: 2px solid black !important;
	}
	div.legende:hover{
		right:-2px !important;
	}
	span.signature{
		opacity: 0.5 !important;
	}
}
