css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {



body {background: url('http://i46.tinypic.com/1z32dnb.jpg') bottom left #fffaf0 no-repeat fixed !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color: transparent!important;}


*{text-shadow:none !important; color:#8d8b8d !important;}


ol#posts li.notification{background-color: #8d8b8d !important; border-bottom: 2px solid #c9c8ca !important; color: #8d8b8d !important;}


#right_column a#dashboard_switch_blog_menu_current:hover {background:#8d8b8d;}


#nav .nav_item.active{background-color:#000000}


#left_column #posts .post .permalink{background-color:#ffffff;}


#dashboard_switch_blog_menu_current,


#dashboard_switch_blog_arrow_and_divider,


#dashboard_switch_blog_arrow_current


{background-image:none !important}


.selection_nipple.#ffffff, .selection_nipple:not([class="selection_nipple white"]) { 


	background: none !important;


	width: 0px !important;


	height: 0px !important;


	margin-top: 0px !important;


	border-bottom: solid 11px #fcc6a3 !important;


	border-right: 11px solid transparent !important;


	border-left: 11px solid transparent !important; }



.selection_nipple:not([class="selection_nipple white"]) {



	border-bottom: solid 11px #fcc6a3 !important;}



.notification_type_icon{display:#8d8b8d !important}



.selected .following,



.selected .followers,



.selected .settings,



.selected .posts,



.selected .messages,



.selected .drafts,



.selected .queue, 



.selected .likes {



	color: #d1d0d1 !important; 



	background: #e9e9e9 !important;}



a.explore_more_tags,



a.add_and_remove,



a.customize,



a.mass_editor



	{background-color:#fcc6a3 !important;  



	background-image:none !important;



	border:0px !important}



div.gradient,



div.dashboard_nav_item, 



a.permalink



	{background-color:#e9bfa3 !important;



	opacity:.9; 



	background-image:none !important;



	border:0px !important}



.content.is_media, 



.mask.top, 



.mask.bottom {



	background: none !important; }



.post.photo_post .photo, 



.post.photo_post .video_thumbnail {



	border-radius: 16px !important;}



.post.photo_post .photo, 



.post.photo_post .video_thumbnail 



	{-moz-box-shadow: 1px 1px 3px #e9bfa3 !important;}



#auto_pagination_loader



	{background: #e9bfa3 !important;}



div.return_to_top_icon{	background: none !important;



	width: 0px !important;



	height: 0px !important;



	margin-top: 0px !important;



	border-bottom: solid 30px #e9bfa3 !important;



	border-right: 15px solid transparent !important;



	border-left: 15px solid transparent !important;}



#left_column #posts .notification {



background-color:#ffffff !important;



color:#000000 !important;



border-bottom-color: #ffffff !important}








#new_post_notice_container{



color:#e9bfa3 !important}



#new_post_notice_container a{



color:#e9bfa3 !important}




.permalink {background: none !important;



width: 16px !important;



height: 16px !important;



  background: #fcc6a3) !important;



  border-bottom-left-radius: 10px !important;



  border-top-right-radius: 10px !important;



  margin-top: 3px !important;



  margin-right: 4px !important;}


}
