css:
@-moz-document domain('www.tumblr.com') {

Body {

  background:url("http://i.imgur.com/jrxIWrP.jpg") no-repeat fixed !important;

  background-color: rgba(0,0,0, 0.95) !important;

  background-position: 0% 0% !important;

  background-size: cover !important;

}
p
.l-content {
background-color: rgba(0,0,0,.0) !important;
}
  .l-container.l-container--two-column-dashboard .left_column{
background-color: rgba(0,0,0,0) !important;
}

#logo, #logo a {

  position: relative;

  z-index: 1;

  width: 270px !important;

  height: 58px !important;

  background-repeat: no-repeat;

  background: url("http://i.imgur.com/Zm2I6N9.png")!important;

}
.mceContentBody {
  background: rgb(255, 255, 255) !important; 
  color: rgb(68, 68, 68) !important; 
}

#new_post {

  opacity: 1.0 !important;

  background-color: rgba(7,12,23,.5) !important;
  border-bottom: 2px solid rgba(252,199,246,0)!important;
  border-top: 2px solid rgba(252,199,246,0)!important;
}


#posts .post .post_content {
  background: transparent !important;
}


#posts .post {
  background: rgba(7,12,23,.5) !important;
  border-radius: 35px !important;
}


.post_notes{
  color: rgba(207,235,255,1) !important;
}


.tracked_tags {
background-color: rgba(7,12,23,1)  !important;
border-radius: 10px !important;
}

.popover_menu_item {
  background: rgba(7,12,23,.9) !important;
  border-bottom: 1px solid #CFEBFF !important;
  
}


.post_content DIV:last-child,BLOCKQUOTE{border-color:rgba(59,219,255,1)!important}


pre,pre a,pre SPAN,.quote_source pre,div pre,pre SPAN,pre a,CODE,pre small{background:rgba(7,12,23,.5)!important;color:rgba(59,219,255,1)!important;font:bold .9em "arial",consolas,courier!important;white-space:pre-wrap!important;white-space:-moz-pre-wrap!important;white-space:-pre-wrap!important;white-space:-o-pre-wrap!important;word-wrap:break-word!important}


.return_to_top_icon{background: url('http://i.imgur.com/Bl5rKby.png')!important;
width:87px!important;
height:100px !important;
position:absolute!important;
margin-top: -10px !important;
margin-left: -85px !important;}

.chat_line:nth-child(even),PRE{background-color:rgba(0,0,0,0)!important;
color:rgba(207,235,255,1)!important}


.answer_container,
.notes LI{background:rgba(7,12,23,.5)!important;
}



#posts li.post_container div[class^="post_question"] {
  background-color: rgba(179,223,255,1) !important;
  border-radius: 45px !important;
}

.post_question{
  color: rgba(7,12,23,1) !important;
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(62,149,249,0.0) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}



.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(207,235,255,1)  !important;

}







li.selected, li.recessed, a.active, div.post_filter_tabs a {

  background: none !important;

}







.post.photo_post .photo, .post.photo_post .video_thumbnail {

 box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;

  border-radius: 12px !important;

}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}





#content_top.content_cap,

#content_bottom.content_cap {

  visibility: hidden !important;

}

div#edit_post_controls {

  top: 20px !important;

}

#dashboard_edit_post #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  border-radius: 14px !important;

}

#auto_pagination_loader{

background: rgba(7,12,23,.5) !important;

border-bottom: 0px solid #2A3B52 !important;

border-top: 2px solid #CFEBFF !important;

}




.post_content,
.new_post_label,

#right_column > UL:first-child > LI:first-child > A > SMALL > DIV,


.post_controls A,

.post_info,

.post_info A,

.footer_links.with_tags.with_source_url A,

.footer_links A,

.footer_links,

.block,


#dashboard_controls A,

#dashboard_controls DIV SPAN,

.add_and_remove,

.notification.single_notification,

.notification,

.notification .hide_overflow A,

.notification.last_notification,

.notification.last_notification A,

.answer_container SPAN,

.notes,

.notes LI,

.notes_hide_link A,

.read_more_container,

#footer, #footer DIV A,

.post_question_asker,

.source_url,

.label_and_follow,

.follow, #search_query, #search_controls_button,

.posts, .followers, .messages, .drafts, .queue, .flat.no_hover DIV,

A[class^="category"],

a:link,

a[href^="http"],

a[href^="/edit"],

a[href^="/likes"],

a[href^="/new"],

a[href^="/tagged"],

a[href^="/login"],

a[href^="/docs"],

a[href^="/explore"],

a[href^="/mega-editor"],

.count, .customize, .settings,

a span[id^="note_link"],

.answer_container,

.no_posts_found,

.following, H1[id="page_heading"], div.l-content P, H1, p SMALL,

.notes_loader,

.small_links A .hide_overflow, .sorry P, .cell.lead_editor, .sidebar_nav_item A, .tag,

 H2, #posts .post .post_controls .reblog_count span {

  color: rgba(207,235,255,1) !important;

}

a:link:hover,

A[href^="http"]:hover,

a[href^="/edit"]:hover,

a[href^="/likes"]:hover,

a[href^="/new"]:hover,

a[href^="/tagged"]:hover,

a[href^="/customize"]:hover,

a[href^="/explore"]:hover,

a[href^="/mega-editor"]:hover,

a[id^="ask_answer"]:hover,

a span[id^="note_link"]:hover,

.block_link:hover,

.following:hover, .add_and_remove:hover, #search_query:hover, #search_controls_button:hover, .settings:hover, .posts:hover, .followers:hover, .messages:hover, .drafts:hover, .queue:hover,

SMALL, .text_field.big.wide, #crushes .count, #l10n_sorry, #footer DIV A:hover, #footer DIV STRONG:hover,

BUTTON[type="submit"]:hover, .small_links A .hide_overflow:hover, .block:hover, #signup_forms_submit:hover, .sidebar_nav_item A:hover,

#posts .post .post_controls .reblog_count span:hover {

  color: rgba(59,219,255,1) !important;

}

.source_url_gradient, #toggle_reasons {

  display: none !important;

}

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

  border-bottom: solid thin rgba(64,64,64,1.0) !important;

}

.post_content P A,

.post_title A {

  color: rgba(207,235,255,1) !important;

}

.tag {color:rgba(7,12,23,1)!important;
}

.post_tags:after{
    visibility:hidden!important;
}
.post_permalink {opacity:.9!important;
  background: #3BDBFF !important;
 border-radius: 15px !important;
  }


#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #CFEBFF !important;
  background-color: rgba(43,43,43,0)!important;
  border-radius: 10px !important; 
}



#right_column .controls_section li a:hover {

  color: #CFEBFF !important;
  background-color: rgba(7,12,23,.8)!important;
  border-radius: 10px !important;
}


.notification.single_notification,

.notification.first_notification,

.notification.last_notification,

.notification {

  background: none !important;

  border: none !important;

}

.notification.single_notification,

.notification.first_notification,

.notification.last_notification,

.notification,

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

  color: rgba(173,221,255,1) !important;

}

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

    border-bottom: solid thin rgba(101,28,13,0) !important;

}

.notification.single_notification A:hover,

.notification.first_notification A:hover,

.notification A:hover,

.notification.last_notification A:hover,

.notification .hide_overflow A:hover {

  color: rgba(128,128,128,1) !important;

}



ol#posts li.notification.alt {

   background: rgba(12,20,38,.6) !important;



}





#content_top, #content_bottom{display:none !important;}





ol#posts li.notification {

   background: rgba(12,20,38,.9) !important;

    border-bottom: 0px solid #ADDDFF !important;



}







#left_column #posts .notification a {

                        color:#ADDDFF !important;

                        border-bottom:0px !important;

                    }


div#right_column,

div#right_column:after {

  background: none !important;

}
}@-moz-document url-prefix('http://www.tumblr.com/dashboard/iframe'), url-prefix('https://www.tumblr.com/dashboard/iframe')  {body {background:none!important;}}
