css:
@-moz-document domain('lisper.ru') {



body {

    color: #b2b2b2 !important;

    background: #3A3E46 !important;

}



a:link, div.actions span.b-share__text {

    color: #9DD7FF !important;

}

a:visited {

    color: #EAB6FF !important;

}

a:hover {

    color: #F0B2B2 !important;

}

div#xsite-nav {

    background: #3C4048 !important;

}

div#xsite-nav a.current, div#xsite-nav a:hover,

.submenu div.options a.current, .submenu div.options span.current {

    background: #5B626D !important;

}

div.actions a:hover {

    color: #F0B2B2 !important;

    background: #5B626D !important;

}



input[type="text"], textarea, div#search button {

    color: #333333 !important;

    background: #CCCCCC !important;

}



div.text {

    background-color: #3C4048 !important;

    border: 1px dotted #616565 !important;

}

div.topicbody {
    background-color: #3C4048 !important;

    border: 1px dotted #616565 !important;

}
div.replybody {
    background-color: #3C4048 !important;

    border: 1px dotted #616565 !important;
}
div.topic-info {
    background-color: #3C4048 !important;}
div {
    background-color: #3C4048 !important;
}

}
